INSERT INTO `broadcast_text_locale` VALUES (21792, 'enUS', '', 'A discovery!  I need aid with these measurements... I\'ll send a signal to my colleague!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21793, 'enUS', '', 'The disruptor towers over charged the crystalforge, and uncovered more secrets!  Hold them!  Hold them while we gather new data!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21794, 'enUS', '', 'At last!  The information we seek!  Gather quickly, skyguard.  We must soon return to headquarters with our findings!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21795, 'enUS', 'This forge can make unstable concoctions... to study them, I need Apexis Shards!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21796, 'enUS', 'I am growing amazing crystals from these waters.  For catalysts, I must have more Apexis shards!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21797, 'enUS', 'Its properties are amazing!  Provide me with Apexis crystals, and I\'ll form weapons and armor within crystal geodes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21798, 'enUS', 'Come, $n. Lord Stormrage awaits.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21799, 'enUS', 'Warriors of Dragonmaw, gather \'round! One among you has attained the rank of highlord! Bow your heads in reverence! Show your respect and allegiance to Highlord $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21800, 'enUS', 'Lord Illidan will be here shortly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21801, 'enUS', 'The pile of skulls reeks of foulness.  You fear the arakkoa have made an addition to the pile fairly recently.\n\nYou will need 10 Time-Lost Scrolls to call forth the descendants of Terokk\'s adversaries.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21802, 'enUS', 'All hail Lord Illidan!', '', 26124);
