INSERT INTO `broadcast_text_locale` VALUES (21992, 'enUS', 'Right, right... So let\'s start the countdown from ten, ready?  TEN!', 'Right, right... So let\'s start the countdown from ten, ready?  TEN!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21993, 'enUS', 'Oh... okay, I\'ll just wait for zero then...', 'Oh... okay, I\'ll just wait for zero then...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21994, 'enUS', 'Speed it up, Tinker!', 'Speed it up, Tinker!', 26124);
