INSERT INTO `broadcast_text_locale` VALUES (21996, 'enUS', 'Yak, yak! Let\'s get to the drinking!', 'Yak, yak! Let\'s get to the drinking!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21997, 'enUS', 'BOOORING!!', 'BOOORING!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21998, 'enUS', 'Drink, Tink! Drink, Tink! Drink, Tink!', 'Drink, Tink! Drink, Tink! Drink, Tink!', 26124);
