INSERT INTO `broadcast_text_locale` VALUES (22072, 'enUS', 'Why does my cousin get to enjoy the balmy beaches of Stranglethorn, anyway?', 'Why does my cousin get to enjoy the balmy beaches of Stranglethorn, anyway?', 26124);
