INSERT INTO `broadcast_text_locale` VALUES (22075, 'enUS', 'The zeppelin to Grom\'gol has just arrived! All aboard for Stranglethorn!', 'The zeppelin to Grom\'gol has just arrived! All aboard for Stranglethorn!', 26124);
