INSERT INTO `broadcast_text_locale` VALUES (22079, 'enUS', 'I hope that my brother\'s doing okay in Durotar. There\'s an awful lot of smelly Orcs out there.... Then again, this isn\'t exactly the greatest gig, either...', 'I hope that my brother\'s doing okay in Durotar. There\'s an awful lot of smelly Orcs out there.... Then again, this isn\'t exactly the greatest gig, either...', 26124);
