INSERT INTO `broadcast_text_locale` VALUES (22096, 'enUS', 'I never get to ride to Grom\'gol. Its just so unfair. Warm, steamy beaches... Crocolisks, Raptors... hmmm... maybe I don\'t really want to go there after all.', 'I never get to ride to Grom\'gol. Its just so unfair. Warm, steamy beaches... Crocolisks, Raptors... hmmm... maybe I don\'t really want to go there after all.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (22097, 'enUS', 'The zeppelin to Grom\'gol has just arrived! All aboard for Stranglethorn!', 'The zeppelin to Grom\'gol has just arrived! All aboard for Stranglethorn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (22098, 'enUS', 'Don\'t be late, the next ship to Stranglethorn departs in only a minute!', 'Don\'t be late, the next ship to Stranglethorn departs in only a minute!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (22099, 'enUS', 'The zeppelin to Undercity has just arrived! All aboard for Tirisfal Glades!', 'The zeppelin to Undercity has just arrived! All aboard for Tirisfal Glades!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (22100, 'enUS', 'The zeppelin to historic Tirisfal Glades has just arrived! Don\'t be caught dead without the latest in Forsaken fashion!', 'The zeppelin to historic Tirisfal Glades has just arrived! Don\'t be caught dead without the latest in Forsaken fashion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (22101, 'enUS', 'The zeppelin should have just arrived at Grom\'gol... ', 'The zeppelin should have just arrived at Grom\'gol... ', 26124);
