INSERT INTO `broadcast_text_locale` VALUES (23542, 'enUS', '<Ulfang howls and sniffs at you, and you can understand what he says.>$B$BThe strange white worg of the north, Garwal, has taken my pack from me. We must rip out his throat so that I will be the alpha worg once again.', '', 26124);
