INSERT INTO `broadcast_text_locale` VALUES (24320, 'enUS', 'Grgggl! Glmrbrgl Uhglrglrglr!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (24321, 'enUS', 'Whatchoo been doin\', pacin\' yourselves?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (24322, 'enUS', 'Grglmbrgl? Mrgl glbmrgl rawglgl!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (24323, 'enUS', 'I hope I have a chance to visit with Greatfather Winter before Winter Veil is over.', 'I hope I have a chance to visit with Greatfather Winter before Winter Veil is over.', 26124);
