INSERT INTO `broadcast_text_locale` VALUES (24858, 'enUS', 'Rest now, you have fought well today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (24859, 'enUS', 'It\'s alive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (24860, 'enUS', 'Purl one, knit one. Purl one, knit one....', '', 26124);
