INSERT INTO `broadcast_text_locale` VALUES (27672, 'enUS', 'Drakkari, this will not stop us from stealing the power of your wild gods. We will take it from them directly, or from you if you\'ve already killed them; it matters not.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (27673, 'enUS', 'Many Argent Dawn are numbered amongst the simpletons that have fallen before me. The Argent Crusade will prove no different.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (27674, 'enUS', 'If you kill them, I will simply reanimate their corpses. And when you die, I will do the same to you.', '', 26124);
