INSERT INTO `broadcast_text_locale` VALUES (28061, 'enUS', 'Goregek hungry.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (28062, 'enUS', 'You wait, slave thing. Goregek eating.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (28063, 'enUS', 'Go kill more stuff while Goregek eat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (28064, 'enUS', 'Chicken! So delicious!', '', 26124);
