INSERT INTO `broadcast_text_locale` VALUES (41834, 'enUS', 'Lok\'tar ogar!', 'Lok\'tar ogar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41835, 'enUS', 'Bow your heads! $n walks among us!', 'Bow your heads! $n walks among us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41836, 'enUS', '$g Sir:Ma\'am;!', '$g Sir:Ma\'am;!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41837, 'enUS', 'There walks a true soldier of the Horde!', 'There walks a true soldier of the Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41838, 'enUS', 'For the Horde!', 'For the Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41839, 'enUS', 'Hellscream\'s eyes are upon you!', 'Hellscream\'s eyes are upon you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41840, 'enUS', '', 'Back!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41841, 'enUS', 'Why must we ride to battle on a mercenary ship? Used to be that we\'d kill pirates as soon as look at them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41842, 'enUS', '', 'Shoo!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41843, 'enUS', '', 'I don\'t want to hurt you, but I will!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41844, 'enUS', '', 'Smell blood, do you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41845, 'enUS', '', 'You\'ll not be eating me today!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41846, 'enUS', '', 'Away, creature of the depths!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41847, 'enUS', '', 'Do not force me to call upon the lightning!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41848, 'enUS', '', 'Stay away from Wil\'hai!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41849, 'enUS', 'See those ships in dock?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41850, 'enUS', 'They\'re the only ones that have returned from battle, and they\'re being repaired.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41851, 'enUS', 'I don\'t like it either, but it boils down to this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41852, 'enUS', 'We pay the mercs to get us out there, or our troops get no reinforcements.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41853, 'enUS', 'That makes sense, I suppose. Just doesn\'t feel right...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41854, 'enUS', '', 'What you want?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41855, 'enUS', 'Show me your goods, Denni\'ka.', 'Show me your goods, Denni\'ka.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41856, 'enUS', 'Welcome to the military, recruit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41857, 'enUS', 'OK! OK! Me do what $r wants!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41858, 'enUS', 'Please no whip me! Me help Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41859, 'enUS', 'Hurts! Stop!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41860, 'enUS', 'No more hurt! Me join you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41861, 'enUS', 'NO! ME NO HELP HORDE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41862, 'enUS', 'Whip make me angry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41863, 'enUS', 'Me shove whip down face!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41864, 'enUS', 'Me make you eat whip!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41865, 'enUS', 'You is BAD $r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41866, 'enUS', 'ME SO ANGRY!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41867, 'enUS', 'Such power! I AM INVINCIBLE!', 'Such power! I AM INVINCIBLE!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41868, 'enUS', 'For the Horde!', 'For the Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41869, 'enUS', 'ALL WILL PERISH!', 'ALL WILL PERISH!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41870, 'enUS', 'Death to the Grimtotem!', 'Death to the Grimtotem!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41871, 'enUS', 'Death to the Alliance!', 'Death to the Alliance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41872, 'enUS', 'Cower before the Horde!', 'Cower before the Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41873, 'enUS', 'KILL PINK SKINS! KILL! KILL!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41874, 'enUS', 'Champion $n!', 'Champion $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41875, 'enUS', 'You bring great pride to the Horde, Champion $n.', 'You bring great pride to the Horde, Champion $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41876, 'enUS', 'Orders, $g sir:ma\'am;?', 'Orders, $g sir:ma\'am;?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41877, 'enUS', 'Champion $n fights alongside us! We cannot lose!', 'Champion $n fights alongside us! We cannot lose!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41878, 'enUS', '$g Sir:Ma\'am;, would you sign my weapon? My young ones would be most thankful.', '$g Sir:Ma\'am;, would you sign my weapon? My young ones would be most thankful.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41879, 'enUS', 'The Alliance must fall!', 'The Alliance must fall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41880, 'enUS', 'For the Horde! For Hellscream!', 'For the Horde! For Hellscream!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41881, 'enUS', 'It is an honor to stand in your presence, Champion $n.', 'It is an honor to stand in your presence, Champion $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41882, 'enUS', 'Hey! Hey Jibbly! This place STINKS!', 'Hey! Hey Jibbly! This place STINKS!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41883, 'enUS', 'WATCH OUT! INCOMING TURD!', 'WATCH OUT! INCOMING TURD!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41884, 'enUS', 'I shoulda just stayed on Kezan and taken my chances with the volcano.', 'I shoulda just stayed on Kezan and taken my chances with the volcano.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41885, 'enUS', 'I can\'t work under these conditions, man! I\'m losin\' it!', 'I can\'t work under these conditions, man! I\'m losin\' it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41886, 'enUS', 'This has got to be some type of health violation!', 'This has got to be some type of health violation!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41887, 'enUS', 'Jibbly, get someone to clean up these turds!', 'Jibbly, get someone to clean up these turds!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41888, 'enUS', 'WORST. JOB. EVER.', 'WORST. JOB. EVER.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41889, 'enUS', 'I\'m feeling ill...', 'I\'m feeling ill...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41890, 'enUS', 'I QUIT! I\'d rather take my chances getting shot at by Alliance!', 'I QUIT! I\'d rather take my chances getting shot at by Alliance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41891, 'enUS', 'I\'m out!', 'I\'m out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41892, 'enUS', 'The Horde doesn\'t pay me enough for this... this crap!', 'The Horde doesn\'t pay me enough for this... this crap!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41893, 'enUS', 'Take your stinking job and stick it where the sun don\'t shine!', 'Take your stinking job and stick it where the sun don\'t shine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41894, 'enUS', 'I\'ve had it! Find someone else to guard your precious turd filled base!', 'I\'ve had it! Find someone else to guard your precious turd filled base!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41895, 'enUS', 'I\'M MAD AS HELL AND I\'M NOT GONNA TAKE IT ANYMORE! I QUIT!', 'I\'M MAD AS HELL AND I\'M NOT GONNA TAKE IT ANYMORE! I QUIT!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41896, 'enUS', 'Find another sucker to stand watch over your enormous mound of poop. I\'m done!', 'Find another sucker to stand watch over your enormous mound of poop. I\'m done!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41897, 'enUS', 'I\'m free!  You\'ve saved me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41898, 'enUS', 'Noooo!  Not again!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41899, 'enUS', 'Ah!  Umm... hello there.  I was just inspecting this shell, here.  Carry on!  Nothing to see here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41900, 'enUS', 'I stopped by the park district on my way here. There\'s nothing left of it...\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41901, 'enUS', 'Aye. I was here when the dragon attacked.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41902, 'enUS', 'He could\'ve destroyed the entire city if he willed it. We were powerless to stop him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41903, 'enUS', 'What drove him off, then?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41904, 'enUS', 'No idea. It made no sense...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41905, 'enUS', 'If you ask me, the beast is insane.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41906, 'enUS', 'He flew in, torched the park, then just flew off.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41907, 'enUS', 'Why are you upside-down?', 'Why are you upside-down?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41908, 'enUS', 'Why is that?', 'Why is that?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41909, 'enUS', 'Fascinating.', 'Fascinating.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41910, 'enUS', 'You will be removed from the Krom\'gar Demolisher if you do not return to Battlescar Valley.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41911, 'enUS', 'How did you get all the way over here, then?', 'How did you get all the way over here, then?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41912, 'enUS', 'Sounds impressive.', 'Sounds impressive.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41913, 'enUS', 'That\'s too bad.  Anyhow...', 'That\'s too bad.  Anyhow...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41914, 'enUS', 'I really need to get going now.', 'I really need to get going now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41915, 'enUS', '...', '...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41916, 'enUS', 'I seem to be having a... floating problem.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41917, 'enUS', 'I couldn\'t tell ya.  Maybe I\'ve got a low bone density, or a larger-than average lung capacity.  Maybe it\'s all that cheese I had for lunch.$b$bEither way, this shell is the only thing that\'s keeping me from rocketing to the surface.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41918, 'enUS', 'Aye, and it runs in the family.  The Digsongs have always had buoyancy issues, I\'m afraid.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41919, 'enUS', 'Hand over hand, $g lad: lass;... hand over aching hand.  I can\'t tell ya how many gallons of seawater went right up my nose.$b$bYou shoulda seen me fight off that pyreshell crab using my feet and my teeth, though!  \'Twas a thing of glory, \'twas.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41920, 'enUS', 'I remember when I was just a wee little lad... the other boys would swim down to the bottom of Loch Modan to pick up sand dollars and seashells.  Oskar and I were stuck on the lake\'s surface, kicking our legs uselessly into the air, but we couldn\'t join them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41921, 'enUS', 'I can\'t even remember how many times I\'ve spotted a beautiful mining vein at the bottom of a lake or river, glimmering mockingly at me.  I\'m forced to float orelessly by, mining pick in hand, only to watch some blasted orc or blood elf swim down to the bottom and take it away from me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41922, 'enUS', 'Ya have no idea what it\'s like to be stuck here in Vashj\'ir like this!  Do ya know how many fish have confused me for a piece of seaweed, nibbling on my toes and hiding between pieces of my armor?$b$bThe tickling alone is enough to drive a dwarf mad!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41923, 'enUS', 'I can tell ya don\'t want to hear my grumbling.  That\'s fine.$b$bJust don\'t forget how lucky you are, with your \"sea legs\" and your \"ability to swim in a direction other than up\".', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41924, 'enUS', 'Earthquakes every other day, a crazy dragon tearing up the world, what\'s next?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41925, 'enUS', 'Put all of that out of your head, recruit.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41926, 'enUS', 'I\'ve been fightin\' Horde for ten years now, and I\'ve never seen it worse than this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41927, 'enUS', 'Just stay focused on the battle in front of us, or you won\'t live to worry about the rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41928, 'enUS', 'Where\'d you ship in from, soldier?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41929, 'enUS', 'Just arrived from the Barrens.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41930, 'enUS', 'Hairy? You heard wrong. It\'s downright desperate!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41931, 'enUS', 'I couldn\'t believe it when they ordered half of us to ship out...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41932, 'enUS', 'Ahh, the Battle Scar. Pretty hairy out there, I hear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41933, 'enUS', 'Goutgut very special. Goutgut am first to get to crush puny humans in new place.$B$BGoutgut so happy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41934, 'enUS', '', 'Victory for Sylvanas! These lands will be ours.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41935, 'enUS', 'Without reinforcements, our positions there will be overrun in days.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41936, 'enUS', 'Light be with them, brother - and with us, for that matter. We\'re spread pretty thin these days.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41937, 'enUS', 'We\'ve destroyed hundreds of elementals, $n, and now they seem to be gaining strength!  Our guards are holding firm, but I\'m sure they could use your support.  King Varian Wrynn is leading the defense in the Trade district, Lady Jaina Proudmoore is helping defend the Mage district and Archbishop Benedictus is holding firm at the Cathedral of the Light.$b$bNo matter where you go in Stormwind, $n, you will be more than helpful.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41938, 'enUS', '$n! Stormwind is under siege by elementals and needs your strength!$b$bWe\'ve evacuated most of the city.  King Wrynn is holding the Trade District while Lady Proudmoore and the Archbishop defend the Mage and Cathedral Districts.$b$bWherever you go, you\'ll be of great help.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41939, 'enUS', 'We\'ve beaten back the elementals, $n.  But they have left behind odd portals.$b$bThe King has gone through the portal, contrary to reason, to see where they lead. The portals have been reported in the Trade district, Cathedral district and Mage district$b$bI must remain here to see to the clean up of the city and the return of the citizens.  Go and help the King!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41940, 'enUS', 'Your claims are outrageous, General.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41941, 'enUS', 'The only thing you will find at Thal\'darah Grove are terrified young druids. They have no weapon of mass destruction.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41942, 'enUS', 'Careful, tauren, your words could be construed as treason.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41943, 'enUS', 'Yessir.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41944, 'enUS', 'My scouts have informed me that the grove holds a powerful weapon. A weapon that the Alliance will use against us if they are not stopped.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41945, 'enUS', 'Furthermore, I am here by the authority of Overlord Krom\'gar, who was himself appointed by our Warchief, Garrosh Hellscream. You will do his bidding or suffer the consequences.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41946, 'enUS', 'I have sent my boy to Thal\'darah Grove to fetch you proof. You will soon see that they pose no threat to us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41947, 'enUS', 'Arf!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41948, 'enUS', '%s seems to be appreciative.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41949, 'enUS', 'I\'m not going anywhere until I find her!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41950, 'enUS', 'I think I can just make it out from here. Right on the edge of the horizon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41951, 'enUS', 'The new island - just surfaced after the last earthquakes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41952, 'enUS', 'It\'s what we\'re fighting for. On this front, at least.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41953, 'enUS', 'This\'ll be my third time now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41954, 'enUS', 'Toughest fighting I\'ve seen...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41955, 'enUS', 'We lose that island to the Horde and Stormwind\'s as good as gone... and they know it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41956, 'enUS', '', 'My baby boy... NO!!!!! WHY!???', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41957, 'enUS', 'Masha, go to our tent and hide yourself away.', 'Masha, go to our tent and hide yourself away.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41958, 'enUS', 'I will not leave you! Do not forget that I am the huntress in this family!', 'I will not leave you! Do not forget that I am the huntress in this family!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41959, 'enUS', '%s sighs.', '%s sighs.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41960, 'enUS', 'Oh Masha...', 'Oh Masha...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41961, 'enUS', 'That\'s enough, soldier.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41962, 'enUS', 'Stormwind still stands, and we won\'t be losing anything to the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41963, 'enUS', 'GENERAL! Come out from your tent and face me!', 'GENERAL! Come out from your tent and face me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41964, 'enUS', 'Now get your minds right! We\'ll be killing orcs within the hour.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41965, 'enUS', 'Make what out, exactly?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41966, 'enUS', 'Have you been there?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41967, 'enUS', 'Yeah, I... I think I can see it too.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41968, 'enUS', 'Foolish old tauren, you dare display such disrespect?!', 'Foolish old tauren, you dare display such disrespect?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41969, 'enUS', 'What is the meaning of this interruption?', 'What is the meaning of this interruption?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41970, 'enUS', 'Did you lose this, General?', 'Did you lose this, General?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41971, 'enUS', 'My insignia! Yes. I did lose it. Where did you find it, tauren?', 'My insignia! Yes. I did lose it. Where did you find it, tauren?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41972, 'enUS', '$n found it.', '$n found it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41973, 'enUS', 'On the corpse of my only child, you miserable son of a pig.', 'On the corpse of my only child, you miserable son of a pig.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41974, 'enUS', '%s laughs.', '%s laughs.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41975, 'enUS', 'Your boy was weak. Spineless. Just like his father.', 'Your boy was weak. Spineless. Just like his father.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41976, 'enUS', 'And as for you, $n, disappointing. You are a disgrace to the Horde!', 'And as for you, $n, disappointing. You are a disgrace to the Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41977, 'enUS', 'The punishment for you all is DEATH!', 'The punishment for you all is DEATH!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41978, 'enUS', 'That was for my son!', 'That was for my son!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41979, 'enUS', 'Masha, we have to run. We must get far away from here. They will come for us.', 'Masha, we have to run. We must get far away from here. They will come for us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41980, 'enUS', 'Thank you for returning Wolf.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41981, 'enUS', 'For the Alliance!', 'For the Alliance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41982, 'enUS', 'For the Horde!', 'For the Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41983, 'enUS', 'Listen up! Our transport should be here momentarily.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41984, 'enUS', 'Fall in!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41985, 'enUS', 'Alright, folks... Once task 37066 is implemented, the boat will arrive \'now-ish\'. In the meantime, I invite you to join us in a little dance to fill the time...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41986, 'enUS', 'Don\'t be shy now, we\'re all friends here...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41987, 'enUS', 'Alright, men! Move out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41988, 'enUS', 'We bring victory...', 'We bring victory...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41989, 'enUS', 'I laugh at you, feeble $n, but I will oblige. Now, let us return to my master, below!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41990, 'enUS', 'Someone is using these beakers and alchemy tools to perform experiments with various types of submarine fuel.', 'Someone is using these beakers and alchemy tools to perform experiments with various types of submarine fuel.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41991, 'enUS', 'Our people were held as prisoners, as slaves, in this very camp by the humans. I was locked in these cages; I saw my brothers held in these stocks, hung in these gallows.$B$BI saw Orgrim Doomhammer, leader of us all, stabbed in the back. His blood stained black the earth we stand on.$B$BAnd look at us now. This is our legacy: Hammerfall, where we lost everything, and then took it back with our own hands.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41992, 'enUS', 'Several small samples of oil sit in front of you, along with an empty beaker.  The beaker looks like it can probably fit about five samples inside of it.$b$bWhat kind of oil will you take first?', 'Several small samples of oil sit in front of you, along with an empty beaker.  The beaker looks like it can probably fit about five samples inside of it.$b$bWhat kind of oil will you take first?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41993, 'enUS', 'What type of oil will you select next?', 'What type of oil will you select next?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41994, 'enUS', 'You now have two samples of oil.  Which sample will you select next?', 'You now have two samples of oil.  Which sample will you select next?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41995, 'enUS', 'You now have three samples.  Which sample will you select next?', 'You now have three samples.  Which sample will you select next?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41996, 'enUS', 'You have four samples, and only have room to test one more.  Which one will it be?', 'You have four samples, and only have room to test one more.  Which one will it be?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41997, 'enUS', 'You have selected all five of your samples.', 'You have selected all five of your samples.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41998, 'enUS', 'Terrapin Oil Sample', 'Terrapin Oil Sample', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41999, 'enUS', 'Remora Oil Sample', 'Remora Oil Sample', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42000, 'enUS', 'Hammerhead Oil Sample', 'Hammerhead Oil Sample', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42001, 'enUS', 'You can use these beakers and alchemy tools to perform experiments with various types of submarine fuel.', 'You can use these beakers and alchemy tools to perform experiments with various types of submarine fuel.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42002, 'enUS', 'Let\'s get started!', 'Let\'s get started!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42003, 'enUS', 'Mix the samples together!', 'Mix the samples together!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42004, 'enUS', 'I want to start over.', 'I want to start over.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42005, 'enUS', '', 'How dare you storm into my throne room and attempt to spread this conspiratorial nonsense!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42006, 'enUS', '', 'I will not let you and this treasonous Commander Stonebreaker steal my son\'s birthright!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42007, 'enUS', 'That is quite an accusation, Moira. It\'s hardly your throne room in the first place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42008, 'enUS', 'I know Commander Stonebreaker and I will vouch for his loyalty and his honesty. We must take his word.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42009, 'enUS', 'Here, I made a Billowing Fuel Sample.  It\'s really smoky!', 'Here, I made a Billowing Fuel Sample.  It\'s really smoky!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42010, 'enUS', 'Here, I made an Atomic Fuel Sample.  It seems to be a little too hot.', 'Here, I made an Atomic Fuel Sample.  It seems to be a little too hot.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42011, 'enUS', 'Here, I made an Anemic Fuel Sample.  It seems... weak.', 'Here, I made an Anemic Fuel Sample.  It seems... weak.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42012, 'enUS', 'What cause do the Dark Irons have for taking up arms against Ironforge? Are you unable to control your own people, Moira?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42013, 'enUS', 'Here, I made a Perduring Fuel Sample.  What do you think?', 'Here, I made a Perduring Fuel Sample.  What do you think?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42014, 'enUS', 'Here, I made a Smoky Fuel Sample.  Pretty, isn\'t it?', 'Here, I made a Smoky Fuel Sample.  Pretty, isn\'t it?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42015, 'enUS', 'Here, I made a Quick-Burning Fuel Sample.  Is this good enough?', 'Here, I made a Quick-Burning Fuel Sample.  Is this good enough?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42016, 'enUS', 'Here, I made a Promising Fuel Sample.  Three parts hammerhead and two parts remora.', 'Here, I made a Promising Fuel Sample.  Three parts hammerhead and two parts remora.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42017, 'enUS', 'Muradin has a point. If you cannot govern your own people, you have no place at this table.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42018, 'enUS', 'Bah!  <cough>  Far too smoky!  <cough>   Looks like you put in too much terrapin oil!  Try again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42019, 'enUS', 'Yes, this will definitely burn too hot.  Do you want to blow up the ship?  Cut back on remora oil next time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42020, 'enUS', 'You\'re right, far too weak.  Needs more remora.  Try it again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42021, 'enUS', 'This looks pretty good... but it burns too long!  I need to be able to stop the fire instantly.  Cut back on hammerhead oil next time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42022, 'enUS', '', 'Both of you know I would never order such a thing! Why would I set one half my son\'s inheritance against the other?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42023, 'enUS', 'We\'re getting close... but it\'s still a little too smoky.  Don\'t put in any terrapin oil next time.  <cough>  Too much smoke.  Give it another shot!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42024, 'enUS', 'How\'d you manage to make this one?  It burns like flash powder... far too quick.  Add more hammerhead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42025, 'enUS', 'This is it!  The perfect bio-fuel!  Good work, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42026, 'enUS', '', 'No, this is the work of a traitor to both our peoples. Despite my best efforts, some Dark Irons remain in the grip of Twilight\'s Hammer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42027, 'enUS', 'You have discovered a new type of bio-fuel!  Bring it to Engineer Hexascrub for testing.', 'You have discovered a new type of bio-fuel!  Bring it to Engineer Hexascrub for testing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42028, 'enUS', '', 'The cult must have someone in the city to coordinate this kind of attack. That only leaves one suspect, Ambassador Slaghammer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42029, 'enUS', 'Watch now, Cliffwalker, as this battle comes to its glorious conclusion.', 'Watch now, Cliffwalker, as this battle comes to its glorious conclusion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42030, 'enUS', 'You are about to cross a terrible threshold, Krom\'gar. May the Earth Mother have mercy on your soul.', 'You are about to cross a terrible threshold, Krom\'gar. May the Earth Mother have mercy on your soul.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42031, 'enUS', 'You have discovered a new type of bio-fuel!  Bring it to Fiasco Sizzlegrin for testing.', 'You have discovered a new type of bio-fuel!  Bring it to Fiasco Sizzlegrin for testing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42032, 'enUS', 'Bah!  <cough>  Far too smoky!  <cough>   Looks like you put in too much terrapin oil!  Try again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42033, 'enUS', 'Yes, this will definitely burn too hot.  Do you want to blow up the ship?  Cut back on remora oil next time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42034, 'enUS', 'You\'re right, far too weak.  Needs more remora.  Try it again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42035, 'enUS', 'This looks pretty good... but it burns too long!  I need to be able to stop the fire instantly.  Cut back on hammerhead oil next time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42036, 'enUS', 'We\'re getting close... but it\'s still a little too smoky.  Don\'t put in any terrapin oil next time.  <cough>  Too much smoke.  Give it another shot!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42037, 'enUS', 'How\'d you manage to make this one?  It burns like flash powder... far too quick.  Add more hammerhead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42038, 'enUS', 'This is it!  The perfect bio-fuel!  Good work, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42039, 'enUS', 'Glad to see that Dark Iron scum is finally getting what he deserves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42040, 'enUS', 'Quiet! Moira\'s in a foul mood and if she hears you, she\'ll have your head...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42041, 'enUS', 'These are grave times for Azeroth, $n.', 'These are grave times for Azeroth, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42042, 'enUS', 'I need a totem to break the elemental prisons!', 'I need a totem to break the elemental prisons!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42043, 'enUS', 'Do as you will with me. I am not afraid. That halfbreed runt will never be my emperor! Long live Twilight\'s Hammer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42044, 'enUS', '', 'If we were in Shadowforge City, I would have you tossed into the Ring of Law for your transgressions, Ambassador. The darkest dungeons of Ironforge will have to do for now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42045, 'enUS', 'Look upon the world, Cliffwalker, and see the might of the Horde!', 'Look upon the world, Cliffwalker, and see the might of the Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42046, 'enUS', 'I go willingly, $c. My purpose in my master\'s plan is fulfilled.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42047, 'enUS', 'What have you done, Krom\'gar?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42048, 'enUS', 'I\'ll escort you to your new \'accommodations\', Your Excellency.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42049, 'enUS', 'Warchief! I... I was carrying out your command!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42050, 'enUS', 'My command?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42051, 'enUS', 'Was my command to murder innocents, Krom\'gar?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42052, 'enUS', 'Magmaw begins to break free from the single chain binding him!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42053, 'enUS', 'Warchief... Sir... I...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42054, 'enUS', 'Am I a murderer, Krom\'gar?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42055, 'enUS', 'No, Warchief!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42056, 'enUS', 'Then I ask you again: WHAT HAVE YOU DONE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42057, 'enUS', 'I sent you into Stonetalon Mountains with an army.  Your orders were to secure this land for the Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42058, 'enUS', 'Instead, you laid waste to the land. Murdered innocents. Children even...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42059, 'enUS', 'I spent a very long time in Northrend, Krom\'gar. I learned much about the Horde in that time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42060, 'enUS', 'While there, a wise old war hero told me something that I would carry with me forever...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42061, 'enUS', '\"Honor,\" Krom\'gar, \"No matter how dire the battle... never forsake it.\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42062, 'enUS', 'Overlord Krom\'gar, you have disgraced the Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42063, 'enUS', 'You have brought shame to us as a people.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42064, 'enUS', 'By my right as Warchief, I hereby relieve you of duty.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42065, 'enUS', 'YOU ARE DISMISSED.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42066, 'enUS', 'And you, $r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42067, 'enUS', 'Wait, Warchief! Please! $n was the hero responsible for uncovering this corruption. $g He:She; tried to stop Krom\'gar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42068, 'enUS', 'Have mercy, Warchief.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42069, 'enUS', 'Mercy... Your wife and child were murdered. Your kin wiped out. Your home burned to the ground.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42070, 'enUS', 'Mercy... Chieftain, on this day I learn from you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42071, 'enUS', 'Mountaineer Barleybrew said you could help me get to Loch Modan.', 'Mountaineer Barleybrew said you could help me get to Loch Modan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42075, 'enUS', '$n, Krom\'gar\'s army is no more. Your rank no longer has meaning. If you wish to truly help the Horde, your considerable power could be used in Desolace or in the Southern Barrens.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42076, 'enUS', 'The choice is yours to make.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42077, 'enUS', 'Let honor guide you, $n. Do not forget that Hellscream\'s eyes are always upon you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42078, 'enUS', '<Furien groans.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42079, 'enUS', 'Ha ha! There\'s no way they\'ll miss that, cannon fire or not.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42080, 'enUS', 'Don\'t worry - if they try to pretend they didn\'t see it, I\'ll swim over there and drag them off of their ships myself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42081, 'enUS', 'We\'ve killed dozens of naga to get this far, but they still keep coming.  What will it take to escape this wretched cavern?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42082, 'enUS', 'I don\'t care if I die inside this husk, $n.  I\'ll pay these naga back for every one of my fellow soldiers they\'ve killed or enslaved!$b$bThe others can worry about escape.  I\'ll worry about doling out punishment to these snakes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42083, 'enUS', '', 'Why are they doing this?  For pearls?  For war?  This ancient, Nespirah, is far more powerful than the naga understand.  If they\'re able to turn her towards their will... the damage they could cause would be tremendous.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42084, 'enUS', 'Sitting here injured and watching my brothers fight is almost too much to bear, but even I understand that I\'m more valuable alive than dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42085, 'enUS', '', 'I don\'t like the feel of this place, $n.  Naga sorceresses and slavedrivers aside... all I can sense inside this cavern is pain.  The ancient one is trying to cry out, but it cannot speak.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42086, 'enUS', 'We need your help, $c.  We\'ve fought tooth and nail to get here, but the naga keep coming.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42087, 'enUS', 'My wounds are not healing, $r.  I fear I may die inside this creature\'s gut if we do not act soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42088, 'enUS', 'If you have naga blood on your hands, then you\'re an ally of mine.$b$bA long time ago, I swore I\'d never be anyone\'s slave again, and I kept that bargain until Idra\'kess captured us.  Now I can barely contain my fury.  All I want is blood... not food, not sleep, not even victory... only blood.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42089, 'enUS', 'Aww, Budd. Now what\'d ya go an\' do that for?\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42090, 'enUS', 'Civilians...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42091, 'enUS', 'Fall in, men!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42092, 'enUS', 'That island on the horizon is our destination.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42093, 'enUS', 'It\'s where we will do our duty, for our king, for Stormwind and for her people...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42094, 'enUS', 'Every orc you slay today will bring honor to them, and to every soldier who has come before you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42095, 'enUS', 'Hellscream has sent his best to take this island. We shall see to it that none return home...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42096, 'enUS', 'Today Hellscream shall learn the lesson that Thrall failed to teach him...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42097, 'enUS', 'Stormwind shall never fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42098, 'enUS', 'You\'ve been paid good coin for non-stop passage, miss. We have no time for delays...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42099, 'enUS', 'Now make ready, for soon we\'ll be ashore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42100, 'enUS', 'A stake has been driven into the rock, and a long braid of rope draped around it.$B$BThis must have some purpose.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42101, 'enUS', 'Tie your lure off and let it loose.', 'Tie your lure off and let it loose.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42102, 'enUS', '<L\'ghorek\'s voice booms inside your mind.>$B$BI AM ALMOST... DEAD. YOU MUST HELP... STOP... THE TWILIGHT\'S HAMMER.$B$BTHEY DRAIN... MY ENERGY TO... CREATE... THEIR ASCENDANTS. THEY ARE A... GRAVE THREAT IN... THE IMMINENT WAR... VERSUS... NEPTULON.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42104, 'enUS', 'I believe you had a turtle prepared for me?', 'I believe you had a turtle prepared for me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42107, 'enUS', 'Tha elements be crying out in rage. It ain\'t no coincidence dat da naga are here. Dey be wantin\' someting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42108, 'enUS', 'I am ready to join you in the vision, Farseer.', 'I am ready to join you in the vision, Farseer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42109, 'enUS', 'Let me know when ya be ready ta leave.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42110, 'enUS', '', 'Budd! Noooo!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42111, 'enUS', '', 'Move forward! Hold the bridge!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42113, 'enUS', 'Take us out, Mister Gearchum.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42114, 'enUS', 'Chart a course east with all possible speed. We\'ll rendezvous with the main fleet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42115, 'enUS', 'What is the status on new recruits, Marshal? We desperately need more soldiers. The Blackrock orcs invading our territory must be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42116, 'enUS', 'Oh gods, someone get me out of this cage!', 'Oh gods, someone get me out of this cage!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42117, 'enUS', 'I\'m too young to die as a sacrifice!', 'I\'m too young to die as a sacrifice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42118, 'enUS', 'When I get out of here, I\'m going to kill every last one of you Twilight\'s Hammer scum!', 'When I get out of here, I\'m going to kill every last one of you Twilight\'s Hammer scum!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42119, 'enUS', 'You don\'t have to sacrifice me. I\'d make a great cultist!', 'You don\'t have to sacrifice me. I\'d make a great cultist!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42120, 'enUS', 'You cultists are cowards. You make me sick!', 'You cultists are cowards. You make me sick!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42121, 'enUS', 'Hey you. Yeah, you. You\'re not one of the cultists. Get me out of here!', 'Hey you. Yeah, you. You\'re not one of the cultists. Get me out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42122, 'enUS', 'One of them has the key. Find it and get me out of here!', 'One of them has the key. Find it and get me out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42123, 'enUS', 'I never thought that it would end like this.', 'I never thought that it would end like this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42124, 'enUS', 'Thank you, $n. Thank you!', 'Thank you, $n. Thank you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42125, 'enUS', 'I never thought that I\'d be rescued by you, $n!', 'I never thought that I\'d be rescued by you, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42126, 'enUS', 'I\'ll make sure to tell Captain Taylor that you\'re destroying the Hammers in here.', 'I\'ll make sure to tell Captain Taylor that you\'re destroying the Hammers in here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42127, 'enUS', '$n is on the job! I better get back to the captain.', '$n is on the job! I better get back to the captain.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42128, 'enUS', 'They\'re transforming themselves into some kind of super-elemental they call an Ascendant. You gotta stop them, $n!', 'They\'re transforming themselves into some kind of super-elemental they call an Ascendant. You gotta stop them, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42129, 'enUS', 'I thought I was a goner for sure. Thank you, $n!', 'I thought I was a goner for sure. Thank you, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42130, 'enUS', 'See you on the other side, $g brother : sister;. Which way is out?', 'See you on the other side, $g brother : sister;. Which way is out?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42131, 'enUS', 'What about this one, McBride? Surely this $c could join the cause!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42132, 'enUS', 'Excellent. $n, you are going to make a fine $c. McBride will teach you everything that you will need to know to join the ranks of the Stormwind guard!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42133, 'enUS', 'I\'ve been in here for days. They were just about to sacrifice me!', 'I\'ve been in here for days. They were just about to sacrifice me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42134, 'enUS', 'Farewell, $n. Until we meet again...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42135, 'enUS', 'FOR THE HORDE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42136, 'enUS', 'This land belongs to the Horde!  Attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42137, 'enUS', 'Crush these Nethergarde maggots!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42138, 'enUS', 'Feel the wrath of the Okril\'lon!  Blasted Lands is ours!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42139, 'enUS', 'Hellscream\'s eyes are upon you, men!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42140, 'enUS', 'Match altitude and speed, Mister Gearchum.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42141, 'enUS', 'Signal the fleet to fall into formation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42142, 'enUS', 'Someone get me out of this cage!', 'Someone get me out of this cage!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42143, 'enUS', 'I\'m too good-looking to die as a sacrifice!', 'I\'m too good-looking to die as a sacrifice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42144, 'enUS', 'When I get out of here, I\'m going to rip every last one of you Twilight\'s Hammer curs to shreds!', 'When I get out of here, I\'m going to rip every last one of you Twilight\'s Hammer curs to shreds!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42145, 'enUS', 'Maybe we can make a deal. I\'d make a great cultist!', 'Maybe we can make a deal. I\'d make a great cultist!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42146, 'enUS', 'You cultists are cowards. You make me sick!', 'You cultists are cowards. You make me sick!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42147, 'enUS', 'You over there. You\'re not one of the cultists. Get me out of here!', 'You over there. You\'re not one of the cultists. Get me out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42148, 'enUS', 'One of them has the key. Find it and get me out of this cage!', 'One of them has the key. Find it and get me out of this cage!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42149, 'enUS', 'I will not die powerless like this!', 'I will not die powerless like this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42150, 'enUS', 'I give you thanks, $n.', 'I give you thanks, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42151, 'enUS', 'I never thought that I\'d be saved by you, $n!', 'I never thought that I\'d be saved by you, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42152, 'enUS', 'I\'ll tell Legionnaire Nazgrim that you\'re destroying the Hammers in here.', 'I\'ll tell Legionnaire Nazgrim that you\'re destroying the Hammers in here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42153, 'enUS', 'I better get back to Nazgrim.', 'I better get back to Nazgrim.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42154, 'enUS', 'They\'re changing themselves into some kind of super-elemental they call an Ascendant. You must stop them, $n!', 'They\'re changing themselves into some kind of super-elemental they call an Ascendant. You must stop them, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42155, 'enUS', 'I thought I was dead for sure. Thanks, $n!', 'I thought I was dead for sure. Thanks, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42156, 'enUS', 'See you outside, $g brother : sister;. Is that the way out?', 'See you outside, $g brother : sister;. Is that the way out?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42157, 'enUS', 'I\'ve been in here for days. They were about to sacrifice me!', 'I\'ve been in here for days. They were about to sacrifice me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42158, 'enUS', 'Peering through the portal, you see the inner portion of an underground cave.  The sound of water falling is muffled by the sound of earth shifting.  This portal must have been opened for a reason, but why?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42159, 'enUS', 'Enter the portal.', 'Enter the portal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42160, 'enUS', 'As you near the portal, you are struck by intense heat radiating out. The sound of hammers echoing through a canyon create a faint steady rhythm all around.  Where does this portal go?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42161, 'enUS', 'Enter the Portal.', 'Enter the Portal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42162, 'enUS', 'What say you, $n? We need all the help we can muster if we are to defeat Deathwing and his minions!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42163, 'enUS', 'Yes. Highness! We will prepare $n for the coming battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42164, 'enUS', 'Order the fleet into close formation. Keep it tight, everyone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42165, 'enUS', '', 'Our allies have arrived! Push them back over the edge, my brethren.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42166, 'enUS', 'Sir! The fleet is straight ahead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42167, 'enUS', 'Approaching the Thandol Span, sir.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42168, 'enUS', '', 'We are victorious.  In Azshara\'s name!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42169, 'enUS', '', 'My Battlemaiden, once again you have earned my respect, and the respect of all our people.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42170, 'enUS', '', 'You have surpassed your title. You are a Battlemaiden no longer. Take your new crown, Nar\'jira, champion of Azshara.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42171, 'enUS', '', 'With the summoning complete and the armies of Azshara gathering for war, Neptulon the Tidehunter will soon fall.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42172, 'enUS', 'Ach, it\'s hard enough keeping order around here without all these new troubles popping up!  I hope you have good news, $n...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42173, 'enUS', 'I wish to ride the Stormwind charger, sir.', 'I wish to ride the Stormwind charger, sir.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42175, 'enUS', 'We have suffered a major setback, but we must see our mission through nevertheless.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42176, 'enUS', 'Pull yourself together, $n.$b$bWe must make for the breach and prevent the naga from entering the Plane of Water!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42177, 'enUS', 'Wow, it\'s totally wrecked! Let\'s try somewhere else.', 'Wow, it\'s totally wrecked! Let\'s try somewhere else.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42178, 'enUS', 'Heart of Orgrimmar, you\'re coming in too low. Too low!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42179, 'enUS', 'Pull up! Pull down! PULL SOMETHING!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42180, 'enUS', '|TInterface\\Icons\\spell_fire_selfdestruct.blp:20|t%s rears back and casts |cFFFF0000|Hspell:77982|h[Searing Flame]|h|r!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42181, 'enUS', 'Arise, friend. We have no time to lick our wounds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42182, 'enUS', 'While the loss of this vehicle is a setback, we must persist. We must succeed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42183, 'enUS', 'Think they\'ll ever fix all this? Let\'s go to the other side.', 'Think they\'ll ever fix all this? Let\'s go to the other side.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42184, 'enUS', 'I kill off any stragglers and send an event...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42185, 'enUS', 'We must make our way to the crater!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42186, 'enUS', 'The submarine\'s gonna blow! Move out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42187, 'enUS', 'Where does it hurt?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42188, 'enUS', 'That foul sea-witch has overpowered our ship! Brace for impact!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42189, 'enUS', '%s throws his spell focus to the ground!', '%s throws her spell focus to the ground!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42190, 'enUS', '', 'You have foolishly trespassed upon our waters, Horde fools. Be whipped by the lash of my storm!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42191, 'enUS', 'Oh, the Goblinity!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42192, 'enUS', 'The valley should mask our movements. Stay low. Stay close.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42193, 'enUS', 'I don\'t want to see any more mistakes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42194, 'enUS', 'Fellow warriors of the Horde. Hear me now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42195, 'enUS', 'Ahead hides our foe. Worshippers of chaos who seek to remake the world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42196, 'enUS', 'A new world is coming, friends. But it is not the world of their design.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42197, 'enUS', 'Together, we will crush the Twilight\'s Hammer, raze their stronghold,', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42198, 'enUS', 'salt the earth, and burn the bodies. All will tremble at our might. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42199, 'enUS', 'Then we will stand astride this world as its masters -', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42200, 'enUS', 'united in our conviction, unrivaled in strength, beholden to no one. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42201, 'enUS', 'Today, WE will remake the world. OUR world. FOR THE HORDE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42202, 'enUS', 'Coming up on the Twilight Highlands, Captain. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42203, 'enUS', 'Look - dwarves. Hah! They sulk like prairie dogs in holes in the ground.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42204, 'enUS', 'Captain, sir! Alliance naval fleet sighted off the port bow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42205, 'enUS', 'The Alliance steams in close formation without escort.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42206, 'enUS', 'Air guard, attack!! Strafe them now, while they cannot maneuver!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42207, 'enUS', 'What - WHAT!? What does he think he\'s DOING? The carriers are unprotected.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42208, 'enUS', 'What kind of madman orders away his close air support?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42209, 'enUS', 'Mister Gearchum, signal the fleet. Defensive formations!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42210, 'enUS', 'We\'re without our escort. I want to see interlocking fields of fire. Now, now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42211, 'enUS', '|TInterface\\Icons\\spell_fire_selfdestruct.blp:20|tThe Blind Dragon rears back and casts |cFFFF0000Searing Flame!|r', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42212, 'enUS', 'A winner.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42213, 'enUS', 'I would like to start the Blood Ritual, Kasim.', 'I would like to start the Blood Ritual, Kasim.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42214, 'enUS', 'Very well.  Stand back... this may get messy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42215, 'enUS', 'The blood ritual is complete... and I can now see where three amulets await you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42216, 'enUS', 'I would like to start the Amulet Ritual, Kasim.', 'I would like to start the Amulet Ritual, Kasim.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42217, 'enUS', 'With the power within the amulets of Razelikh...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42218, 'enUS', '... I bind you to his lair!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42219, 'enUS', 'Turn your head and cough twice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42220, 'enUS', 'To have seen the things I\'ve seen, to have done the things I\'ve done, and to still breathe... I am a lucky gnome.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42221, 'enUS', 'What the WHAT!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42222, 'enUS', 'The guns, man the guns! Fire everything we\'ve got!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42223, 'enUS', 'It\'s an attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42224, 'enUS', 'It\'s a bloodbath, lieutenant. They\'ve been murdered.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42225, 'enUS', 'Given the body temperature, I\'d say they\'ve been dead no more than 6 hours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42226, 'enUS', 'Damn shame what they did to Old Blanchy...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42227, 'enUS', 'No kidding, rookie...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42228, 'enUS', 'Looks like they really put the cart...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42229, 'enUS', '...before the horse.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42230, 'enUS', 'Operation: Gnomeregan was a success. Sure, that dastardly Thermaplugg had an unforeseen trick up his sleeve, but we have him on the run! It\'s only a matter of time before Gnomeregan is cleaned up and in our hands again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42231, 'enUS', 'Harvesting', 'Harvesting', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42232, 'enUS', 'Cooking for the King is an honor, not a job.  Do you have time to help me on some errands?', 'Cooking for the King is an honor, not a job.  Do you have time to help me on some errands?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42233, 'enUS', 'Never stop training, $n. I want every gnome ready for the day when we take down the traitor Thermaplugg for good.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42234, 'enUS', '', 'The end of Operaton: Gnomeregan meant a chance to rest for most people. However, my assistants and I have been caring for the wounded and for the survivors that the S.A.F.E. teams rescue from inside the city.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42235, 'enUS', '', 'Things can get a little stuffy here in Nethergarde.  Sometimes a dwarf just needs a brew to take her mind off her duty for a bit.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42236, 'enUS', 'I was just taking a rest.  Eternal watch is not as easy as it sounds, $r.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42237, 'enUS', 'Return to L\'ghorek!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42238, 'enUS', '', 'Erunak, take a look at this. This looks like a sufficient focus for the vision magics I was speaking to you about.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42239, 'enUS', 'Erunak examines the shard and nods in agreement.', 'Erunak examines the shard and nods in agreement.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42240, 'enUS', '', 'I will prepare the enchantment right away then. This may finally shed some light on the means and intentions of the naga.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42241, 'enUS', 'The Trade District is secured!  Now, secure the rest of the city!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42242, 'enUS', '', 'We have secured the Mage District! All invaders have been repelled and the evacuation has been completed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42243, 'enUS', '', 'Greetings, $n. I can offer you training in the way of the mage.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42244, 'enUS', 'The Cathedral District is under Stormwind Control again. The Light always prevails!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42245, 'enUS', '', 'I\'m sorry, $c, I cannot help you. You\'ll have to find a $c trainer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42246, 'enUS', 'Begone, Alliance thief!', 'Begone, Alliance thief!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42247, 'enUS', 'Sorry, I cannot train you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42248, 'enUS', 'Keep pressing the fight, $n!', 'Keep pressing the fight, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42249, 'enUS', 'Don\'t let up, $n!', 'Don\'t let up, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42250, 'enUS', 'Show these monsters the might of Stormwind, $n!', 'Show these monsters the might of Stormwind, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42251, 'enUS', 'Fight on, $n!', 'Fight on, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42252, 'enUS', 'Continue the attack, $n!', 'Continue the attack, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42253, 'enUS', 'It seems your reputation as a formidable $c is well earned, $n!', 'It seems your reputation as a formidable $c is well earned, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42254, 'enUS', 'Your skills in battle are impressive, $n.', 'Your skills in battle are impressive, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42255, 'enUS', 'How can Stormwind fall when we have $cs such as $n to defend it?  FOR STORMWIND!', 'How can Stormwind fall when we have $cs such as $n to defend it?  FOR STORMWIND!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42256, 'enUS', 'They attacked while you were gone... the Captain fell fighting them off. The rest retreated to the cave, you should do the same.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42257, 'enUS', 'Fresh air! By the light, I missed it. We have some ships just in sight to the north, $n - fire the flare in their direction.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42258, 'enUS', 'Fresh air! Grom\'s beard, I missed it. We have some ships just in sight to the north, $n - fire the flare in their direction.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42259, 'enUS', 'Say \"Ahhhhh\".', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42260, 'enUS', 'When was your last check up?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42261, 'enUS', 'What are you doing?  You threaten my work!  The Tainted Scar\'s revival is all thanks to me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42262, 'enUS', 'Take two potions and call me in the morning.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42263, 'enUS', 'You should consider losing some weight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42264, 'enUS', 'I only wanted to... do something... good...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42265, 'enUS', 'Before the day is done, you will all be slaves and corpses!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42266, 'enUS', 'Another brief confrontation with naga here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42267, 'enUS', 'Press forward...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42268, 'enUS', 'Die, worm!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42269, 'enUS', 'More delays... Form up, men!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42270, 'enUS', 'Our goal is in sight - press forward...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42271, 'enUS', 'Neptulon\'s minions are growing desperate! We must hurry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42273, 'enUS', 'We\'ve been spotted! Make ready!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42274, 'enUS', '', 'Kelsey Steelspark of Gnomeregan Covert Operations at your service! If you\'re looking for rogue training, you\'ve come to the right place.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42275, 'enUS', '', 'I\'m sorry, I can\'t offer you training. You\'ll have to find someone more familiar with your skills.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42276, 'enUS', '', 'Welcome back, $n. That took longer than expected. Learn anything useful?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42277, 'enUS', 'Erunak! We don\'t have time for this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42278, 'enUS', 'We\'re being besieged by the naga attempting to breech our airlocks! Defend the ship topside!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42279, 'enUS', 'Goblin! Status report!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42280, 'enUS', 'Continue the bombardment of the ocean floor! Show those Naga the might of the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42281, 'enUS', 'Naga attacking from the port and starboard! Faceless ones attacking head on...! We\'re heavily  outnumbered, but the ship is holding, sir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42282, 'enUS', '', 'This miserable can is nothing compared to the might of <Kraken Name here>!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42283, 'enUS', '', 'The Park\'s been ruined... we\'ll have to go another way, today. Miserable dragon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42284, 'enUS', 'More naga! Brace yourselves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42285, 'enUS', '', 'Thank the waves, you\'re back! The others have already retreated, we need to get out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42286, 'enUS', 'L\'ghorek Dies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42287, 'enUS', 'Quickly, $n!  We haven\'t much time before Razelikh makes his return.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42288, 'enUS', '', 'Quickly, $n!  We haven\'t much time before Razelikh makes his return.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42289, 'enUS', '<These baby Rockpool murlocs look toward the ocean.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42290, 'enUS', '<Continue helping the Rockpool tadpoles.>', '<Continue helping the Rockpool tadpoles.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42291, 'enUS', 'Hold on!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42292, 'enUS', 'Ah!  Get back over here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42293, 'enUS', 'Citizens of Stormwind! Begin to evacuate the city!', 'Citizens of Stormwind! Begin to evacuate the city!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42294, 'enUS', 'Guests of Stormwind please find shelter from the coming storm!', 'Guests of Stormwind please find shelter from the coming storm!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42295, 'enUS', 'Clear the streets!', 'Clear the streets!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42296, 'enUS', 'Evacuate the city!  Clear the streets of Stormwind!', 'Evacuate the city!  Clear the streets of Stormwind!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42297, 'enUS', 'Enough!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42298, 'enUS', 'Enough!  The Earthen Ring will temporarily stop the invasion.  But be warned, the elementals will return!', 'Enough!  The Earthen Ring will temporarily stop the invasion.  But be warned, the elementals will return!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42299, 'enUS', 'We must make haste! The elementals cannot endure this siege.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42300, 'enUS', '', 'You have foolishly trespassed upon our waters, Alliance fools. Be whipped by the lash of my storm!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42301, 'enUS', '', 'Ahh, visitorsss!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42302, 'enUS', '', 'What an unexpected sssurprise.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42303, 'enUS', '', 'You mussst forgive me... I have no time to ssstay and play.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42304, 'enUS', '', 'In my absssence, my ssservantsss will... sssee to your needs. ', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42305, 'enUS', '', 'Do enjoy yourssselves!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42306, 'enUS', 'Pssst...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42307, 'enUS', 'You need something, $g mack:lady;?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42308, 'enUS', 'Whatever you need, I can get...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42309, 'enUS', 'Nice stuff. You must be rich.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42310, 'enUS', 'What\'re you doin\' out here? Slummin\'?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42311, 'enUS', 'Whaddya need? Information? I got that too!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42312, 'enUS', 'You lookin\' for somethin\' a little more... exotic?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42313, 'enUS', 'I think we can do business.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42314, 'enUS', 'Keep your head low, $g buddy:lady;.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42315, 'enUS', 'We don\'t die that easily, sea-witch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42316, 'enUS', 'If we are to die, let it be with honor against our hated enemies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42318, 'enUS', 'What the hell happened here?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42319, 'enUS', 'The final battle against the naga is imminent. We will triumph or die trying in the Abyssal Breach.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42320, 'enUS', 'Looks like the Furlbrow\'s were murdered...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42321, 'enUS', 'What\'s happened to us? We\'re in worse shape now than when the Defias ran amok!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42322, 'enUS', 'Yep. That King is good for nothin\'.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42323, 'enUS', 'Let\'s get out of here before they try and pin this on us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42324, 'enUS', 'Good call.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42325, 'enUS', 'Before we can join the final battle, we must ensure that whatever\'s happening inside of that Ancient across from us is put to an end. Otherwise, we will not stand a chance.$B$BLend Erunak your full assistance, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42326, 'enUS', 'L\'ghorek is the name of the Ancient god that sits across the chasm from us. It is being infested by something.$B$BWe must put an end to this or we will certainly all meet our doom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42327, 'enUS', 'In spite of our best efforts, the naga have attacked the breach!$B$BThe Plane of Water must be defended!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42328, 'enUS', 'There is a battle brewing at the Abyssal Breach. I hope that the reinforcements that arrived with the Pincer X2 will be enough.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42329, 'enUS', 'Let us in!', 'Let us in!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42330, 'enUS', 'Down with the king!', 'Down with the king!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42331, 'enUS', 'This is horse poop!', 'This is horse poop!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42332, 'enUS', 'What right do you have keeping us out?', 'What right do you have keeping us out?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42333, 'enUS', 'We\'re hungry and cold! Help us!', 'We\'re hungry and cold! Help us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42334, 'enUS', 'Heartless monsters!', 'Heartless monsters!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42335, 'enUS', 'Is this equality and justice for all?', 'Is this equality and justice for all?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42336, 'enUS', 'What do you expect us to do?', 'What do you expect us to do?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42337, 'enUS', 'Before we join the final battle at the Abyssal Breach, we must stop whatever\'s happening inside of that Ancient across from us. Otherwise, we will fail.$B$BLend Erunak all of your might, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42338, 'enUS', 'The final battle against the naga has begun. We will destroy them or gloriously die trying in the Abyssal Breach.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42339, 'enUS', '', 'You ask me, I think we let the soup and gravy take care of this ready and write.$B$BIt\'s bird lime to kick and prance ourselves out of here before we\'re all brown bread.$B$BCatch my meaning, love?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42340, 'enUS', 'WHOA THERE, BUDDY!', 'WHOA THERE, BUDDY!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42341, 'enUS', 'Where do you think you two are going?', 'Where do you think you two are going?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42342, 'enUS', 'We\'re just trying to get a decent meal and maybe a comfortable night\'s sleep.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42343, 'enUS', '', 'Just between you and me, hon, I\'m too short to pull this kind of duty. One more month and I\'m out.$B$BI\'ve already died once. No sense doing it a second time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42344, 'enUS', 'Can\'t you read, pal? Probably not, huh?', 'Can\'t you read, pal? Probably not, huh?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42345, 'enUS', 'The sign here says: \"ABSOLUTELY NO BUMS!\" Now get lost!', 'The sign here says: \"ABSOLUTELY NO BUMS!\" Now get lost!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42346, 'enUS', '', 'Dealing with the first ancient, Nespirah, was hard enough. The one across from us, L\'ghorek, is dying.$B$BI can sense its cries of agony. Something is stealing its life force. But to what end?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42347, 'enUS', 'Dirty, rotten pig-lickers. You\'ll get your come-uppance some day.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42348, 'enUS', 'Let\'s get the hell out of here. I hope this place burns to the ground.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42349, 'enUS', '', 'I wish that I was going to fight by your side in the upcoming battle, but Erunak wants me to remain here.$B$BGood luck, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42350, 'enUS', '', 'These depths truly are a nightmare.$B$BAlthough, the battle that is about to be joined down in the breach below will make this look like kittens and rainbows.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42351, 'enUS', 'JACKPOT!', 'JACKPOT!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42352, 'enUS', 'Looks like I found us a savory and clean piece of dirt! Tonight we eat like kings, Mr. Penguin! Of course I\'ll share it with you! You\'re my best friend!', 'Looks like I found us a savory and clean piece of dirt! Tonight we eat like kings, Mr. Penguin! Of course I\'ll share it with you! You\'re my best friend!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42353, 'enUS', 'I\'m starving!', 'I\'m starving!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42354, 'enUS', 'HAHAHAH! Good one, Mr. Penguin! GOOD ONE!', 'HAHAHAH! Good one, Mr. Penguin! GOOD ONE!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42355, 'enUS', 'Get off my land!', 'Get off my land!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42356, 'enUS', 'I don\'t think I\'m gonna make it...', 'I don\'t think I\'m gonna make it...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42357, 'enUS', 'What happened to me? I used to be the king of Stormwind!', 'What happened to me? I used to be the king of Stormwind!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42358, 'enUS', 'Looks like we\'ll be eating shoe leather for dinner again.', 'Looks like we\'ll be eating shoe leather for dinner again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42359, 'enUS', 'I wonder if it\'s possible to eat rocks? Got plenty of rocks around here. Just imagine it! I\'d be the richest person in the world for making that discovery!', 'I wonder if it\'s possible to eat rocks? Got plenty of rocks around here. Just imagine it! I\'d be the richest person in the world for making that discovery!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42360, 'enUS', 'I miss being robbed by the Defias. At least they\'d leave you a little something to eat from time to time.', 'I miss being robbed by the Defias. At least they\'d leave you a little something to eat from time to time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42361, 'enUS', 'HEY! HEY YOU! GET OFF MY PROPERTY!', 'HEY! HEY YOU! GET OFF MY PROPERTY!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42362, 'enUS', 'Does $g he:she; have boots? Get \'em!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42363, 'enUS', 'MINE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42364, 'enUS', 'Check $g his:her; pockets!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42365, 'enUS', 'Whaddya want?', 'Whaddya want?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42366, 'enUS', 'I ain\'t tellin\' you nothin\'!', 'I ain\'t tellin\' you nothin\'!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42367, 'enUS', 'Get lost.', 'Get lost.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42368, 'enUS', 'Oh the big hero has arrived. I\'m saved! Yea, right...', 'Oh the big hero has arrived. I\'m saved! Yea, right...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42369, 'enUS', 'You wanna talk? Pay up.', 'You wanna talk? Pay up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42370, 'enUS', 'Did you see who killed the Furlbrows?', 'Did you see who killed the Furlbrows?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42371, 'enUS', 'Maybe a couple copper will loosen your tongue. Now tell me, did you see who killed the Furlbrows?', 'Maybe a couple copper will loosen your tongue. Now tell me, did you see who killed the Furlbrows?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42372, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42373, 'enUS', 'Are you sure you want to give this hobo money?', 'Are you sure you want to give this hobo money?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42374, 'enUS', 'You can\'t buy me! DIE!', 'You can\'t buy me! DIE!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42375, 'enUS', 'The only thing I\'ll tell you is to get the hell out of here!', 'The only thing I\'ll tell you is to get the hell out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42376, 'enUS', 'You got no business here, lout! Beat it!', 'You got no business here, lout! Beat it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42377, 'enUS', 'They died of natural causes...', 'They died of natural causes...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42378, 'enUS', 'You\'ll get nothing out of me!', 'You\'ll get nothing out of me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42379, 'enUS', 'Judgment day comes! The brotherhood will be reborn!', 'Judgment day comes! The brotherhood will be reborn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42380, 'enUS', 'Listen, $g pal:lady;, I don\'t want any trouble, ok? I didn\'t see who murdered \'em, but I sure heard it! Lot of yelling. Human voices... you dig? Now get out of here before I change my mind about beating you up and takin\' your shoes.', 'Listen, $g pal:lady;, I don\'t want any trouble, ok? I didn\'t see who murdered \'em, but I sure heard it! Lot of yelling. Human voices... you dig? Now get out of here before I change my mind about beating you up and takin\' your shoes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42381, 'enUS', 'I didn\'t see who killed \'em, $g bub:lady;, but I got a whiff. Smelled rich, kinda like you. Damn shame too. Furlbrows were a fixture around here. Nice people, always willin\' to share a meal or a patch of dirt.', 'I didn\'t see who killed \'em, $g bub:lady;, but I got a whiff. Smelled rich, kinda like you. Damn shame too. Furlbrows were a fixture around here. Nice people, always willin\' to share a meal or a patch of dirt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42382, 'enUS', 'Who killed the Furlbrows? I\'ll tell you who killed the Furlbrows: KING VARIAN WRYNN, THAT\'S WHO! And he\'s killin\' the rest of us too. One bum at a time. The only thing I can tell you is that I saw some gnolls leavin\' the place a few hours before the law arrived.', 'Who killed the Furlbrows? I\'ll tell you who killed the Furlbrows: KING VARIAN WRYNN, THAT\'S WHO! And he\'s killin\' the rest of us too. One bum at a time. The only thing I can tell you is that I saw some gnolls leavin\' the place a few hours before the law arrived.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42383, 'enUS', 'Between you, me, and the tree, murlocs killed the Furlbrows. Yep, saw \'em with my own two eyes. Think they\'d been casin\' the joint for days, maybe months. They left in a hurry once they got wind of \"Johnny Law\" and the idiot brigade over there...', 'Between you, me, and the tree, murlocs killed the Furlbrows. Yep, saw \'em with my own two eyes. Think they\'d been casin\' the joint for days, maybe months. They left in a hurry once they got wind of \"Johnny Law\" and the idiot brigade over there...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42384, 'enUS', 'You rich punks are all the same!', 'You rich punks are all the same!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42385, 'enUS', 'I ain\'t tellin\' you a damn thing!', 'I ain\'t tellin\' you a damn thing!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42386, 'enUS', 'Livin\' like an animal is hard enough without your insults!', 'Livin\' like an animal is hard enough without your insults!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42387, 'enUS', 'Maybe I can sell your liver for some gold.', 'Maybe I can sell your liver for some gold.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42388, 'enUS', 'I TOLD YOU TO PAY UP, PAL!', 'I TOLD YOU TO PAY UP, PAL!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42389, 'enUS', 'Now you\'ve gone and done it! TIME FOR THE FIST!', 'Now you\'ve gone and done it! TIME FOR THE FIST!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42390, 'enUS', 'You want my foot in your front or in your rear? Make the call.', 'You want my foot in your front or in your rear? Make the call.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42391, 'enUS', 'Begin the Assault!', 'Begin the Assault!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42392, 'enUS', 'These are grave times for Azeroth, $n.  The elementals have imprisoned both citizens around the city and those they battle with.  We can give you a totem that will break the elemental prison.', 'These are grave times for Azeroth, $n.  The elementals have imprisoned both citizens around the city and those they battle with.  We can give you a totem that will break the elemental prison.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42393, 'enUS', 'You heard him, men. We make our stand here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42394, 'enUS', 'Let us re-convene by my camp, as friends and fellow heroes, to discuss our final and most dangerous quest.  Hup Hup, Pimento!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42395, 'enUS', 'Fight on, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42396, 'enUS', 'Lok\'tar, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42397, 'enUS', 'Your deeds bring glory and honor to the Horde, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42398, 'enUS', 'Bring these invaders a swift death, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42399, 'enUS', 'Crush these elementals, $n!  Drive them from our land!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42400, 'enUS', 'This is it! The entrance to Neptulon\'s realm...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42401, 'enUS', 'Fight dem back, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42402, 'enUS', 'Don\'t be lettin\' up, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42403, 'enUS', 'De elements don\'t stand a chance against ya, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42404, 'enUS', 'De elements be in for a world of hurt!  Follow my lead, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42405, 'enUS', 'Ya got some powerful mojo, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42406, 'enUS', '', 'Come, children of Azshara! At last, we lay claim to the Plane of Water!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42407, 'enUS', '', 'Go, my naga! Welcome the mighty Tidehunter to Azeroth!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42408, 'enUS', 'We assembled a mighty force and defeated Thermaplugg\'s servants, fighting our way into the city. But, the usurper Thermaplugg had one last trick up his sleeve...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42409, 'enUS', 'Sandfury trolls working with the Twilight\'s Hammer?! No matter. That foul witch won\'t be sending her monster to MY city.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42410, 'enUS', 'Come, brave ones! We\'ll slick the sand with their gore and return home triumphant.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42411, 'enUS', 'We were forced to retreat. Thermaplugg continues to hide behind the radiation, sending his crony, Crushcog, to harass us here on the surface.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42412, 'enUS', 'The Valley of Strength is Horde again! This pathetic attack on Orgrimmar will be crushed.  For the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42413, 'enUS', 'Da voodoo is da strength of da horde.  Da Valley of da Sprits is now under Vol\'jin control! ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42414, 'enUS', 'The Valley of Honor has been secured!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42415, 'enUS', 'The Pincer X2 will be arriving shortly! Get your gear and form up, boys!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42417, 'enUS', 'The Pincer X2 has docked. All aboard!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42418, 'enUS', 'What little humie want? Why you call Glubtok?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42419, 'enUS', '', 'Sad... Is this the life that you had hoped for, Glubtok? Running two-bit extortion operations out of a cave?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42420, 'enUS', 'Glubtok crush you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42421, 'enUS', '', 'Oh will you? Do you dare cross that line and risk your life?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42422, 'enUS', 'What option two?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42423, 'enUS', '', 'You may attempt to kill me - and fail - or you may take option two.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42424, 'enUS', '', 'You join me and I shower wealth and power upon you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42425, 'enUS', 'So Glubtok have two choices: die or be rich and powerful?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42426, 'enUS', 'Well done, $n!  Stormwind is in your debt.', 'Well done, $n!  Stormwind is in your debt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42427, 'enUS', 'Glubtok take choice two.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42428, 'enUS', '', 'I thought you\'d see it my way.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42429, 'enUS', '', 'I will call for you when the dawning is upon us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42430, 'enUS', 'GET!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42431, 'enUS', 'OFF!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42432, 'enUS', 'MY!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42433, 'enUS', 'SHIP!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42434, 'enUS', 'You and your scaly worms are no match for me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42435, 'enUS', '', 'You fool! This day shall be your last!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42436, 'enUS', 'Get a totem from any Earthen Ring member! Use it to free citizens or defenders from the Elemental Prisons!', 'Get a totem from any Earthen Ring member! Use it to free citizens or defenders from the Elemental Prisons!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42437, 'enUS', 'They\'re after the Warchief -', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42438, 'enUS', 'The Warchief\'s zeppelin is in flames! It\'s going down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42439, 'enUS', 'Did you... Did you meet her?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42440, 'enUS', 'Yep. She\'s for real.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42441, 'enUS', 'She wanted me to tell you lugs that she appreciates the job that we did for her on the Furlbrows. Gave me a pile o\' gold to split with you all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42442, 'enUS', 'See her face? Is it really...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42443, 'enUS', 'Whoa, what do we have here? Looks like we have ourselves an eavesdropper, boys.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42444, 'enUS', 'Only one thing to do with a lousy, good-for-nothin eavesdropper.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42445, 'enUS', 'DIE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42446, 'enUS', 'Hurry back to the Furlbrow\'s Cottage!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42447, 'enUS', 'Doesn\'t look good, rookie.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42448, 'enUS', 'The land suffers... it cries for mercy, $r, it cries for mercy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42449, 'enUS', 'This was an execution. Whoever did this was sending a message...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42450, 'enUS', 'A message for anyone that would dare snitch on these criminals.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42451, 'enUS', 'It would appear that poor Lou really put his foot...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42452, 'enUS', 'In his mouth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42453, 'enUS', 'Please heroes, help my brothers and we shall assist you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42454, 'enUS', 'Thank you for rescuing me, now please aid the rest of my brood!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42455, 'enUS', 'Feeding a Horde army is no easy task.  I have lots to do, do you have time to help?', 'Feeding a Horde army is no easy task.  I have lots to do, do you have time to help?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42456, 'enUS', 'You were standing right here! What the hell did you see? Speak up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42457, 'enUS', 'I... I didn\'t see nothin\'! He... he died of natural causes.', 'I... I didn\'t see nothin\'! He... he died of natural causes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42458, 'enUS', 'Natural causes? Two bullets in the chest and his shoes are on his head. What kind of natural death would that be?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42459, 'enUS', '', 'We must do whatever it takes to recombine the fragments of the World Pillar.$B$BBut first, we must regain all of them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42460, 'enUS', 'Been in Westfall all my life, $g brother:sister;. I can remember a day when Westfall was the lushest land this side of Stranglethorn Vale. Those days are long gone.$B$BWhile we no longer have Defias causing problems, the fallout from their reign of terror can still be felt. To make matters worse we\'re in the worst economic downturn in history. All that gold we spent on fighting the Scourge left anyone not in a military related occupation jobless and eventually homeless.$B$BGuess where all those people ended up?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42461, 'enUS', 'Enough!  The Earthen Ring will temporarily stop the invasion.  But bewarned, the elementals will return!', 'Enough!  The Earthen Ring will temporarily stop the invasion.  But bewarned, the elementals will return!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42462, 'enUS', 'Egah - all hands aft. One\'s aboard. Shoot it! Kill it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42463, 'enUS', 'Somebody, DO SOMETHING! That\'s an order!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42464, 'enUS', 'It\'s tangled up in the rigging.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42465, 'enUS', 'Well done, heroes. Let us return through the very portal that witch meant to send this beast through! Much work awaits me in Stormwind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42466, 'enUS', 'Place barricades around Orgrimmar to protect the city!$B$BPick up a barricade from a box scattered around the city$B$Band then place them at a location marked by a red arrow |TInterface\\Icons\\ABILITY_HUNTER_MARKEDFORDEATH.blp:32|t.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42467, 'enUS', 'Oh sure, she said, it\'ll be a simple trip out into the wastes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42468, 'enUS', '', 'Thank you, $n. Your kindness will not be forgotten.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42469, 'enUS', '', 'Dinner\'s ready! Come and get it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42470, 'enUS', 'I\'m so happy. Thank you, mama and thank you, $n.', 'I\'m so happy. Thank you, mama and thank you, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42471, 'enUS', 'She\'s going to blow! Get a parachute and jump!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42472, 'enUS', 'YAY FOR $n!', 'YAY FOR $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42473, 'enUS', '%s cries.', '%s cries.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42474, 'enUS', '%s buries itself in the sand!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42475, 'enUS', 'You may only ride the Overloaded Harvest Golem at the Molsen Farm.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42476, 'enUS', 'Initiating cleanup ... ...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42477, 'enUS', 'She\'s breaking up, sir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42478, 'enUS', 'Tamp down the inflammants. Batten up the gear carriage. Rudder for the shore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42479, 'enUS', 'Everyone, to the parachutes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42480, 'enUS', 'Mayday! Mayday! Mayday! Bail out everyone. BAIL!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42481, 'enUS', '%s disperses!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42482, 'enUS', 'Even here, among the surf and sun, I can feel the darkness that lurks in these lands.$B$BIt\'s fascinating, don\'t you think?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42483, 'enUS', 'Even here, among the surf and sun, I can feel the darkness that lurks in these lands.$B$BIt\'s fascinating, isn\'t it?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42484, 'enUS', 'Da elementals be gone!  Da Valley of Spirits be ours!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42485, 'enUS', 'We are proud to call ourselves the last tribe of Forest Trolls to serve the Horde.$B$BIn time, we will overcome the other tribes, and the dwarves, and the Hinterlands will be ours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42486, 'enUS', 'Thank you, kind and gentle stranger.', 'Thank you, kind and gentle stranger.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42487, 'enUS', 'Westfall stew? I\'ll never forget this moment!', 'Westfall stew? I\'ll never forget this moment!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42488, 'enUS', 'I\'d know that smell anywhere! Salma\'s famous stew!', 'I\'d know that smell anywhere! Salma\'s famous stew!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42489, 'enUS', 'Maybe... maybe life is worth living.', 'Maybe... maybe life is worth living.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42490, 'enUS', 'Bless you, friend.', 'Bless you, friend.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42491, 'enUS', 'Perhaps one day I will repay you for this kind act.', 'Perhaps one day I will repay you for this kind act.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42492, 'enUS', 'I can\'t help ya. Go talk ta Voldreka if ya want warlock trainin\'.', 'I can\'t help ya. Go talk ta Voldreka if ya want warlock trainin\'.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42493, 'enUS', 'Wit\' practice, ya\'ll find that the dark arts can be much stronga than any weapon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42494, 'enUS', '', 'The trolls here are quite accepting of my kind. After all, they spoke to the dead for centuries before the Forsaken even existed.$B$BIt\'s good for business.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42495, 'enUS', '', 'In my youth, my family did not approve of my interest in shamanism. Brash and foolish as I was, I ran away from them and practiced on my own.  Upon returning to my home one day, I found my parents gone. My home destroyed. They never found who did it.\n\nI\'ve spent my life striving to extend my shamanistic visions to see into the past - to find out what happened to my family and who is responsible. I\'ve come a great way. I\'ve learned to focus in on impactful events in the recent past, but I have much further to go if I ever hope to glimpse something that occurred so long ago.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42496, 'enUS', 'I misplaced the enchanted blade that you gave me. Have you seen it?', 'I misplaced the enchanted blade that you gave me. Have you seen it?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42497, 'enUS', '<Wind howls past the statue as you stand in front of it.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42498, 'enUS', 'Akil\'darah, return me to my allies.', 'Akil\'darah, return me to my allies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42499, 'enUS', 'Jintha\'alor gonna be ours, mon. The vilebranch be weak, nasty traitors, undeservin\' of the favor of the loa.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42500, 'enUS', 'Send me to Akil\'darah at the middle terrace.', 'Send me to Akil\'darah at the middle terrace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42505, 'enUS', 'You have our full cooperation, lieutenant. The Furlbrows were good people. Their deaths will not go unpunished.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42506, 'enUS', 'That\'s good to hear, marshal. While $n has brought the perpetrators to justice, their ringleader is still on the loose. Perhaps with the aid of the Westfall Brigade we will finally be able to solve this case.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42507, 'enUS', 'Might I ask, what\'s with the worgen in the stocks?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42508, 'enUS', 'We caught this beast slaughtering sheep in the countryside. He is unrepentant and easily agitated. Keep your distance, lieutenant.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42509, 'enUS', 'When I break out of these stocks I\'m going to rip out your heart and devour it whole!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42510, 'enUS', 'Wow, the marshal wasn\'t kidding.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42511, 'enUS', 'But is your bark...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42512, 'enUS', 'Worse than your bite?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42518, 'enUS', 'You\'ve reached too far, serpent. You shall suffer greatly for your trespass!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42526, 'enUS', '%s attempts to hide on the ocean floor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42527, 'enUS', 'The war in Northrend took its toll on us as a people, $n. While we were victorious in the frozen wastes, our own land fell into disarray.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42528, 'enUS', 'Do you know anything about this, beast? Who is the admiral? Speak!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42529, 'enUS', 'The dawning comes, Stoutmantle, and with it comes your end.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42530, 'enUS', 'Gibberish from a mad wolf. Bah!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42531, 'enUS', '', 'I\'ve got your back, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42532, 'enUS', '', 'Headshot!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42533, 'enUS', '', 'Not a chance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42534, 'enUS', '', 'Easy peasy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42535, 'enUS', '', 'Got him!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42536, 'enUS', 'The gnolls have failed, mistress. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42537, 'enUS', '', 'They provided the distraction I required. We continue as planned.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42538, 'enUS', 'But mistress, the admiral is sti...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42539, 'enUS', '', 'We will free the admiral during the dawning.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42540, 'enUS', 'Yes, mistress.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42541, 'enUS', '', 'Judgment day is soon upon us, Helix.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42542, 'enUS', '', 'Call for the people. I wish to speak to them one last time before the dawning.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42543, 'enUS', 'Moonbrook, mistress?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42544, 'enUS', '', 'Aye. Tonight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42545, 'enUS', '', 'To me, my minions! Strike now!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42546, 'enUS', '', 'Do not relent! He is weakening!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42547, 'enUS', 'Ahhhhh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42548, 'enUS', 'Nooooo!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42549, 'enUS', 'Naz\'jar and her honor guard! Fight for your lives!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42550, 'enUS', 'Naz\'jar and her honor guard... Fight for your lives!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42551, 'enUS', 'Little early to the party, aren\'t ya?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42552, 'enUS', 'This is the end of the line, pal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42553, 'enUS', 'Where do you think you\'re goin\'?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42554, 'enUS', 'If you ain\'t with us, you\'re against us! DIE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42555, 'enUS', 'Ready for inspection, $gsir:ma\'am;!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42556, 'enUS', 'This baby is all mine! I call her, \"The Throttler.\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42557, 'enUS', 'Ready for inspection! ...Be gentle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42558, 'enUS', 'See that? Ironclad!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42559, 'enUS', 'That\'s right. Takes a lickin\' but keeps on kickin\' butt!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42560, 'enUS', 'There is nothing left for us but to wait... and hope.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42561, 'enUS', 'Uh oh. Um. Oops.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42562, 'enUS', 'Look, look, it\'s okay! It\'s just a tiny little glitch. See?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42563, 'enUS', 'A little paint and it\'ll be fine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42564, 'enUS', 'I ... I never wanted to be an engineer anyways.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42565, 'enUS', 'I always wanted to be... a dancer! Power tools frighten me.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42566, 'enUS', 'I can float. On my toes. I\'m like a butterfly. Wanna see?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42567, 'enUS', '%s takes off running.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42568, 'enUS', 'It\'s still good enough to ship. I can fix it with a patch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42569, 'enUS', '', 'The rally is about to begin!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42570, 'enUS', '', 'Gather, brothers and sisters! Come, all, and listen!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42571, 'enUS', '', 'Brothers. Sisters. We are ABANDONED - the orphaned children of Stormwind.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42572, 'enUS', '', 'Our \"king\" sits atop his throne made of gold and shrugs at our plight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42573, 'enUS', '', 'Meanwhile, our children die of starvation on these very streets!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42574, 'enUS', '', 'HIS war, not ours, cost us our livelihood. WE paid for the Alliance\'s victories with our blood and the blood of our loved ones!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42575, 'enUS', '', 'The time has come, brothers and sisters, to stop this injustice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42576, 'enUS', '', 'The government of Stormwind, of the ALLIANCE, must be made accountable for what it has done to us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42577, 'enUS', '', 'Today, we are reborn! Today, we take a stand as men and women, not nameless, faceless numbers!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42578, 'enUS', '%s throws his fish at you and attempts to run away in fear!\n', '%s throws his fish at you and attempts to run away in fear!\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42579, 'enUS', 'Thank you for breaking me out of here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42580, 'enUS', 'I don\'t ever want to smell unwashed trogg again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42581, 'enUS', 'Thanks. Now, let\'s blow up that cave!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42582, 'enUS', 'You have no idea how happy I am to see you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42583, 'enUS', 'Finally, someone who\'s not a trogg!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42584, 'enUS', 'I\'m free! I\'m really free!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42585, 'enUS', 'Friends of the tribe, our city is under attack!  Take up your arms and defend our home!  If you do not wish to battle, then grab weather breakers and line our city to protect it\'s citzens from the elements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42586, 'enUS', 'Place weather breakers around Thunder Bluff to protect the city!$B$BPick up wind breaks in crates around the city$B$Band place them at locations marked with a red arrow |TInterface\\Icons\\ABILITY_HUNTER_MARKEDFORDEATH.blp:32|t.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42587, 'enUS', 'We\'ll get right on this, $gsir:ma\'am;!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42588, 'enUS', 'The Center Rise has been reclaimed!  The Earthmother be praised!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42589, 'enUS', 'We\'re on it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42590, 'enUS', 'Yes $gsir:ma\'am;! Right away, $gsir:ma\'am;! ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42591, 'enUS', '', 'We have secured the Hunter\'s Rise! All invaders have been repelled and the evacuation has been completed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42592, 'enUS', 'Spirit Rise is secure! The invaders have been eliminated and the evacuation is complete!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42593, 'enUS', 'There\'s nowhere left to hide, VanCleef! The Defias are THROUGH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42594, 'enUS', 'We\'re here to finish this! Face us, coward!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42595, 'enUS', 'None may challenge the Brotherhood!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42596, 'enUS', 'Make your preparations, $n, but do it quickly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42597, 'enUS', 'I am prepared, Erunak. Let\'s go!', 'I am prepared, Erunak. Let\'s go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42598, 'enUS', 'Cover the door - we\'ve got him!', 'Cover the door - we\'ve got him!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42599, 'enUS', 'Oh no not de bar! Take it outside, mon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42600, 'enUS', 'I\'m stopping my patrol now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42601, 'enUS', 'I\'m starting my patrol now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42602, 'enUS', 'Victory for the Alliance! Glory to Stormwind!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42603, 'enUS', 'Let us return to Sentinel Hill, allies, and inform Gryan of VanCleef\'s demise!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42604, 'enUS', '', 'Daddy...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42605, 'enUS', 'The wind and air elementals threaten to destroy Thunder Bluff.  If you think you are strong enough to go in there, be my guest.  Otherwise, stay out of the city and let us take care of it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42606, 'enUS', 'Elemental rifts have opened up around the city.  We need to shut them down now! Evacuate all non-combat ready citizens.  Make sure they get to safety!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42607, 'enUS', 'Keep pressing the fight, $n!', 'Keep pressing the fight, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42608, 'enUS', 'Don\'t let up, $n!', 'Don\'t let up, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42609, 'enUS', 'Fight on, $n!', 'Fight on, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42610, 'enUS', 'Continue the attack, $n!', 'Continue the attack, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42611, 'enUS', 'It seems your reputation as a formidable $c is well earned, $n!', 'It seems your reputation as a formidable $c is well earned, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42612, 'enUS', 'Your skills in battle are impressive, $n.', 'Your skills in battle are impressive, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42613, 'enUS', 'Leave this old Dragonmaw to his drink, $c.$B$BI\'ve fought creatures that would curl your spine and I\'ve seen my people all but slaughtered in the swamps of the wetlands and amidst the ruins of Outland.$B$BGet. Lost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42614, 'enUS', 'What\'s this, $c? You know my name?$B$BDo you know, then, of my reputation?$B$BI am Flametusk of the Dragonmaw clan. I commanded legions, flew on dragonback, witnessed the sundering of Grim Batol...$B$BCurse the Twilight\'s Hammer, and curse their ogre lapdogs. Look at me now. All talk and cheap liquor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42615, 'enUS', 'The Dragonmaw can be great again. Help our conquest of the Twilight Highlands - can you give me a map?', 'The Dragonmaw can be great again. Help our conquest of the Twilight Highlands - can you give me a map?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42616, 'enUS', 'I will help, but know that you\'re stepping into a viper\'s nest. Let me draw you a map - wha - behind you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42617, 'enUS', 'So, Twilight\'s Hammer is trying to finish me off? Honorless filth!$B$BI will definitely help you, $c. You say you need a map?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42618, 'enUS', '', 'No matter what\'s going on elsewhere, Ironforge hasn\'t forgotten this land, nor any of Khaz Modan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42619, 'enUS', 'You sense that you are being followed...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42620, 'enUS', 'You fight without honor and die like vermin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42621, 'enUS', 'Twilight scum.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42622, 'enUS', 'Feel the bite of the Dragonmaw!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42623, 'enUS', 'Another worm driven to the ground.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42624, 'enUS', 'Only the weak hide in shadows, Twilight filth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42625, 'enUS', 'Yes, a map of the coastal approaches.', 'Yes, a map of the coastal approaches.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42626, 'enUS', '%s scrawls out a depiction of his home port on a torn parchment.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42627, 'enUS', 'There you have it, $n. All the safe approaches to the Twilight Highlands. Unleash havoc on our foes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42628, 'enUS', 'Twilight\'s Hammer. Bah. Bunch of prancing purple... pussycats. Back to business. Gravy! What am I drinking?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42629, 'enUS', 'Good. Then keep it coming.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42630, 'enUS', 'You be drinkin\' everyting, mon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42631, 'enUS', '%s motions for you to speak with him again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42632, 'enUS', 'What are you still doing here, $c?$B$BShouldn\'t you be in Twilight Highlands laying waste to that wretched cult?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42633, 'enUS', 'They poisoned our supplies... we had cultists onboard all along...', 'They poisoned our supplies... we had cultists onboard all along...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42634, 'enUS', 'They tricked us into ambushing the Horde gunship... but why?', 'They tricked us into ambushing the Horde gunship... but why?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42635, 'enUS', 'I heard them praying to their dark gods as everything went black... the Twilight\'s Hammer did this!', 'I heard them praying to their dark gods as everything went black... the Twilight\'s Hammer did this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42636, 'enUS', 'Everyone started dropping like flies... everyone who ate the rations.', 'Everyone started dropping like flies... everyone who ate the rations.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42637, 'enUS', 'Twilight scum!  You did this to us!', 'Twilight scum!  You did this to us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42638, 'enUS', 'Throw de big skull in de pot!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42639, 'enUS', 'Throw de mojos on de skull!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42640, 'enUS', 'Magic\'s done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42641, 'enUS', 'Watch Nimboya do what Nimboya does best.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42642, 'enUS', 'Warriors. You\'ve fought hard in the frozen wastes. Your courage is to be tested again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42643, 'enUS', 'My warriors! You have already proven your valor. This is why the Warchief has selected you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42644, 'enUS', 'Heroes of the Horde! Are you ready to create history?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42645, 'enUS', 'We embark soon for distant shores, to hurl ourselves at the enemy homeland.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42646, 'enUS', 'Our enemy, cloaked in shadow, seeks to unmake our world. We will not lose another home.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42647, 'enUS', 'Our destination is the Twilight Highlands. We strike at the beating heart of our enemy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42648, 'enUS', 'We assemble an enormous fleet: land, sea, and air. We are legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42649, 'enUS', 'When our fury is unleashed, our enemies will cower!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42650, 'enUS', 'Our wrath will paint the highlands red  - or whatever color our enemies bleed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42651, 'enUS', 'The eyes of Hellscream are upon you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42652, 'enUS', 'Let\'s see some spirit. Strike! Strike! Strike!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42653, 'enUS', 'Show me what you\'ve got!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42654, 'enUS', 'Now. Show me your fury. Show me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42655, 'enUS', 'I don\'t like this, $n. Stay alert!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42656, 'enUS', '', 'You bastards will burn for what you did.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42657, 'enUS', 'Hope! Wha..', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42658, 'enUS', '', 'Hope? Is that what I was supposed to feel when I saw my father decapitated by your henchmen?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42659, 'enUS', '', 'Hope is a cruel joke, played upon us by a harsh and uncaring world. There is no Hope, there is only Vanessa. Vanessa VanCleef.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42660, 'enUS', '', 'RISE UP BROTHERHOOD! THE DAWNING DAY IS UPON US!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42661, 'enUS', '', 'Tie them up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42662, 'enUS', '', 'Admiral, your hat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42663, 'enUS', 'Thank you, my dear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42664, 'enUS', 'Boden the Imposing, I come on behalf of the Earthen Ring. We wish your kind no harm. We seek to repair the rift between our worlds. Why do you attack us?', 'Boden the Imposing, I come on behalf of the Earthen Ring. We wish your kind no harm. We seek to repair the rift between our worlds. Why do you attack us?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42665, 'enUS', 'Hah! Did you mistake me for Diamant, $r? Or perhaps some other whimpering, compliant stone trogg who cares?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42666, 'enUS', 'If you seek peace, relinquish the World Pillar and leave Deepholm. This is our realm. Your only welcome here shall be found underneath my stone foot.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42667, 'enUS', 'Jessup McCree, of McCree\'s Mountain Movers, at your service.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42668, 'enUS', 'If you need a mountain moved, I can do that. I can make \'em for you, too. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42669, 'enUS', 'This isn\'t the biggest job I\'ve done, not even close. This cataclysm may just be the best thing that ever happened to us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42670, 'enUS', 'What can I do for you, chief?', 'What can I do for you, chief?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42671, 'enUS', 'Thanks for the help. That one was far tougher for some reason.', 'Thanks for the help. That one was far tougher for some reason.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42672, 'enUS', '', 'Leave nothing but ashes in your wake, brothers! Burn Sentinel Hill to the ground!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42673, 'enUS', '', 'And you, $n, I will spare your life. You have done much to help our cause, albeit unwittingly, but the next time we meet it will be as enemies. ', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42674, 'enUS', 'Just tell me one thing, Vanessa.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42675, 'enUS', 'Why\'d you have the Furlbrows killed?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42676, 'enUS', '', 'I had no choice, lieutenant. They recognized me. The only people in the world who even knew I existed, recognized my face from when I was an infant.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42677, 'enUS', '', 'I took no pleasure in their deaths.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42678, 'enUS', '', 'So it is certain den. Dese elementals were bound from dormant stone only recently.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42679, 'enUS', '', 'Only an elemental lord could bind so many elementals so quickly. It seems we\'re at war with da Stonemother herself...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42680, 'enUS', '', 'May da elements protect us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42681, 'enUS', '$n, get to Stormwind. Tell King Wrynn everything. EVERYTHING! GO NOW!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42682, 'enUS', 'A chance to catch my breath! Thank you.', 'A chance to catch my breath! Thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42683, 'enUS', 'By the elements... that one... just wouldn\'t die...', 'By the elements... that one... just wouldn\'t die...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42684, 'enUS', 'Thanks for the relief. It\'s getting hairy out here.', 'Thanks for the relief. It\'s getting hairy out here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42685, 'enUS', 'I\'d started to think we\'d been left out here. Thanks for the hand!', 'I\'d started to think we\'d been left out here. Thanks for the hand!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42686, 'enUS', 'Careful out here, they don\'t stop coming.', 'Careful out here, they don\'t stop coming.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42687, 'enUS', 'Don\'t stay out here too long, there will be more on top of you any second.', 'Don\'t stay out here too long, there will be more on top of you any second.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42688, 'enUS', 'Thank you. I could use the rest.', 'Thank you. I could use the rest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42689, 'enUS', 'Nice job! Let\'s finish him off.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42690, 'enUS', 'Need a hand?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42691, 'enUS', 'I saw that move you pulled with the grapple. The boys will be hearing about that one when we get back for sure.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42692, 'enUS', 'Quick! Finish him while he\'s down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42693, 'enUS', 'That\'s more like it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42694, 'enUS', 'Not tossing boulders at us anymore, are ya?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42695, 'enUS', 'Where can I get one of those ropes?! That was brilliant.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42696, 'enUS', 'And now to make sure he doesn\'t get back up...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42697, 'enUS', 'Brothers and sisters, Ironforge is under attack!  Take up your arms and defend our capital!  If you do not want to answer the call of battle, then grab some fortifications and line help keep the walls from collapsing on us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42698, 'enUS', 'The Commons are secured!  Now, secure the rest of the city!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42699, 'enUS', 'Fortify the walls of Ironforge to prevent the city from collapsing!$B$BPick up wall fortifications in crates around the city$B$Band place them at locations marked with a red arrow |TInterface\\Icons\\ABILITY_HUNTER_MARKEDFORDEATH.blp:32|t.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42700, 'enUS', 'Do it!  Give him de raptor!  I don\'t like havin\' this sword in my face!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42701, 'enUS', 'I think I lost them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42702, 'enUS', 'Optical sensor malfunction detected! Shutting down...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42703, 'enUS', 'The Military Ward is clear of rifts and we have completed the evacuation of the citizens in this district!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42704, 'enUS', 'There\'d be no more elementals in the Mystic Ward now. We\'ve also cleared out all of those trapped folks too! ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42705, 'enUS', 'I\'VE BEEN INTERRUPTED! CALL ALEX!', 'I\'VE BEEN INTERRUPTED! CALL ALEX!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42706, 'enUS', 'The giant is down.  Kill it quickly!', 'The giant is down.  Kill it quickly!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42707, 'enUS', 'Ironforge is under attack!  You\'d better be strong enough to defend the city if ya want to go in there.$B$BKing Magni ordered a full evacuation of the city and put me in charge of seeing that through.  Don\'t go in there, get yerself hurt and then blame me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42708, 'enUS', 'I hope you can fly, pipsqueak!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42709, 'enUS', 'That\'s the way, $n!', 'That\'s the way, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42710, 'enUS', 'Don\'t ya let up now, $n!', 'Don\'t ya let up now, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42711, 'enUS', 'Keep on fightin\', $n!', 'Keep on fightin\', $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42712, 'enUS', 'Fight on, $n!', 'Fight on, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42713, 'enUS', 'Continue the attack, $n!', 'Continue the attack, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42714, 'enUS', 'Yer reputation as a formidable $c is well earned, $n!', 'Yer reputation as a formidable $c is well earned, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42715, 'enUS', 'Yer skills in battle are impressive, $n.', 'Yer skills in battle are impressive, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42716, 'enUS', 'Precisely how to do it, $n!', 'Precisely how to do it, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42717, 'enUS', 'Do not even think of letting up, $n!', 'Do not even think of letting up, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42718, 'enUS', 'Stay on the attack, $n!', 'Stay on the attack, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42719, 'enUS', 'You are a most effective fighter, $n!', 'You are a most effective fighter, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42720, 'enUS', 'Hey, $c!$B$BGot any leads on some lucrative contracts? I\'ll cut you in on one tenth! Of a percent! ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42721, 'enUS', 'What\'s the deal, $c?$B$BWord on the docks is that you\'ve been poking around my construction sites...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42722, 'enUS', 'Nobody shorts Garrosh Hellscream. You finish those ships - and finish them right - or this gets personal.', 'Nobody shorts Garrosh Hellscream. You finish those ships - and finish them right - or this gets personal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42723, 'enUS', '', 'Eek!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42724, 'enUS', 'Nobody tells me how to run my business here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42725, 'enUS', 'Be ready! The ground shakes and the fires burn hotter! Get out if ya still can!', 'Be ready! The ground shakes and the fires burn hotter! Get out if ya still can!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42726, 'enUS', 'Watch fer fallin\' rocks!  The ground shakes harder.  Flee the city!', 'Watch fer fallin\' rocks!  The ground shakes harder.  Flee the city!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42727, 'enUS', 'Citizens of Ironforge! Begin the evacuation!', 'Citizens of Ironforge! Begin the evacuation!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42728, 'enUS', 'Guests of Ironforge! Get out of the city! It may collapse at any moment!', 'Guests of Ironforge! Get out of the city! It may collapse at any moment!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42729, 'enUS', 'Watch out fer fallin\' rocks!  Clear the streets!', 'Watch out fer fallin\' rocks! Clear the streets!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42730, 'enUS', 'Flee the city!  The ground can\'t stop shakin\'!', 'Flee the city!  The ground can\'t stop shakin\'!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42731, 'enUS', 'The sky darkens and the elements are restless! Seek shelter!', 'The sky darkens and the elements are restless! Seek shelter!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42732, 'enUS', 'Be on your guard! The clouds move in and the wind picks up! Seek shelter!', 'Be on your guard! The clouds move in and the wind picks up! Seek shelter!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42733, 'enUS', 'Citizens of Thunder Bluff! Evacuate the city!', 'Citizens of Thunder Bluff! Evacuate the city!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42734, 'enUS', 'The wind grows stronger! Take the lifts to safer ground!', 'The wind grows stronger! Take the lifts to safer ground!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42735, 'enUS', 'Clear the paths! Stay away from the sides of the bluff!', 'Clear the paths! Stay away from the sides of the bluff!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42736, 'enUS', 'Evacuate Thunder Bluff!  Stay away from the sides of the bluff!', 'Evacuate Thunder Bluff!  Stay away from the sides of the bluff!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42738, 'enUS', 'It was just a couple of rivets. That ship had plenty!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42739, 'enUS', 'I\'m sure it would\'ve been seaworthy in calm waters.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42740, 'enUS', 'Less rivets mean a cleaner environment!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42741, 'enUS', '...can we settle this with cash?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42742, 'enUS', 'Okay okay, stop! Stop! You make a compelling argument.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42743, 'enUS', 'I\'ll double the worker shifts and put in my own money to get those ships seaworthy and back on schedule. Count on it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42744, 'enUS', 'Dere be nothin\' chaotic about dis attack. Da elementals and giants be organizin\' against us. It be seemin\' dat somehow we\'ve earned da ire of Therazane, da Stonemother herself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42745, 'enUS', 'Welcome to Zul\'Gurub, little one.  It might not look like much now, but give it time...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42746, 'enUS', 'Ahh... it\'s good to be home.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42747, 'enUS', 'There\'s plenty others of your kind here, but I wouldn\'t worry about competition.  You\'re from the bloodline of Ohgan, my oldest friend.  You see... our souls are intertwined, yours and mine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42748, 'enUS', 'Here we are.  Get ready for your training, little one.  I recommend you forget about that worthless $r $c and start thinking about your duties here in Zul\'Gurub.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42749, 'enUS', 'NO MORE! ONGOLONGO GO HOME!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42750, 'enUS', 'PUNY TROLLS NOT STOP ONGOLONGO!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42751, 'enUS', '', 'Priestess knows much about Zul\'Gurub.$b$bPriestess thinks you need her help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42752, 'enUS', 'I am ready for the Mind Vision ritual, Hu\'rala.', 'I am ready for the Mind Vision ritual, Hu\'rala.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42753, 'enUS', 'You get back in dat pen!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42754, 'enUS', 'Crushcog\'s defeat will send a powerful message to his master, Thermaplugg. The gnomes of Gnomeregan will not be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42755, 'enUS', 'I\'m ready to start the assault.', 'I\'m ready to start the assault.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42756, 'enUS', 'I have deduced that you must die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42757, 'enUS', 'I should have... predicted... this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42758, 'enUS', 'Mekgineer Thermaplugg refuses to acknowledge that his defeat is imminent! He has sent Razlo Crushcog to prevent us from rebuilding our beloved Gnomeregan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42759, 'enUS', 'But $n has thwarted his plans at every turn, and the dwarves of Ironforge stand with us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42760, 'enUS', 'Let\'s send him crawling back to his master in defeat!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42761, 'enUS', 'You! How did you escape detection by my sentry-bots?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42762, 'enUS', 'No matter! My guardians and I will make short work of you. To arms, men!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42763, 'enUS', 'We\'ve done it! We\'re victorious!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42764, 'enUS', 'SAMOPHLANGE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42765, 'enUS', 'With Crushcog defeated, Thermaplugg is sure to be quaking in his mechano-tank, and rightly so. You\'re next Thermaplugg. You\'re next!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42766, 'enUS', 'That\'ll teach you to mess with the might of Ironforge and Gnomeregan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42767, 'enUS', 'Aye, let\'s teach this addle-brained gnome a lesson!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42768, 'enUS', '$n stiff-arms Smoot and throws him to the ground.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42769, 'enUS', 'Oh samophlange... I have failed you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42770, 'enUS', 'They need you on the northwestern field! Go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42771, 'enUS', 'The temple is under siege. We need you outside, now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42772, 'enUS', 'Get to the battlefield!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42773, 'enUS', 'Keep moving! They need you on the field!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42774, 'enUS', 'We\'re holding them in the northwest - reinforce the line!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42775, 'enUS', 'Our brothers are falling to the northwest. Get out there and help them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42776, 'enUS', 'The earth elementals have turned on us. Get onto the field and hold them back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42777, 'enUS', 'We are besieged by elementals and giants! Your help is needed in the northwest, Earthguard!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42778, 'enUS', 'Bind at this location.', 'Bind at this location.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42779, 'enUS', 'Purchase food and water.', 'Purchase food and water.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42780, 'enUS', 'You may rest here, and there is food and water.', 'You may rest here, and there is food and water.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42781, 'enUS', 'You will never defeat the true sons of Gnomeregan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42782, 'enUS', 'All hail Samophlange!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42783, 'enUS', 'One Samophlange to rule them all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42784, 'enUS', 'Behold: The Samophlange.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42785, 'enUS', 'I have built it. And it is mine. Samophlange!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42786, 'enUS', 'Oh, Samophlange. Is there nothing you can\'t do?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42787, 'enUS', 'I love you, Samophlange!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42788, 'enUS', 'And upon the dawn of the first day the Samophlange beheld what it had wrought, and lo, it was good.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42789, 'enUS', 'You are brave to try to escape, young Ohgan\'aka.$b$bBefore you continue, let me teach you how to use your teeth to bite through the trolls\' nets.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42790, 'enUS', '<Learn to bite through nets from the elder raptor.>', '<Learn to bite through nets from the elder raptor.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42791, 'enUS', 'You are leaving the raptor dens?  I was commanded to guard this post and prevent escapes, but... I think I understand why you want to leave.$b$bFine.  You may pass, but first, I have a request.$b$bThe trolls often carry packs of raptor food, and I haven\'t been fed in days.  You don\'t stand a chance in combat, but you\'re small enough that you might be able to steal it from one of the sleeping trolls inside the tents...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42792, 'enUS', 'Alright, Tenjiyu.  I\'ll go get you some food from the sleeping trolls.', 'Alright, Tenjiyu.  I\'ll go get you some food from the sleeping trolls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42793, 'enUS', 'I\'ve brought some food for you.', 'I\'ve brought some food for you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42794, 'enUS', 'Quite impressive to have made it this far, Ohgan\'aka.  You are truly a talented young raptor... but the toughest test is yet to come.$b$bThe trolls patrol the bridges up ahead, and they will be difficult to distract.  If you drop a skull in their path, however, you may be able to cause them to trip and fall...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42795, 'enUS', '<Learn to drop skulls from the elder raptor.>', '<Learn to drop skulls from the elder raptor.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42796, 'enUS', 'Gotcha!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42797, 'enUS', 'I am ready for the Mind Control ritual, Hu\'rala.', 'I am ready for the Mind Control ritual, Hu\'rala.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42798, 'enUS', 'The line is secured to his ankle.  Pull away to trip him!', 'The line is secured to his ankle.  Pull away to trip him!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42799, 'enUS', '', 'Over here, you ugly whale!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42800, 'enUS', 'You!  Let me go now!  Wait... no!  Don\'t let go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42801, 'enUS', '$n, along with High Tinker Mekkatorque and Mountaineer Stonegrind, has defeated Razlo Crushcog!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42802, 'enUS', 'The victorious heroes have returned! Control of Chill Breeze Valley is ours once again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42803, 'enUS', '$gSir:Ma\'am;!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42804, 'enUS', '$gSir, yes sir:Ma\'am, yes ma\'am;!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42805, 'enUS', 'Three cheers for $n!', 'Three cheers for $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42806, 'enUS', '$gRight away, sir:Right away ma\'am;!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42807, 'enUS', 'Victory at last!', 'Victory at last!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42808, 'enUS', 'I\'m sorry, $gsir:ma\'am;!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42809, 'enUS', 'I\'ll drink to that!', 'I\'ll drink to that!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42810, 'enUS', 'It won\'t happen again, $gsir:ma\'am;.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42811, 'enUS', 'Please don\'t tell the Warlord...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42812, 'enUS', '', 'Meet me back up top.  This two-headed freak has a few questions to answer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42813, 'enUS', 'Ok, ok, I give up!  I\'ll tell you everything!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42814, 'enUS', 'chews out an AWOL grunt.', 'chews out an AWOL grunt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42815, 'enUS', 'You\'re no superior of mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42816, 'enUS', 'I think this $c is spoiling for a fight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42817, 'enUS', 'You want some of this!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42818, 'enUS', 'You want some of this!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42819, 'enUS', 'Stupid $r think $ghe\'s:she\'s; the boss of me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42820, 'enUS', '$C, you just earned yourself a beating.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42821, 'enUS', 'I\'m sorry - I\'ll return to my post!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42822, 'enUS', 'Enough! I shouldn\'t have questioned your orders...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42823, 'enUS', 'That\'s the best brawl I\'ve had in weeks! I\'ll save the rest for our enemies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42824, 'enUS', 'You fight well! I hope to have you by my side in the highlands.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42825, 'enUS', 'Blackrock Caverns, a level 79-81 dungeon on Blackrock Mountain, is now available for testing. Please be in your full group before teleporting to the dungeon. Meeting Stones are not available for this dungeon yet.\n\nTo leave the Halls of Stone, simply exit through the normal exit inside the dungeon and you will be teleported to Dalaran. If you die and release, you should teleport back from this NPC.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42826, 'enUS', 'I\'m in my group and ready to teleport to the Blackrock Caverns!', 'I\'m in my group and ready to teleport to the Blackrock Caverns!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42827, 'enUS', 'Take me back to Blackrock Caverns!', 'Take me back to Blackrock Caverns!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42828, 'enUS', 'The AWOL Grunt throws some dice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42829, 'enUS', 'Mekkatorque-Cannon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42830, 'enUS', 'The dice do not lie!', '', 26124);
