INSERT INTO `broadcast_text_locale` VALUES (42965, 'enUS', 'We got another $c inbound!', 'We got another $c inbound!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42971, 'enUS', 'Among the Twilight cult are magics that threaten us greatly.$B$BWe have every desire to wipe their existence from our home.$B$BWe are divided, and so we cannot.$B$BAnd so I watch...$B$BAnd wait...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42982, 'enUS', 'Another rockworm is upon us! Kill it quickly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42983, 'enUS', 'We are under attack! To arms!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42984, 'enUS', 'Another rockworm in the encampment! Send for the drakes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42985, 'enUS', 'By the elements, this one is even bigger than the last!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42986, 'enUS', 'Outgoing, $c.', 'Outgoing, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42987, 'enUS', 'Good hunting, $n!', 'Good hunting, $n!', 26124);
