INSERT INTO `broadcast_text_locale` VALUES (42989, 'enUS', '$C departing Redridge!', '$C departing Redridge!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42990, 'enUS', 'Good luck out there, $n!', 'Good luck out there, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42991, 'enUS', 'The Earthen Ring shall fall!', 'The Earthen Ring shall fall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42992, 'enUS', 'Deepholm shall collapse upon you!', 'Deepholm shall collapse upon you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42993, 'enUS', 'You face the glorious wrath of the Twilight!', 'You face the glorious wrath of the Twilight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42994, 'enUS', 'Deathwing\'s shadow settles over you.', 'Deathwing\'s shadow settles over you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42995, 'enUS', 'Therazane\'s doom is assured!', 'Therazane\'s doom is assured!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42996, 'enUS', 'Your world is broken! Soon, you shall join it in death!', 'Your world is broken! Soon, you shall join it in death!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42997, 'enUS', 'There is no hope for enemies of the Twilight!', 'There is no hope for enemies of the Twilight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42998, 'enUS', 'Dusk approaches! Tremble in fear!', 'Dusk approaches! Tremble in fear!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42999, 'enUS', 'Razlo Crushcog is no more! The people of Ironforge and Gnomeregan speak with one voice this day. Hear us well, Thermaplugg. The day of your defeat approaches!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43022, 'enUS', 'Better make that a rocket scientist.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43037, 'enUS', 'Strange... Just yesterday, I was dispatched to Ashenvale. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43038, 'enUS', 'Halfway there I got orders to meet you all here. This must be important.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43039, 'enUS', 'Have you not heard?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43040, 'enUS', 'An island has emerged from the depths... just off the coast of Stormwind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43041, 'enUS', 'We\'re gunna take it.', '', 26124);
