INSERT INTO `broadcast_text_locale` VALUES (43043, 'enUS', 'An island rose up from the sea... within striking distance of Stormwind? ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43044, 'enUS', 'Take me to Revantusk Village.', 'Take me to Revantusk Village.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43045, 'enUS', 'This is no coincidence! Azeroth herself has cast her lot with the Horde!', '', 26124);
