INSERT INTO `broadcast_text_locale` VALUES (43047, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43049, 'enUS', 'Here, warrior. Eat this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43050, 'enUS', 'Your reflexes will quicken, and your stamina will increase.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43051, 'enUS', 'Hah! I don\'t need your crutches, friend. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43052, 'enUS', 'I\'ve faced the best that Stormwind has to offer, and still I stand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43053, 'enUS', 'My axe has split as many Alliance skulls as yours, brother. Maybe more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43054, 'enUS', 'Hellscream will settle for nothing short of total victory. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43055, 'enUS', 'Should we fail, he\'ll chase us into the afterlife to take his retribution.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43056, 'enUS', 'There is wisdom in your words, brother. I\'ll take that food of yours...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43058, 'enUS', 'Indeed, brother. It would seem so...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43076, 'enUS', 'Best of luck, $n!', 'Best of luck, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43080, 'enUS', 'Aye, as will I.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43081, 'enUS', 'We are Hellscream\'s Vanguard!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43082, 'enUS', 'Why must we ride to battle in some pathetic mercenary vessel?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43083, 'enUS', 'The rest of the fleet has already been sent to the region.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43084, 'enUS', '...their return is long overdue.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43085, 'enUS', 'Hah! They should\'ve known to send us in the first place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43086, 'enUS', 'Agreed, brother, but the Horde fights many fires these days and the Vanguard are few in number...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43087, 'enUS', 'Fortunately for our enemies, we can be in only one place at a time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43088, 'enUS', '', 'Prepare for war! We will no longer tolerate the Vilebranch!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43089, 'enUS', 'Enough jawing! There\'s our ride.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43090, 'enUS', 'That ship\'s been commissioned by Hellscream himself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43091, 'enUS', 'You\'re all under strict orders - No harassing her crew!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43092, 'enUS', 'We must put an end to the corruption permeating Ashenvale.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43093, 'enUS', 'Gah... this ship reeks of human.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43094, 'enUS', 'Hah! Humans...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43095, 'enUS', 'Hear my words, brothers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43096, 'enUS', 'We sail to a virgin island, a gift from Azeroth to the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43097, 'enUS', 'Securing this land will win us our war!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43098, 'enUS', 'The enemy has fought well, for Alliance...but they grow weary.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43099, 'enUS', 'We shall crush what remains of them and lay claim to this land.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43100, 'enUS', 'From its shores, we shall conquer Stormwind, and with it any hope the enemy clings to.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43101, 'enUS', 'Azeroth will be reforged under a single banner!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43102, 'enUS', 'This banner!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43103, 'enUS', 'We can\'t afford to let our enemy, Thermaplugg, continue to tighten his grip on the rest of Gnomeregan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43104, 'enUS', 'He has ordered his crony, Razlo Crushcog, to keep us on the defensive. Hinkles, what\'s the latest intelligence on Razlo and his men?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43105, 'enUS', 'It\'s been hard for our scouts to get information on what Crushcog is planning.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43106, 'enUS', 'Come aboard, now. Step on up!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43107, 'enUS', 'Cap\'n Samir is navigatin\' this here ship non-stop to that there ocean over yonder...\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43108, 'enUS', '...where we\'ll commence to assistin\' the Horde in their little skirmish with them Alliance fellers.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43109, 'enUS', 'Easy money! Commission\'s to be dispersed evenly among any and all survivors!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43110, 'enUS', 'What we do know is that Crushcog is using an advanced mechano-tank prototype and his irradiated gnomes are trying to repair more at the old arsenal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43111, 'enUS', '', 'We\'re going to have a hard time handling this one alone. I sent a message to the dwarven capital of Ironforge asking for their support.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43112, 'enUS', 'And if you\'ll look to your left, you\'ll see that the gnomes have retaken Gnomeregan.  Hooray for us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43113, 'enUS', 'There\'s plenty else going on out here, too.  We\'ve opened up the path to the airport, there\'s rumors of Dark Irons in the east... oh, and I think something weird is going on with that dwarf king, Magni.  Might want to check it out.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43114, 'enUS', 'What is the meaning of this!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43115, 'enUS', 'And here we are!  Welcome to Kharanos!  Watch your step on the way out.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43116, 'enUS', 'We have a battle to win!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43117, 'enUS', '', 'A messenger reached us from Brewnall Village this morning. The dwarves are sending a squad of mountaineers to help us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43118, 'enUS', 'We have to be in position to strike against Crushcog when the mountaineers arrive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43119, 'enUS', 'With Crushcog defeated, Thermaplugg is sure to be quaking in his mechano-tank, and rightly so. You\'re next, Thermaplugg. You\'re next!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43120, 'enUS', '', 'Ready for a tussle, $Glad:lass;?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43121, 'enUS', '', 'Meddlesome gnats! You think us defeated so easily?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43122, 'enUS', 'Stand your ground, brothers! Make it bleed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43123, 'enUS', 'Deepholm was a slow, quiet place. Always alive. Steadily moving. Never rushed. In this time, I strived to become one with the earth, and to a large degree, I succeeded.$B$BLater, the Earthwarder returned, injured. He did not upset the balance at first. He\'d stayed here before, and we knew of him. The shift did not truly begin until the Twilight cult joined him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43124, 'enUS', 'When the Earthwarder began to recover, he became ill content with the quiet. With the peace. It was not long before he turned the stone dragons upon us.$B$BWith Deathwing gone, they are without purpose again. We can remind them of our strength. Bring them back to the Earthmother.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43125, 'enUS', 'And so I see that you have appealed to Diamant. I have been bidden to abide your passing, fleshling. But that shall be the extent of the welcome you shall receive from me.$B$BDo not forget our first conversation. My trust for you is shallow. You have much to prove.$B$BYou would do best to leave. I grow tired of being wary of where my feet fall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43126, 'enUS', 'Step aboard, friends! Carefully now...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43127, 'enUS', 'There ya go, all aboard. That\'s right...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43128, 'enUS', 'And please. Pay no heed to the... uhh... diversity of our crew.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43129, 'enUS', 'Rest assured, they\'ll deliver ya safely to yer destination.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43130, 'enUS', 'That being the fresh piece of land just off the coast of the Eastern Kingdoms.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43131, 'enUS', 'Alright then. Make ready everyone... here we go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43132, 'enUS', 'You hear the sound of shuffling stones behind you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43133, 'enUS', 'Pebble bounces up and down happily.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43134, 'enUS', 'Pebble shuffles around happily.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43135, 'enUS', 'Pebble races in circles, clearly happy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43136, 'enUS', 'Pebble shuffles up against your leg happily.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43137, 'enUS', 'Pebble slumps down sadly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43138, 'enUS', 'Pebble turns away sadly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43139, 'enUS', 'Pebble looks up at you hopefully.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43140, 'enUS', 'Your hear the Pebble\'s stones jittering about inside of him. He\'s clearly excited about something.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43141, 'enUS', 'Pebbles stones burst apart onto the ground, then slowly reform into a full fledged, happy Pebble.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43142, 'enUS', 'Pebble sees the crystals ahead and excitedly rushes towards them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43143, 'enUS', 'Hold your ground!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43144, 'enUS', 'Put those stone troggs back into the earth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43145, 'enUS', 'We have reinforcements! For the Stonefather!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43146, 'enUS', 'Do you want to live forever?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43147, 'enUS', 'Shore up those flanks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43148, 'enUS', 'This Is Stonehearth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43149, 'enUS', 'By Khaz\'goroth\'s hammer, we will break them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43150, 'enUS', 'For Stonefather Oremantle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43151, 'enUS', 'I can\'t do this while they\'re attacking me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43152, 'enUS', 'Get these stone troggs off of me so that I can summon the rockslide!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43153, 'enUS', 'I can\'t perform the ritual while they\'re beating on me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43154, 'enUS', 'A little help here first, $c?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43155, 'enUS', 'Give me the reagents again when these stone troggs are dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43156, 'enUS', 'Keep them from hitting me and I\'ll seal the hatch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43157, 'enUS', 'I don\'t know how they found a way up from underneath, but I can\'t seal the hatch until they\'re off of me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43158, 'enUS', 'We have to deal with these stone troggs first. Then hand me the reagents.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43159, 'enUS', 'Pebble bobbles around appreciatively.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43160, 'enUS', 'For the Stonefather!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43161, 'enUS', 'For Stonefather Oremantle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43162, 'enUS', 'For Stonehearth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43163, 'enUS', 'Kill the stone troggs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43164, 'enUS', 'Hail, citizen. I would recommend that you speak with Watch Captain Parker. He\'s currently at the top of this tower.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43165, 'enUS', 'Pebble\'s excitement has drawn the attention nearby rock flayers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43166, 'enUS', '', 'All right, sunshine.  Time to spill your guts before I spill \'em for ya!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43167, 'enUS', 'Wait!  You can\'t do this!  You people must have a code of honor or something!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43168, 'enUS', '', 'I\'m a daughter of the Wildhammer Clan and a shaman of the Earthen Ring.  My code compels me to honor my ancestors and the elements.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43169, 'enUS', '', 'Doesn\'t say a damn thing about dog-ugly ogres.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43170, 'enUS', '', 'You can start by telling us where the World Pillar fragment is.  My gryphon\'s starting to look a bit tired from hauling you around.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43171, 'enUS', 'Stop it, you tiny, angry woman!  I\'ll tell you everything!  What do you want to know?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43172, 'enUS', 'It was delivered to the forgemaster at Deathwing\'s Fall.  I swear... that\'s all I know!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43173, 'enUS', 'Somebody... please... get... rock... off... me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43174, 'enUS', 'Hurts... Hurts so bad...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43175, 'enUS', 'Please... kill... me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43176, 'enUS', 'I... just... wanted... to... build... the... damned... bridge...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43177, 'enUS', '', 'You were never in any danger, sweetheart.  My friends down below would\'ve caught you.  You\'re worth too much to us alive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43178, 'enUS', 'Happy now?  Pull me up, you vicious little dwarf!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43179, 'enUS', 'What? What have you done?  No!  Do you have any idea what the Twilight Council will do to me when they find out I\'ve talked!?  I would sooner die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43180, 'enUS', '', 'Interesting.  I\'d never heard of the Twilight Council until now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43181, 'enUS', 'PUT YOUR BACKS INTO IT, BOYS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43182, 'enUS', 'HEAVE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43183, 'enUS', 'HO!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43184, 'enUS', 'DAMN ROCK WON\'T BUDGE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43185, 'enUS', 'PUSH HARDER!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43186, 'enUS', 'I\'M PUSHIN\' AS HARD AS I CAN! THING WEIGHS A TON!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43187, 'enUS', 'We\'ll get you out of there, Foreman, just HANG ON!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43188, 'enUS', 'Going ashore, $n?$B$BSay the word and I\'ll lower a boat for you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43189, 'enUS', 'Send me ashore, Horzog.', 'Send me ashore, Horzog.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43190, 'enUS', 'Well, it\'s not good. The battle has taken its toll on both your steam tanks. My salvage teams will secure what\'s left of them as soon as it\'s safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43191, 'enUS', 'With an ample supply of gyromechanic gears, and a team of engineers, your tanks can make a full recovery, but it\'s going to be a long journey.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43192, 'enUS', 'Poor, poor Trollplow. At least the old gal went out doin\' something she loved.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43193, 'enUS', 'Pull yerself togther, Stone! You\'re a steam tank pilot, for Light\'s sake!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43194, 'enUS', 'How dare they take our king prisoner!$B$BWe will take back Stonehearth and then move through the front to crush the stone troggs in their home!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43195, 'enUS', 'Horzog orders a landing boat to be lowered into the water. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43196, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43197, 'enUS', 'Let\'s get out there and put those stone troggs back into the ground, $r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43198, 'enUS', 'Razzle, tell him everything\'s going to be fine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43199, 'enUS', 'I need you out on the front, $n.$B$BYou\'re going to make the difference. We\'ll crush the stone troggs once and for all.$B$BWe must rescue King Oremantle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43200, 'enUS', 'Yes, yes, everything will be fine! Wonderful, in fact. She\'ll be better than ever!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43201, 'enUS', '<Clay grumbles and sneers.>$B$BDamn kids these days.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43202, 'enUS', 'In fact, I\'ll go see about those gears right now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43203, 'enUS', 'There, see? Ol\' Trollplow\'ll be better in no time! Before you know it, you\'ll be bragging about out-gunning me again...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43204, 'enUS', 'C\'mon, let\'s get you another ale.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43206, 'enUS', 'By the time the ritual is complete, Marnal will arrive, and my men will help carve up this grotesque creature.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43207, 'enUS', 'Imagine the most amazing elevator you have ever seen... right here. Yea, its sweet isn\'t it? Sadly, its not ready yet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43208, 'enUS', 'Just teleport me for now. But I can\'t wait for that sweet sweet elevator!', 'Just teleport me for now. But I can\'t wait for that sweet sweet elevator!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43209, 'enUS', 'Patience... our turn will come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43210, 'enUS', '<The murloc looks at you with thirsty eyes.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43211, 'enUS', '<Offer the murloc a drink.>', '<Offer the murloc a drink.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43212, 'enUS', '$R come to look at Orgus?  Take picture, last longer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43213, 'enUS', '<Offer the ogre a drink.>', '<Offer the ogre a drink.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43214, 'enUS', 'Yarr, thar be nary a soul s\'thirsty as me, says I.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43215, 'enUS', '<Offer the pirate a drink.>', '<Offer the pirate a drink.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43216, 'enUS', 'Deathwing\'s entry into our world opened a rift between it and Deepholm.$B$BRebuilding the World Pillar is the only way to stabilize the rift.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43217, 'enUS', 'Negotiations aren\'t going well, $n. The Dragonmaw are not yet free orcs.$B$BSomehow, we MUST get them to join the Horde!$B$BLet me know if you need transport back to the fleet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43218, 'enUS', 'ROCK NOT SO HEAVY! PUNY HUMIES!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43219, 'enUS', 'Can I get a ride back to the fleet?', 'Can I get a ride back to the fleet?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43220, 'enUS', 'Where trow? TROW ON BRIDGE??', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43221, 'enUS', 'Gmurgl starts to follow you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43222, 'enUS', 'OK! Me trow in water!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43223, 'enUS', 'Ornak orders a boat to push off for the fleet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43224, 'enUS', 'Oh... Orgus not feel so good...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43225, 'enUS', 'Orgus think something wrong with $r lemonade...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43226, 'enUS', 'BYE, BYE, DADDY!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43227, 'enUS', 'Well blow me down!  The landlubber scalawag comes bearin\' rum!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43228, 'enUS', 'Yarr?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43229, 'enUS', 'Shiver me timbers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43230, 'enUS', 'NO, NO, NO! Please!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43231, 'enUS', 'NOOOOOO!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43232, 'enUS', 'ANYWHERE BUT THE BRIDGE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43233, 'enUS', 'Where to, buddy?', 'Where to, buddy?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43234, 'enUS', 'See me if you need a boat to the shore, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43235, 'enUS', 'We\'re getting bogged down in the fight against the Frostmane trolls.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43236, 'enUS', 'Most of our steam tanks are out of commission and we\'re losing mountaineers quickly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43237, 'enUS', 'Very well, Zaela. I trust you to do the right thing. $C, I can help your fallen warriors...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43238, 'enUS', 'I ... I can\'t give you any of our supplies! Our Warchief... He would...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43239, 'enUS', '', 'Gregor, you coward! Orcs of pure blood are dying off our shores. If you doom them, you doom us all.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43240, 'enUS', 'Welcome to the Orgrimmar Rocketway Exchange! You still look to be in one piece.', 'Welcome to the Orgrimmar Rocketway Exchange! You still look to be in one piece.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43241, 'enUS', '', '$n, know that our \"Warchief\" does not speak for us all. There are those among us who hope to see the Dragonmaw great again... and free from corruption.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43242, 'enUS', '', 'It won\'t be long before we\'re out of supplies too, Captain. Between the trolls and the yeti raids, we\'ll be down to half rations within a week.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43243, 'enUS', 'We can\'t keep up the operation much longer. Delber and his team must launch their attack from Steelgrill\'s Depot as soon as possible!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43244, 'enUS', 'Not so fast, wet guy!', 'Not so fast, wet guy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43245, 'enUS', 'Delber and his men are working overtime, captain. He has devised a means of delivering an assault team to the rear flank of the battle.', 'Delber and his men are working overtime, captain. He has devised a means of delivering an assault team to the rear flank of the battle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43246, 'enUS', 'Orgrimmar Rocketway Exchange, please.', 'Orgrimmar Rocketway Exchange, please.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43247, 'enUS', 'Zombie pirates?!  Kill it!', 'Zombie pirates?!  Kill it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43248, 'enUS', 'We\'ve also secured air support for the operation and will be ready to deploy soon. If any of your men want to join the assault team, just send them to Delber.', 'We\'ve also secured air support for the operation and will be ready to deploy soon. If any of your men want to join the assault team, just send them to Delber.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43249, 'enUS', 'Southern Rocketway Terminus, please.', 'Southern Rocketway Terminus, please.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43250, 'enUS', 'Gallywix Rocketway Exchange, please.', 'Gallywix Rocketway Exchange, please.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43251, 'enUS', 'Northern Rocketway Exchange, please.', 'Northern Rocketway Exchange, please.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43252, 'enUS', 'Northern Rocketway Terminus, please.', 'Northern Rocketway Terminus, please.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43253, 'enUS', 'Glad to hear it. I\'ll send Delber any forces I can spare.', 'Glad to hear it. I\'ll send Delber any forces I can spare.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43254, 'enUS', 'We Dragonmaw have seen so much suffering... our defeat at Grim Batol... the loss of so many great warriors in Outland... and now incursions from Twilight\'s Hammer.$B$BI sometimes wonder if we will ever be great again. But younglings like Zaela, they give this old heap of bones some hope.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43255, 'enUS', 'What is it you need, outsider? I shouldn\'t be speaking with you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43256, 'enUS', 'Our warriors are dying. Do you have medical supplies I can take back with me?', 'Our warriors are dying. Do you have medical supplies I can take back with me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43257, 'enUS', 'Welcome to the Gallywix Rocketway Exchange! Debts to pay or get the heck away.', 'Welcome to the Gallywix Rocketway Exchange! Debts to pay or get the heck away.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43258, 'enUS', 'Welcome to the Northern Rocketway Exchange! We at Rocketway Relations are glad you survived that last turn!', 'Welcome to the Northern Rocketway Exchange! We at Rocketway Relations are glad you survived that last turn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43259, 'enUS', 'Welcome to the Northern Rocketway Terminus! You probably took a wrong turn, but we\'re happy to have you anyway!', 'Welcome to the Northern Rocketway Terminus! You probably took a wrong turn, but we\'re happy to have you anyway!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43260, 'enUS', 'Welcome to the Southern Rocketway Terminus! Home of the world famous Secret Lab!', 'Welcome to the Southern Rocketway Terminus! Home of the world famous Secret Lab!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43261, 'enUS', 'This ancient totem is fashioned into the shape of a bat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43262, 'enUS', 'We\'re finished! I might as well call for an evacuation now and clear out Lakeshire before the orcs march.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43263, 'enUS', 'And right when we finally finished building that damned bridge! What hope is there, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43264, 'enUS', 'There is hope, Solomon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43265, 'enUS', 'What hope, colonel? What could possibly save us from the impending assault?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43266, 'enUS', 'He served 248 consecutive tours of duty across the First, Second and Third Wars.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43267, 'enUS', 'Captured five years ago by a battalion of filthy, pig sucking Blackrocks, five clicks out from the southern DMZ.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43268, 'enUS', 'Tortured and kept alive as a urinal for young orc warriors. For FIVE YEARS.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43269, 'enUS', 'JOHN J. KEESHAN.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43270, 'enUS', 'Keeshan\'s alive? I thought he died.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43271, 'enUS', 'He\'s alive alright, and been living like an outcast in Lakeshire for the past five months. Or hadn\'t you noticed? Now enough chatter. We\'ve got people to save!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43272, 'enUS', 'I misplaced my Negotiator. In fact, he\'s probably dead. Is another available?', 'I misplaced my Negotiator. In fact, he\'s probably dead. Is another available?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43273, 'enUS', 'I\'m sick of sittin\' in this hole. I hope whatever sorry pencil-pusher is holdin\' up Danath\'s return gets a punch in the jimmies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43274, 'enUS', 'Hey, does this red shirt make me look expendable?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43275, 'enUS', 'I am ready, $n. Soon the Dragonmaw will join the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43276, 'enUS', 'I hear a great Dragonmaw medicine man lives here, in the large hut to our right.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43277, 'enUS', 'This ancient totem is fashioned into the shape of a bat.  It must be the totem that Chabal spoke of.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43278, 'enUS', 'Place the incense at the foot of the statue.', 'Place the incense at the foot of the statue.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43279, 'enUS', 'After these negotiations, I am looking forward to a long and prosperous life.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43280, 'enUS', 'The Dragonmaw Warchief is in the largest building up on the hilltop here. I\'m sure he\'ll be reasonable.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43281, 'enUS', 'This is it! Stay close and let me do the talking. I\'m an expert.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43282, 'enUS', 'Welcome to Aerie Peak, home of the Wildhammer Clan.$B$BWith High Thane Falstad off in Ironforge, you\'ll be dealin\' with me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43283, 'enUS', 'The battle begins in 1 minute.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43284, 'enUS', 'The battle begins in 30 seconds.  Prepare yourselves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43285, 'enUS', 'The spirits o\' this land won\'t tolerate this dark magic and vicious sacrifice any longer.$B$BThe Vilebranch have got to go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43286, 'enUS', 'Ya head\'s gonna make a nice bag!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43287, 'enUS', 'Let\'s get this over with. I\'d rather be back home at Aerie Peak than starin\' down these ugly trolls.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43288, 'enUS', 'You thought those long arms gave you an advantage, $r? Think again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43289, 'enUS', 'Leave Ongo\'longo ALONE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43290, 'enUS', 'Let\'s not waste time! There\'s undead afoot.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43291, 'enUS', 'Hold it now. Let\'s not rush in.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43292, 'enUS', 'I\'ve had enough of this!  Time to take a break...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43293, 'enUS', 'Zanzil\'s back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43294, 'enUS', 'If I ever get out of here I\'m gonna crap all over your head, you pig sucking orc.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43295, 'enUS', 'Where\'s the rest of my crew??!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43296, 'enUS', 'You think these bars can hold me? HAH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43297, 'enUS', '', 'High Priestess Jeklik is nearly healed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43298, 'enUS', 'Most esteemed Warchief -', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43299, 'enUS', 'Warchief, you misunderstand. Garrosh Hellscream will have a beach head on these shores, with or without your help. What we ask -', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43300, 'enUS', 'What\'re you doin\' here?  Don\' matter, I\'m gonna resurrect Jeklik before you get a chance to interfere!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43301, 'enUS', 'But the Horde has assembled an enormous host! A vast fleet of war machines and hardened veterans. Hellscream would as soon crush you as-', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43302, 'enUS', 'This is madness!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43303, 'enUS', '', 'I LIVE!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43304, 'enUS', 'Uh oh... Jin\'do\'s not gonna be happy about this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43305, 'enUS', 'Time to get out of here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43306, 'enUS', 'Quit wasting words, worm. If you want the aid of the Dragonmaw, you should\'ve come on bent knee and with offerings of gold.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43307, 'enUS', 'Was that a THREAT? I knew Hellscream in Outland. A whimpering whelp. He is no Warchief.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43308, 'enUS', 'Bring him on!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43309, 'enUS', 'This is...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43310, 'enUS', 'DRAGONMAW!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43311, 'enUS', 'Keep the $c alive. $gHe:She; will be our bargaining chip.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43312, 'enUS', 'Call for the others. We will crush Hellscream\'s Horde and hurl their bodies to the sea...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43313, 'enUS', 'Keeping the bomb warm, obviously. What does it look like I\'m doing?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43314, 'enUS', 'What are you doing just sitting there?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43315, 'enUS', 'High Priest Venoxis is nearly healed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43316, 'enUS', 'Take me to the battle at the Ruins of Feathermoon.', 'Take me to the battle at the Ruins of Feathermoon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43318, 'enUS', 'Will you do whatever it takes to save your kingdom?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43319, 'enUS', 'I must return to Feathermoon Stronghold!', 'I must return to Feathermoon Stronghold!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43320, 'enUS', 'You\'re back for more, huh?  Too bad, because I\'ve got a stronger potion mixed up this time!  Venoxis will live!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43321, 'enUS', 'I can\'t take much more of this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43322, 'enUS', 'Here comes Zanzil!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43323, 'enUS', 'Ahahaha!  Zanzil is the winner this time!  And look who came to celebrate!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43324, 'enUS', 'Nooo!  Zanzil is a failure...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43326, 'enUS', '', 'The Kvaldir have been purged from the city. Now we can begin our preparations.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43327, 'enUS', '', 'Battlemaiden, I have a task that requires your attention.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43328, 'enUS', '', 'Battlemaiden! You are needed on the battlefield. The Kvaldir have broken through our lines!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43329, 'enUS', 'Welcome!', 'Welcome!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43330, 'enUS', 'Greetings, $c.', 'Greetings, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43331, 'enUS', '', 'Stand fast! The ritual is almost complete.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43332, 'enUS', '', 'Cut them down, Battlemaiden!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43333, 'enUS', 'Greetings! Please have a look around.', 'Greetings! Please have a look around.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43334, 'enUS', '', 'Hold them back! Keep them away from the temple!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43335, 'enUS', 'Let me know if you need help finding anything, $c.', 'Let me know if you need help finding anything, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43336, 'enUS', 'Welcome. May I help you find something?', 'Welcome. May I help you find something?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43337, 'enUS', 'Greetings.', 'Greetings.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43338, 'enUS', 'Welcome.', 'Welcome.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43339, 'enUS', 'Welcome!', 'Welcome!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43340, 'enUS', 'Greetings, $c.', 'Greetings, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43341, 'enUS', 'Greetings! Please have a look around.', 'Greetings! Please have a look around.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43342, 'enUS', 'Let me know if you need help finding anything, $c.', 'Let me know if you need help finding anything, $c.', 26124);
