INSERT INTO `broadcast_text_locale` VALUES (45277, 'enUS', 'Greetin\'s, $c.\n\nIt be a sad ting, de way de secrets of de world are hidden from ya: Time, Fate, de burnin\' stars an\' de cold, cold darkness between... never will ya know de truth of such tings!', 'Greetin\'s, $c.\n\nIt be a sad ting, de way de secrets of de world are hidden from ya: Time, Fate, de burnin\' stars an\' de cold, cold darkness between... never will ya know de truth of such tings!', 26124);
