INSERT INTO `broadcast_text_locale` VALUES (45319, 'enUS', 'The fleet was mostly intact as it limped into harbor after our engagement with the Horde. We weighed anchor in the shadow of Highbank here to regroup.$B$BThen the naga came.$B$BScaly, glistening hands clambering up the anchor chains... dragging men, screaming, into the depths...$B$BThese are cursed shores, $n. Cursed. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (45320, 'enUS', 'Slaughter them, $c! Destroy as many naga as you can to honor those that fell.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (45321, 'enUS', 'The broodmother flies above us! Do not fear, fleshling, I am called \"The Steady\" for a reason...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (45322, 'enUS', 'Jump from rock to rock to attract the attention of the Broodmother!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (45323, 'enUS', 'We cannot fail, $n.  The future of multiple worlds is at stake here.', '', 26124);
