INSERT INTO `broadcast_text_locale` VALUES (45364, 'enUS', 'If you really want to help out, you can help me and my friends with some combat practice.', 'If you really want to help out, you can help me and my friends with some combat practice.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (45365, 'enUS', 'These so-called paladins won\'t let me through so I can see if any of the legendary Fras Siabi\'s famous tobacco remains in Stratholme! They\'re no better than the scourge, if you ask me.$B$BBetween you an\' me, I think their \'brotherhood of the light\' is a hoax. An elaborate ploy I tell ya!', '', 26124);
