INSERT INTO `broadcast_text_locale` VALUES (45412, 'enUS', 'The Brotherhood of the Light transcends the ability of any other group to eradicate affronts to the Light. We will take any actions necessary to rid the world of foul beasts like these undead. Nothing will stand in our way.$B$BAre you with us, or against us?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (45413, 'enUS', 'Witch Doctor Umbu spends his time walkin\' about de valley, tendin\' to de people and elemental spirits alike.$B$BLook for him around de central building.', 'Witch Doctor Umbu spends his time walkin\' about de valley, tendin\' to de people and elemental spirits alike.$B$BLook for him around de central building.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (45414, 'enUS', 'Kuzari and Zanga are trainin\' headhunters on one of de small islets towards de southeast end of de valley, mon.', 'Kuzari and Zanga are trainin\' headhunters on one of de small islets towards de southeast end of de valley, mon.', 26124);
