INSERT INTO `broadcast_text_locale` VALUES (45760, 'enUS', 'Tailoring', 'Tailoring', 26124);
INSERT INTO `broadcast_text_locale` VALUES (45761, 'enUS', 'I\'d like to purchase secondary profession training.', 'I\'d like to purchase secondary profession training.', 26124);
