INSERT INTO `broadcast_text_locale` VALUES (46424, 'enUS', 'And I take it you\'re going again...', 'And I take it you\'re going again...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46425, 'enUS', 'Damn right!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46426, 'enUS', 'Well, let\'s get to it then.', 'Well, let\'s get to it then.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46427, 'enUS', 'That\'s tha spirit!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46428, 'enUS', 'That\'s right, show \'em our colors. Let \'em come!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46429, 'enUS', 'If Keely sent ya to be ropin\' me back in, ya can just forget it. There\'s Twilight that need bashin\'.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46430, 'enUS', 'I\'ll be headin\' back shortly, $glad:lass;. Just one more go, for good time\'s sake.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46431, 'enUS', 'That all ya got?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46432, 'enUS', 'Cayden\'s got an axe with your name on it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46433, 'enUS', 'Come\'n get it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46434, 'enUS', 'I know there\'s more of ya, hidin\' in your little holes...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46435, 'enUS', 'Weee! Shinies all over this place!$b$bTake a look at what I found diggin\' right here...$b$bIt\'s a breathin\' hat!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46436, 'enUS', 'Get up you lazy bums, there\'s fighting to do!', 'Get up you lazy bums, there\'s fighting to do!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46437, 'enUS', 'Knock, knock!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46438, 'enUS', 'Come outta ya holes, cowards!', '', 26124);
