INSERT INTO `broadcast_text_locale` VALUES (46440, 'enUS', 'Anyone home?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46442, 'enUS', 'Move out - strike them now! ', 'Move out - strike them now! ', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46443, 'enUS', 'See you soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46444, 'enUS', 'That was fun.', '', 26124);
