INSERT INTO `broadcast_text_locale` VALUES (46459, 'enUS', 'Freedom!', 'Freedom!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46460, 'enUS', 'Kill this fool, or Randolph will kill us!', 'Kill this fool, or Randolph will kill us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46461, 'enUS', 'Fight for your miserable, worthless lives!', 'Fight for your miserable, worthless lives!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46462, 'enUS', 'Greetings, $r. I am Lord Itharius of the Green Dragonflight.$B$BI am here to free my kin from the evil within this temple, hopefully for good. Will you assist me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46463, 'enUS', '', 'I knew you were coming. No one can resist me, not even you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46464, 'enUS', 'You\'ve come for training. Good!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46465, 'enUS', 'You there - $n! Help us defend this square.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46466, 'enUS', 'Look out, more Dragonmaw. And the Horde is with them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46467, 'enUS', 'Come, we\'ve done enough damage here. Let\'s prepare another ambush.', 'Come, we\'ve done enough damage here. Let\'s prepare another ambush.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46468, 'enUS', 'We did it! Cover the roads and keep this place locked down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46469, 'enUS', 'Have you checked out my breathin\' hat?$b$bNo more suckin\' on clams for Budd! No sirree...\n', '', 26124);
