INSERT INTO `broadcast_text_locale` VALUES (46472, 'enUS', 'You\'ll pay for this, Hogger!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46473, 'enUS', 'One day...I will have my revenge...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46474, 'enUS', 'Forest just setback!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46475, 'enUS', 'Kill now, eat later!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46476, 'enUS', 'Yiiipe!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46477, 'enUS', 'Thank the Light! I\'ve made it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46478, 'enUS', 'It\'s a relief to be surrounded by normal gnomes again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46479, 'enUS', 'I thought I was goner!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46480, 'enUS', 'You... you\'re not going to try to eat me, are you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46481, 'enUS', 'I never would\'ve made it on my own...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46482, 'enUS', 'Thank you for getting me out of there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46483, 'enUS', 'My family... did you find my family?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46484, 'enUS', 'Ah, a new arrival. Right this way, sir.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46485, 'enUS', '%s realizes it\'s been spotted and quickly plays dead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46486, 'enUS', 'Fathom-Lord Zin\'jatar has entered the battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46487, 'enUS', 'ALL MUST BURN!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46488, 'enUS', 'ASH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46489, 'enUS', 'FIRE... EXTINGUISHED!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46490, 'enUS', 'Stay back in the name of the King!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46491, 'enUS', 'You shall not pass!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46492, 'enUS', 'Hold the line! Don\'t let them through!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46493, 'enUS', 'Warden Thelwater, we need to push forward!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46494, 'enUS', 'We need to make a move now, while we still can!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46495, 'enUS', 'Allow me to introduce myself. I am Randolph Moloch, and I will be killing you all today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46496, 'enUS', 'Such a tragic waste of life.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46497, 'enUS', 'My epic schemes, my great plans! Gone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46498, 'enUS', 'Steady men...steady...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46499, 'enUS', 'Hold your fire! Warden\'s orders..no shootin\'...yet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46500, 'enUS', 'Keep \'em in your sights. If they make a move on the guards, they\'re dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46501, 'enUS', 'Stay with me. You\'re going to make it out of here.', 'Stay with me. You\'re going to make it out of here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46502, 'enUS', 'We\'re going to get you some help.', 'We\'re going to get you some help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46503, 'enUS', 'You\'re doing well... you\'re going to be fine.', 'You\'re doing well... you\'re going to be fine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46504, 'enUS', 'Freeing', 'Freeing', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46505, 'enUS', 'The naga escaped out of the tunnel ahead. Show them no mercy, $c.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46506, 'enUS', '', 'I am eternally grateful to you, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46507, 'enUS', '', ' I\'ll take care of the drakes. You worry about the rest of the Twilight rubbish.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46508, 'enUS', 'Ramkahen will only wage war against Neferset if the High Council\'s vote is unanimous.$B$BThe odds are against us, but my brother needs to understand there is no reasoning with Deathwing\'s allies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46509, 'enUS', 'There\'s one - get it!', 'There\'s one - get it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46510, 'enUS', 'Keep your eyes peeled for dwarven pigs.', 'Keep your eyes peeled for dwarven pigs.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46511, 'enUS', '', '$n, is that you? Please free me from these chains.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46512, 'enUS', 'They\'ve got one of ours! No mercy for the Dragonmaw!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46513, 'enUS', 'Hand over the bird, yeh filthy beasts. WILDHAMMER!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46514, 'enUS', 'Are you with the Neferset scum?  Did they send you to do their dirty work?  Speak!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46515, 'enUS', 'I\'m looking for Tahet.  Let\'s get you out of these chains.', 'I\'m looking for Tahet.  Let\'s get you out of these chains.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46516, 'enUS', 'It ain\'t all bad...$b$bI still got my breathin\' hat!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46517, 'enUS', 'Rescue the Gryphon', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46518, 'enUS', 'Defend the captured prize', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46519, 'enUS', 'You! $C! Help us defend against a counterattack.', 'You! $C! Help us defend against a counterattack.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46520, 'enUS', 'Wait!  Here come the Neferset guards!  Fight near me so I can beat their skulls in!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46521, 'enUS', 'Freedom at last.  I thank you, stranger.  Tahet is no longer here.  A cruel Neferset warlord called Caimas took him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46522, 'enUS', 'I must warn you.  Caimas does horrible things to prisoners... he forces them to fight his pet beasts.  Prepare for the worst, friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46523, 'enUS', 'Look out - the Alliance is aiding the Wildhammer!', 'Look out - the Alliance is aiding the Wildhammer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46524, 'enUS', '%s picks up a key from Neferset Enforcer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46525, 'enUS', 'That\'s one more for the Wildhammer!', 'That\'s one more for the Wildhammer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46526, 'enUS', 'Ahoy!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46527, 'enUS', 'Ride into the Holding Pens and DEVOUR naga!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46528, 'enUS', 'The Horde war machine needs you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46529, 'enUS', 'Are you here to help the cause? Mount Hyjal burns!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46530, 'enUS', 'Could you please send me to Moonglade, Emissary?', 'Could you please send me to Moonglade, Emissary?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46531, 'enUS', '', 'The admiral and his forces pushed forward to take one of the bases you scouted for him earlier. You should go and meet them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46532, 'enUS', 'That giant squid didn\'t stand a chance. The Verne\'s already rearmed and ready for another go... now we just wait.$B$BIt\'ll be back.$B$BYou\'ll see.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46533, 'enUS', 'Great work, everyone. Take a moment to grab your gear and let\'s move on.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46534, 'enUS', 'This will keep you occupied!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46535, 'enUS', 'DRAGONMAW!', 'DRAGONMAW!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46536, 'enUS', 'Jump to shake off the young crocolisks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46537, 'enUS', 'Come. There\'s more havoc elsewhere on the ridge.', 'Come. There\'s more havoc elsewhere on the ridge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46538, 'enUS', '', 'Ah... I do not have many visitors here.$B$BPlease, sit a while.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46539, 'enUS', 'The Alliance needs you. Answer the hero\'s call!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46540, 'enUS', 'Now we finish the job.', 'Now we finish the job.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46541, 'enUS', 'Bah!  Mere luck!  My pets will make mincemeat out of you yet!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46542, 'enUS', 'Enough!  I grow tired of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46543, 'enUS', '', 'Are you here to help the cause? Mount Hyjal burns!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46544, 'enUS', 'Search the town. Kill any survivors!', 'Search the town. Kill any survivors!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46545, 'enUS', 'Psst! $n! Over here...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46546, 'enUS', 'Lure Caimas over the Croc Eggs without stepping on them yourself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46547, 'enUS', 'Over the wall, HUP!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46548, 'enUS', 'For glory! For honor! FOR THUNDERMAR!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46549, 'enUS', 'That heap o\' rubble yonder was me family\'s homestead fer five generations.$B$BWe\'re takin\' it back.$B$BWe\'re outnumbered... But we\'re Wildhammer! Are yeh with me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46550, 'enUS', 'Let\'s go!', 'Let\'s go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46551, 'enUS', 'Short work, an\' none too pretty! ', 'Aye, that\'s how the hammer falls, gents!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46552, 'enUS', 'Right! Burn the bodies of this filth while I search the ol\' homestead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46553, 'enUS', 'Aw, hell\'s hangover. They trashed the place!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46554, 'enUS', 'We\'ve got what we need. Clear out. We\'ll hit them again elsewhere.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46555, 'enUS', 'Farewell, friend! Good treasure hunting!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46556, 'enUS', 'I\'ll deliver the news to Thundermar.', 'I\'ll deliver the news to Thundermar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46557, 'enUS', '$C! Look sharp. I smell the stench of Wildhammer nearby...', '$C! Look sharp. I smell the stench of Wildhammer nearby...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46558, 'enUS', 'Look out - more dwarves attacking from the rear!', 'Look out - more dwarves attacking from the rear!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46559, 'enUS', 'Egad! My sophisticated heart!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46560, 'enUS', 'Hah! Another legendary Dragonmaw triumph.', 'Hah! Another legendary Dragonmaw triumph.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46561, 'enUS', 'Mortimer Moloch collapses from a heart attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46562, 'enUS', 'Sweep the area for anything of use. Quickly now - they\'ll attack again soon.', 'Sweep the area for anything of use. Quickly now - they\'ll attack again soon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46563, 'enUS', 'I remember it well.  It was a bright, shiny day...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46564, 'enUS', 'I was just mindin\' my own business, when all of a sudden, Deathwing appeared!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46565, 'enUS', 'I said to myself, \"I\'m gonna punch that dragon in the face!\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46566, 'enUS', 'I\'m gonna punch that dragon in the face!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46567, 'enUS', 'Unfortunately, the cataclysm had knocked loose a bunch of angry elementals.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46568, 'enUS', '\"You dang-blasted rock elementals!\", I said.  \"I\'ll punch you too!\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46569, 'enUS', 'You dang-blasted rock elementals!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46570, 'enUS', 'I\'ll punch you too!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46571, 'enUS', 'This ends here, Hogger!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46572, 'enUS', 'He\'s...he\'s dead? ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46573, 'enUS', 'It\'s simply too good to be true. You couldn\'t have killed him so easily!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46574, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46575, 'enUS', 'Get punched!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46576, 'enUS', 'Taste my fist!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46577, 'enUS', 'P-p-punch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46578, 'enUS', 'I\'m coming for you, Deathwing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46579, 'enUS', 'Theldurin the Lost!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46580, 'enUS', 'I\'m going to punch that dragon right in the face!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46581, 'enUS', 'Face punch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46582, 'enUS', 'Take that, dangerous enemy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46584, 'enUS', 'You have no right to keep us out!', 'You have no right to keep us out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46585, 'enUS', 'We\'ve committed no wrongs against the city or its king...', 'We\'ve committed no wrongs against the city or its king...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46586, 'enUS', 'You have to let me in, my customers are waiting.', 'You have to let me in, my customers are waiting.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46587, 'enUS', 'As a tax-paying subject, I have a right to visit the city!', 'As a tax-paying subject, I have a right to visit the city!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46588, 'enUS', 'What does King Wrynn fear from his own people?', 'What does King Wrynn fear from his own people?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46589, 'enUS', 'You can\'t treat us this way!', 'You can\'t treat us this way!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46590, 'enUS', 'I want to speak to Marshal McBride! This is an outrage!', 'I want to speak to Marshal McBride! This is an outrage!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46591, 'enUS', 'I have powerful friends, and they will not be happy to hear I was treated this way!', 'I have powerful friends, and they will not be happy to hear I was treated this way!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46592, 'enUS', 'You lookin\' for me?', '', 26124);
