INSERT INTO `broadcast_text_locale` VALUES (48885, 'enUS', '[Silly:] \"Knifefight.\"', '[Silly:] \"Knifefight.\"', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48886, 'enUS', '[Bawdy:] \"Tight!\"', '[Bawdy:] \"Tight!\"', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48887, 'enUS', 'Speak your peace, $c.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48888, 'enUS', 'I need a bat, Kellen.  I\'m headed to Western Plaguelands.  The Dark Lady needs me!', 'I need a bat, Kellen.  I\'m headed to Western Plaguelands.  The Dark Lady needs me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48889, 'enUS', 'I\'m ready, Russell. Let\'s write a song!', 'I\'m ready, Russell. Let\'s write a song!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48890, 'enUS', 'I need a bat, Timothy.  I\'m headed to Andorhal.  The Dark Lady needs me!', 'I need a bat, Timothy.  I\'m headed to Andorhal.  The Dark Lady needs me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48891, 'enUS', 'I need a gryphon, Bibilfaz.  I\'m headed to Andorhal.  My king needs me!', 'I need a gryphon, Bibilfaz.  I\'m headed to Andorhal.  My king needs me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48892, 'enUS', 'My lute is all tuned up. Are we ready to compose something?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48893, 'enUS', '|cFFFFFFFFSofera\'s Naze, the first of the four fingers of Hillsbrad.|r', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48894, 'enUS', '', 'I wasn\'t expecting anyone down here. Come to keep me company, or just looking for a way out?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48895, 'enUS', 'I\'d like to leave this place.', 'I\'d like to leave this place.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48896, 'enUS', 'Relief at last...', 'Relief at last...', 26124);
