INSERT INTO `broadcast_text_locale` VALUES (50140, 'enUS', 'This is the end... for you, and for the world.', 'This is the end... for you, and for the world.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50141, 'enUS', 'Embrace the coming of the end!', 'Embrace the coming of the end!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50142, 'enUS', 'I will prove myself to the master!', 'I will prove myself to the master!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50143, 'enUS', 'Whispers... whispers... I can hear the whispers.... ', 'Whispers... whispers... I can hear the whispers.... ', 26124);
