INSERT INTO `broadcast_text_locale` VALUES (50153, 'enUS', 'To the crushing depths with you!', 'To the crushing depths with you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50154, 'enUS', 'These waters were ours even before the Sundering!', 'These waters were ours even before the Sundering!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50155, 'enUS', 'Tides take you!', 'Tides take you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50156, 'enUS', 'These waters will be your grave. ', 'These waters will be your grave. ', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50157, 'enUS', 'A curse upon your kind, trespasser! ', 'A curse upon your kind, trespasser! ', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50158, 'enUS', 'Tides, give me strength!', 'Tides, give me strength!', 26124);
