INSERT INTO `broadcast_text_locale` VALUES (50163, 'enUS', 'If you want a fight, you\'ll get one!', 'If you want a fight, you\'ll get one!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50164, 'enUS', 'Confounded beasts! You\'ll pay for what you did to our fleet!', 'Confounded beasts! You\'ll pay for what you did to our fleet!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50165, 'enUS', 'Begone from here, Horde filth!', 'Begone from here, Horde filth!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50166, 'enUS', 'Where\'s your \"honor\" now, Horde scum?', 'Where\'s your \"honor\" now, Horde scum?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50167, 'enUS', 'You Horde are as bad as the Twilight\'s Hammer!', 'You Horde are as bad as the Twilight\'s Hammer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50168, 'enUS', 'This land belongs to the Alliance, Horde wretch! Away with you!', 'This land belongs to the Alliance, Horde wretch! Away with you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50169, 'enUS', 'Killin\' time! ', 'Killin\' time! ', 26124);
