INSERT INTO `broadcast_text_locale` VALUES (50182, 'enUS', '', 'Your skill at range would be best suited to the fight higher up. Use your hooks to ascend to one of the upper Vermillion Skyscreamers, then pick off the circling Pyrewings from there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50183, 'enUS', 'The drake under you is mortally wounded and about to fall! Move to another drake, quickly!\n\n|TInterface\\Icons\\inv_misc_hook_01.blp:32|t', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50184, 'enUS', 'Keep it up.', 'Keep it up.', 26124);
