INSERT INTO `broadcast_text_locale` VALUES (50186, 'enUS', 'We\'ll keep the buckets coming.', 'We\'ll keep the buckets coming.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50187, 'enUS', 'Over there! The fire\'s flarin\' up!', 'Over there! The fire\'s flarin\' up!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50188, 'enUS', 'We\'ve got your back.', 'We\'ve got your back.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50189, 'enUS', 'Ready for some more?', 'Ready for some more?', 26124);
