INSERT INTO `broadcast_text_locale` VALUES (50191, 'enUS', '', 'Vanessa begins throwing poison vials!  Climb up quickly!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50192, 'enUS', 'You\'ve been bitten by a worgen.  It\'s probably nothing, but it sure stings a little.$B$B|TInterface\\Icons\\INV_Misc_monsterfang_02.blp:32|t', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50193, 'enUS', 'A chest is revealed near the rear entrance!', 'A chest is revealed near the rear entrance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50194, 'enUS', 'What... what\'s happening to me? Why have you done this to me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50195, 'enUS', 'I should be dead and to death I will return!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50196, 'enUS', 'This is Lady Sylvanas\'s doing?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50197, 'enUS', 'What magic is this that turns back the hand of death?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50198, 'enUS', 'I... I don\'t remember... Why have you done this?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50199, 'enUS', 'What use has the Dark Lady for me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50200, 'enUS', 'If the Banshee Queen has offered me this chance, I will gladly serve.', '', 26124);
