INSERT INTO `broadcast_text_locale` VALUES (50202, 'enUS', 'Ha! You throw like my greatmother.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50203, 'enUS', 'Watch and learn, pup.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50204, 'enUS', 'Prepare to witness perfection.', '', 26124);
