INSERT INTO `broadcast_text_locale` VALUES (50763, 'enUS', '', 'The Wickerman festivities will begin in ten minutes! Please join us just outside the gates of Stormwind City and celebrate Hallow\'s End!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50764, 'enUS', 'One of those blasted Jaedenar demons saw me! Gave me a good slash in the side. The wound isn\'t pretty, but I\'ll live.$B$BThere are more urgent matters at hand, like stopping the Shadow Council from launching a full-scale attack on our friends at Wildheart Point. I\'m in no shape to fight, so you\'ll have to go in my place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50765, 'enUS', 'The people of Gilneas understand the gravity of change all too well.', '', 26124);
