INSERT INTO `broadcast_text_locale` VALUES (50769, 'enUS', '', 'She\'s coming! Quiet down, you fools! The Dark Lady approaches...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50770, 'enUS', 'Oh great, thanks. That looks...um...delicious.', 'Oh great, thanks. That looks...um...delicious.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50771, 'enUS', 'I heard that Gott Weedlespan fired his last assistant...by pushing him off a cliff! Best not to get on his bad side.', 'I heard that Gott Weedlespan fired his last assistant...by pushing him off a cliff! Best not to get on his bad side.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50772, 'enUS', 'The Assistant Resort Director doesn\'t like to see us taking breaks. We don\'t like to see him, either.', 'The Assistant Resort Director doesn\'t like to see us taking breaks. We don\'t like to see him, either.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50773, 'enUS', 'Oh goody! I haven\'t had a break since the day before yesterday!', 'Oh goody! I haven\'t had a break since the day before yesterday!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50774, 'enUS', 'I want to go home and see my family, but Gott won\'t let us off this mountain!', 'I want to go home and see my family, but Gott won\'t let us off this mountain!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50775, 'enUS', 'Food? What about my last paycheck? Did you bring that too?', 'Food? What about my last paycheck? Did you bring that too?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50776, 'enUS', 'Heehee! You can\'t catch me!', 'Heehee! You can\'t catch me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50777, 'enUS', 'Ooooh, a $c! Try to catch me, slowpoke!', 'Ooooh, a $c! Try to catch me, slowpoke!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50778, 'enUS', 'I can run fast! Daddy let me get a speed enchant on my shoes!', 'I can run fast! Daddy let me get a speed enchant on my shoes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50779, 'enUS', 'Weeeeeeeeeeeeeeeeeeeeeeeee!', '\nWeeeeeeeeeeeeeeeeeeeeeeeee!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50780, 'enUS', 'Am I fast? I wanna be a $c like you!', 'Am I fast? I wanna be a $c like you!\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50781, 'enUS', 'Awww, we were just playing!', 'Awww, we were just playing!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50782, 'enUS', 'Guess I need to run faster next time.', 'Guess I need to run faster next time.', 26124);
