INSERT INTO `broadcast_text_locale` VALUES (50784, 'enUS', 'Where are you taking us? Are we there yet?', 'Where are you taking us? Are we there yet?\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50786, 'enUS', 'Me will mark where on your map.', 'Me will mark where on your map.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50787, 'enUS', '', 'Now, we burn this wickerman as a symbol of our victory against old enemies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50788, 'enUS', '', 'We are the instruments of an unyielding ire...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50789, 'enUS', '%s looks towards the cave entrance, but A-Me 01 is nowhere to be seen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50790, 'enUS', '%s is having trouble sleeping.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50791, 'enUS', 'Help! I can\'t swim!', 'Help! I can\'t swim!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50792, 'enUS', 'Over here, $c! Help me!', 'Over here, $c! Help me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50793, 'enUS', '', 'Hallow\'s End is upon us, $c.  I hope you are enjoying the festivities.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50794, 'enUS', 'I\'m ready to fly, let\'s bomb the Undercity!', 'I\'m ready to fly, let\'s bomb the Undercity!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50797, 'enUS', 'Ugh, what is this swill? It tastes like wet dog fur!', 'Ugh, what is this swill?  It tastes like wet dog fur!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50798, 'enUS', 'Who in their right mind would want to drink THIS? Blecchhhh!', 'Who in their right mind would want to drink THIS? Blecchhhh!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50799, 'enUS', 'Have I seen anything suspicious? The only suspicious things around here are the outrageous prices.', 'Have I seen anything suspicious? The only suspicious things around here are the outrageous prices.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50800, 'enUS', 'I haven\'t seen a thing. Now stop bothering me about Twilight\'s Hammer and let me get back to my tan.', 'I haven\'t seen a thing. Now stop bothering me about Twilight\'s Hammer and let me get back to my tan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50801, 'enUS', 'I might have seen someone in a Twilight\'s Hammer robe. They were very tall. I\'m sure of it.', 'I might have seen someone in a Twilight\'s Hammer robe. They were very tall. I\'m sure of it.\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50802, 'enUS', 'I saw someone setting one of those fire totems. It was dark, but I remember they were wearing red.', 'I saw someone setting one of those fire totems. It was dark, but I remember they were wearing red.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50803, 'enUS', 'Suspicious activity? Assistant Director Weedlespan doesn\'t want anyone going to the north side of the lake. Weird, don\'t you think?', 'Suspicious activity? Assistant Director Weedlespan doesn\'t want anyone going to the north side of the lake. Weird, don\'t you think?\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50804, 'enUS', 'Yes, I overheard someone talking about Twilight\'s Hammer at the party last night. I didn\'t see them, but they were extremely rude.', 'Yes, I overheard someone talking about Twilight\'s Hammer at the party last night. I didn\'t see them, but they were extremely rude.\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50805, 'enUS', 'Accuse', 'Accuse', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50806, 'enUS', 'Greetings! Wonderful day, isn\'t it?', 'Greetings! Wonderful day, isn\'t it?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50807, 'enUS', 'How dare you accuse me of being a Twilight Cultist! Do I look crazy to you?', 'How dare you accuse me of being a Twilight Cultist! Do I look crazy to you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50808, 'enUS', '<Gott sneers contemptuously at you.>$B$BCan you possibly work any slower? I\'m surrounded by useless idiots!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50809, 'enUS', 'Hey. Have we met before?', 'Hey. Have we met before?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50810, 'enUS', 'If ye want me advice, don\'t go strugglin\' tae much. Ye\'ll just encourage them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50811, 'enUS', 'If ye climb up another notch, I\'ll share me brew with ye!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50812, 'enUS', 'Nae, I\'m enjoying me fine some Ironforge mead. Might be the last drink we have for a while yet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50813, 'enUS', 'And who forgot tae grab the ale on the way up? Not me! Hahahaha!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50814, 'enUS', 'Stop drinking tha last o\' our ale and help me off this blasted branch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50815, 'enUS', 'Mebbe someone will come around and get us out of this mess!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50816, 'enUS', 'I told ye not tae leave the boar ribs out! Why couldna yae had a proper stomach and eaten yer dinner like the rest o\' us Buckeys?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50817, 'enUS', 'Come any closer ya wee bear and I\'ll punch ya in the face!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50818, 'enUS', 'Wow! A haunted lighthouse with a pirate captain\'s ghost and everything! D\'you think he\'ll tell us where the treasure is buried?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50819, 'enUS', 'Holy Light! Look what happened to the dam! The other orphans aren\'t gonna believe this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50820, 'enUS', 'Wow, the big bear is still here! Do you think he gets mad when everyone puts their treasure in his tummy? That must hurt...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50821, 'enUS', '', 'Welcome to the Menders\' Stead, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50822, 'enUS', 'Is Zen\'Kiki available for some more combat practice?', 'Is Zen\'Kiki available for some more combat practice?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50823, 'enUS', 'Do you feel it? It\'s almost like Arthas is still here right in this room! But he\'s gone, right? This place still feels like evil...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50824, 'enUS', 'What\'re ye lookin\' at, ye tuskarr-faced bung?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50825, 'enUS', 'Yarr!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50826, 'enUS', 'Scuttle me skivvies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50827, 'enUS', 'The tale of ol\' Duncan is a sad one, I\'m afraid.  I used to be captain of me own ship, ye know?$b$bOne more demotion and I\'ll be a swabbie again.  I don\'t think I could take the humiliation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50828, 'enUS', 'Whatever, Duncan.  Grab a mop and get to work.', 'Whatever, Duncan.  Grab a mop and get to work.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50829, 'enUS', 'Wow, these docks are huge! The ships that dock here must be as big as zeppelins! Is this the same ocean I can see from Orgrimmar?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50830, 'enUS', 'I can see those dirty night elves from here! When I grow up, I want to fight for the warchief just like these warriors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50831, 'enUS', 'Will you pay \"Pretty Boy\" Duncan 1 gold to swab the decks for you?', 'Will you pay \"Pretty Boy\" Duncan 1 gold to swab the decks for you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50832, 'enUS', 'Having some trouble there, sailor?  Need some help?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50833, 'enUS', 'This is humiliating.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50834, 'enUS', 'I can\'t believe it\'s come to this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50835, 'enUS', 'A mop?  Really?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50836, 'enUS', 'I suppose sometimes y\'need to hit rock bottom before y\'can crawl yer way back up.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50837, 'enUS', 'Maybe I need a new nickname.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50838, 'enUS', 'Warchief, you are a hero to my young ward. Would you mind signing an autograph for him?', 'Warchief, you are a hero to my young ward. Would you mind signing an autograph for him?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50839, 'enUS', 'Of course, $c. What is the young warrior\'s name?$B$BI wish more of our people shared your concern for the orphans of Orgrimmar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50840, 'enUS', 'While I miss Gilneas greatly, there\'s ancient ruins to explore!', 'While I miss Gilneas greatly, there\'s ancient ruins to explore!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50841, 'enUS', 'Tadpoles!  Tadpoles is the winner!', '', 26124);
