INSERT INTO `broadcast_text_locale` VALUES (50858, 'enUS', 'Our brothers at Baradin Hold on Tol Barad are very busy and not always available when you need them.  I have been stationed here around the clock to help you with tasks they might have given you.', 'Our brothers at Baradin Hold on Tol Barad are very busy and not always available when you need them.  I have been stationed here around the clock to help you with tasks they might have given you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50860, 'enUS', 'You no belong here!', 'You no belong here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50861, 'enUS', 'You break and enter?! Me break YOU!', 'You break and enter?! Me break YOU!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50862, 'enUS', 'You not allowed here!', 'You not allowed here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50863, 'enUS', 'Ozumat is vulnerable, strike now!', 'Ozumat is vulnerable, strike now!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50864, 'enUS', 'Cowards!  I will not fall so easily into your trap!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50865, 'enUS', 'You there! Hiding like a coward when you should be fighting these devils?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50866, 'enUS', 'Quit running and DO SOMETHING!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50867, 'enUS', 'Feckless cowards, the lot of you! Maybe if you\'d show some backbone, I would actually pay your wages!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50868, 'enUS', 'Idiots! Dolts! Morons! Imbeciles! Fools! Dimwits! Ignoramuses! Shall I go on?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50869, 'enUS', 'Maybe someone should go fetch some water. A person with a brain would have done that already!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50870, 'enUS', 'You nincompoops! This place deserves to burn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50871, 'enUS', 'Nightmare spiders appear in the darkness!  Kill Helix before his nightmare overwhelms you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50874, 'enUS', 'You\'ve impressed me so far, $n. How do you feel about helping me in creating the ultimate dish?', '', 26124);
