INSERT INTO `broadcast_text_locale` VALUES (50878, 'enUS', '%s fills the area with a |cFF33FF00toxic gas|r!', '%s fills the area with a |cFF33FF00toxic gas|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50879, 'enUS', 'Woooweeee! Look at her run! Just relax and enjoy the view. I\'ll have us across the lake in no time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50880, 'enUS', 'Enjoying your stay? One dip in the steam pools and you come out looking 10 years younger. Just look at me if you want proof!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50881, 'enUS', 'Have you tried any of Chef Tramsay\'s dishes yet? I really enjoyed his Moth Soup. Delicious!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50882, 'enUS', 'Wait, do you see that light up there? What\'s going on?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50883, 'enUS', 'The...they\'re agents of the Twilight\'s Hammer! Is that...the Assistant Resort Director with them?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50884, 'enUS', 'Oh no! They\'ve spotted us! Hang on -- I\'ll get us out of here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50885, 'enUS', 'We barely made it out alive. You\'d better run and tell Lasha what you\'ve seen. We\'ve got a serious problem to deal with!', '', 26124);
