INSERT INTO `broadcast_text_locale` VALUES (50890, 'enUS', '', 'The darkness is closing in!  I will not go without a fight, monster!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50892, 'enUS', 'Hi, my name is Kevin, do you want to play a movie ?', 'Hi, my name is Kevin, do you want to play a movie ?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50893, 'enUS', 'I intend to fight the Soulflayer, but I have no way of summoning him.  Can you assist me?', 'I intend to fight the Soulflayer, but I have no way of summoning him.  Can you assist me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50894, 'enUS', 'Victory in Tol Barad is ours! While the Horde offensive is crippled, we need your immediate efforts to gather resources and secure the island!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50895, 'enUS', 'Victory in Tol Barad is ours! While the Alliance offensive is crippled, we need your immediate efforts to gather resources and secure the island!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50896, 'enUS', 'Greetings, hapless adventurer.  I ask only that you wait a moment...and BLEED!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50897, 'enUS', 'If you\'re looking for a place to rest, I can help.', 'If you\'re looking for a place to rest, I can help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50898, 'enUS', 'You choose my corpse over another ally against the undead?  So be it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50899, 'enUS', 'Burn...my corpse...do not let me join them...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50900, 'enUS', '', 'Plot revenue minus supply expenditures against the elasticity of demand, considering assets and liabilities inflected across the regression quotient....oh hi! How are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50901, 'enUS', 'Do you have what it takes to stay in my kitchen?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50902, 'enUS', 'Have you seen the outrageous prices here?', 'Have you seen the outrageous prices here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50903, 'enUS', 'Could you go get me a drink? I\'d do it myself, but I\'m working on my tan.', 'Could you go get me a drink? I\'d do it myself, but I\'m working on my tan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50904, 'enUS', 'The Twilight\'s Hammer will rise again! The very earth beneath your feet will be swallowed by the fury of our resurgence!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50905, 'enUS', 'Ah ken see very well through this haze, but I\'d know tha\' smell anywhere!  Die ye foul ogre!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50906, 'enUS', 'Ooooooooh...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50907, 'enUS', 'To the plaguelands went old Duggan, ta\' send them Scourge back inta\' th\' groun\'.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50908, 'enUS', 'Where th\' scent of death is on th\' wind and everythin\' is mostly brown.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50909, 'enUS', 'An\' when he did arrive there, what\'d his dwarf eyes see?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50910, 'enUS', 'A hundred crates of barley there ta\' be makin\' inta\' mead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50911, 'enUS', 'But tha\' mead was cursed with th\' plague o\' death, and now old Duggan, too.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50912, 'enUS', 'An\' surrounded by the Lich King\'s beasts, what could old Duggan do?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50913, 'enUS', 'But though I feel the plague within, my hopes \'ave not yet sunk.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50914, 'enUS', 'If\'n I\'m gonna be Scourge anyway, I might as well be drunk!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50915, 'enUS', 'This teleporter will transport you to Grom\'gol in Northern Stranglethorn.$B$BUsually.$B$BI mean, sometimes it\'ll just disintegrate you, or reduce all your soft tissues into a greenish, foul-smelling mist, but mostly it\'s safe!$B$B...mostly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50916, 'enUS', 'This teleporter will transport you to the irradiated ruins of Gnomeregan, deep beneath Dun Morogh.$B$BDon\'t worry, there\'s another one there that\'ll send you back here... well, if it doesn\'t turn you inside-out or transmogrify your organs into unstable organic explosives, that is!$B$BWell? What are you waiting for? Hop on in!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50917, 'enUS', 'Finally out from under the boot of that she-witch Abbendis, and free to pursue my own interests: BLOOD, BLOOD, AND MORE BLOOD!', 'Finally out from under the boot of that she-witch Abbendis, and free to pursue my own interests: BLOOD, BLOOD, AND MORE BLOOD!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50918, 'enUS', 'I haven\'t eaten in weeks; this bread is mine!  FOR THE SANDWICH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50919, 'enUS', 'I judge you...DELICIOUS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50920, 'enUS', 'Abbendis will return, and I will be waiting for her!  SHE WILL COME BACK FOR US!', 'Abbendis will return, and I will be waiting for her!  SHE WILL COME BACK FOR US!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50921, 'enUS', '', 'I will drive your foul stench from the forest, monster!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50923, 'enUS', 'Welcome to de great show, friends. Just wait \'til ya see what I got in store for ya.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50924, 'enUS', 'No... NOOOO!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50925, 'enUS', 'Ain\'t NO ONE gonna stop Jin\'do!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50927, 'enUS', 'Grass, sand, and sludge mix together to form the vague resemblance of a footprint.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50928, 'enUS', 'The tracks are smeared, almost as if the creature drags its feet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50929, 'enUS', 'Mixed in with the mud are traces of oily sludge from the nearby rig.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50930, 'enUS', 'This trail is bloody. You follow it to a nearby bush, where you find the remains of the creature\'s last meal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50931, 'enUS', 'These tracks are fresh, and they lead down into the water...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50933, 'enUS', '', 'Did Brak say this teleporter thing was tested? Well, if you\'re ready to go back to Nozzlepot\'s Outpost, just say the word! I hope you get there in one piece.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50934, 'enUS', '', 'Did you see that? Someone just fell out of the sky!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50935, 'enUS', 'Send me back to Nozzlepot\'s Outpost.', 'Send me back to Nozzlepot\'s Outpost.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50936, 'enUS', 'Wow, this sure goes fast!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50937, 'enUS', 'You disssssturb the plans of Gurubashi, little one.  It\'sss too late for you.  Too late for all of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50938, 'enUS', '', 'Haaa ha ha haa!  Now, heathens... face the TRUE might of Bethekk!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50939, 'enUS', '', 'What have you done? I can\'t... control... RRAAAARRRGHHH!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50940, 'enUS', 'I\'m ready. Let\'s go!', 'I\'m ready. Let\'s go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50941, 'enUS', '$n, glad to see you\'re still in one piece! I saw a rocket fly by earlier and heard your voice screaming, so I was a bit worried.$B$BWe have one last shipment to take care of. Talk to me when you\'re ready and we\'ll get going.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50942, 'enUS', 'Everybody on board! This caravan\'s leaving for the Mor\'shan Rampart!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50943, 'enUS', 'How are you doing back there? Not too bumpy I hope.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50944, 'enUS', 'Our enemies wouldn\'t dare attack us this close to the Crossroads. It\'s further out that we\'ll have to watch our backs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50945, 'enUS', 'Hope you don\'t mind, but we\'ll stop at Dreadmist Camp to pick up additional supplies and fresh mounts for the last leg of the journey.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50949, 'enUS', '', 'How can Bwemba aid ya?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50950, 'enUS', 'Yesss...ssssuccumb to the venom...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50951, 'enUS', 'I need your help to remove the poison from these adventurers.', 'I need your help to remove the poison from these adventurers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50952, 'enUS', 'You seek power, yes? But will you walk the cursed path I do for it?', 'You seek power, yes? But will you walk the cursed path I do for it?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50953, 'enUS', 'My path is a dark and cursed one, $c. You do not wish to walk with me.', 'My path is a dark and cursed one, $c. You do not wish to walk with me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50954, 'enUS', 'We\'re here. Just wait a moment while we load up---HEY! What\'s happening!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50955, 'enUS', '', 'Quickly now! Grab one of them while I take the supply cart!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50956, 'enUS', 'Guild Services', 'Guild Services', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50957, 'enUS', 'You may find Shattrath\'s guild master - as well as guild and tabard vendors - within the Terrace of Light\'s central structure, in the middle of the city.', 'You may find Shattrath\'s guild master - as well as guild and tabard vendors - within the Terrace of Light\'s central structure, in the middle of the city.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50958, 'enUS', 'Thank you for your help.', 'Thank you for your help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50959, 'enUS', 'Class trainers may be found in the various ground-floor wings of the Terrace of Light\'s central structure.$B$BAldor, Scryer or otherwise, someone will be able to help you.', 'Class trainers may be found in the various ground-floor wings of the Terrace of Light\'s central structure.$B$BAldor, Scryer or otherwise, someone will be able to help you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50960, 'enUS', 'You saved my life!', 'You saved my life!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50961, 'enUS', 'Another troll?  At least you aren\'t a serpent...', 'Another troll?  At least you aren\'t a serpent...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50962, 'enUS', 'The serpents, they are everywhere!', 'The serpents, they are everywhere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50963, 'enUS', 'The serpents overwhelmed us.', 'The serpents overwhelmed us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50964, 'enUS', 'I thought I was going to die in this awful jungle.', 'I thought I was going to die in this awful jungle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50965, 'enUS', 'I had just about given up hope.', 'I had just about given up hope.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50966, 'enUS', 'Thank you for saving my life.', 'Thank you for saving my life.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50967, 'enUS', '<The Arcane Guardian gestures towards the city\'s center.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50968, 'enUS', 'Ho, Nagala! How about some lunch for your starving brother?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50969, 'enUS', '', 'You\'ve grown a few inches around the waist, little brother! Come down here and give your sister a hug!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50970, 'enUS', 'Which one should I take?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50971, 'enUS', '', 'Balgor! No!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50972, 'enUS', 'Attack the nightmare illusions and defend your sleeping allies!', 'Attack the nightmare illusions and defend your sleeping allies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50973, 'enUS', '', 'Bwemba be watchin\' you, young one.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50974, 'enUS', 'Now, have a taste of Jin\'do\'s true powah!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50975, 'enUS', '', 'The fat one! He\'ll make for a nice sacrifice.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50976, 'enUS', 'The Burning Blade sends its greetings.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50977, 'enUS', 'Look, someone\'s attacking Sentinel Hill. C\'mon guards, you just hafta win!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50978, 'enUS', 'I\'ve never seen a flying tree before. Let\'s get closer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50979, 'enUS', 'That looks fun! I wanna drive, but the orphan matron says I can\'t get a mount \'til I\'m older.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50980, 'enUS', 'Look at all that slime. One is flying straight at me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50981, 'enUS', 'Eeeeeeeeeeeeeeew, it\'s on my head! Help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50982, 'enUS', '%s is about to blast you with lightning! Get away!', '%s is about to blast you with lightning! Get away!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50983, 'enUS', 'My tummy doesn\'t feel so good...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50984, 'enUS', 'Whoa! Did you see how high we went?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50985, 'enUS', 'That was great! Let\'s do it again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50986, 'enUS', 'You disssssturb the plans of Gurubashi, little one.  It\'sss too late for you.  Too late for all of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50987, 'enUS', 'My ssssserpents, dey already ssssslither beneath every rock an\' shrub of Ssssstranglethorn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50988, 'enUS', 'We will reclaim thisss land. Gurubashi land.  An\' we kill anyone dat getssss in our way!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50989, 'enUS', 'We reclaiming our land, Gurubashi land.  An\' we gonna kill anyone dat get in our way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50990, 'enUS', 'Go an\' get dem, Ohgan! We WON\'T be fooled again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50991, 'enUS', 'Shaman...? A troll mystic of some sort has come to the city recently to visit Harene upon the Walk of Elders. She might be able to help you.', 'Shaman...? A troll mystic of some sort has come to the city recently to visit Harene upon the Walk of Elders. She might be able to help you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50992, 'enUS', 'I thought the Park fell into a big hole, but this one\'s way bigger and it has a whirlwind!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50993, 'enUS', 'They\'re planning to sacrifice me! Their demons are already licking their chops over my corpse.$B$B<Balgor shudders.>$B$BMakes me think twice about my own diet.$B$B Anyway, they\'ve got a guard watching my every move. I can get myself out of here, but I need a good distraction.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50994, 'enUS', '', 'What!? The Burning Blade kidnapped my brother?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50995, 'enUS', '', 'All the supplies flowing north to Ashenvale stop here to rest and re-fuel. It\'s important to keep our operations running smoothly.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50996, 'enUS', 'I\'m ready to ride.', 'I\'m ready to ride.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50997, 'enUS', 'There is a pair of tauren visiting the city. I\'m told they\'ve come from Mulgore to learn more about the Forsaken \"condition,\" or something to that effect.$B$BSeek them out near the herbalist by the Apothecarium.', 'There is a pair of tauren visiting the city. I\'m told they\'ve come from Mulgore to learn more about the Forsaken \"condition,\" or something to that effect.$B$BSeek them out near the herbalist by the Apothecarium.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50998, 'enUS', '|TInterface\\Icons\\ability_heroicleap.blp:20|t%s begins to cast |cFFFF0000|Hspell:96740|h[Devastating Slam]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50999, 'enUS', 'This has to be the biggest diamond ever!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51000, 'enUS', 'It looks just like the paintings of King Magni too! Did he really do this to himself?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51001, 'enUS', 'Aye, lad. He tried to use an old titan ritual to keep his people safe, but this is what it did to him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51002, 'enUS', 'Wow, I don\'t think that was such a good idea...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51003, 'enUS', 'I\'m really sorry you lost your king. But in a way, it\'s like he\'s with you forever!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51004, 'enUS', 'They\'ve spotted us! Defend the caravan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51005, 'enUS', 'This was my fault, my lord. I should\'ve done more research on those tablets...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51006, 'enUS', 'Thank you, lad.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51007, 'enUS', 'It\'s got me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51008, 'enUS', 'Welcome, my $g brother : sister;.  If you are here to train, then I would be delighted to train you.', 'Welcome, my $g brother : sister;.  If you are here to train, then I would be delighted to train you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51009, 'enUS', 'If you seek training in the Old Ways, I cannot help you.', 'If you seek training in the Old Ways, I cannot help you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51010, 'enUS', 'There\'s a worgen druid by the name of Dareth just outside the city gates; he might be able to help you.$B$BBig beast of a fellow, covered in white leather and furs. You can\'t miss him.', 'There\'s a worgen druid by the name of Dareth just outside the city gates; he might be able to help you.$B$BBig beast of a fellow, covered in white leather and furs. You can\'t miss him.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51011, 'enUS', 'Wow, you sure don\'t look like any night elf I\'ve seen before!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51012, 'enUS', 'Our orphan matron says druids can turn into birds, cats, and bears, but you look like a combination of all of them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51013, 'enUS', 'I see what you\'re doing. You\'re gonna take all the druid forms and make them into one super-form!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51014, 'enUS', 'The Emerald Dream doesn\'t sound like a place for me. What if it turns me into a turtle? I\'ve always wanted wings, though...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51015, 'enUS', 'Wheeee! I\'m a bird-boy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51016, 'enUS', 'No, little one, it\'s nothing like that. My spirit spent many years in the Emerald Dream while my body slept, and it started to take on attributes of our animal forms.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51017, 'enUS', 'Good day! Need some screws tightened or some bolts loosened? You\'ve come to the right place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51018, 'enUS', 'Huuuuuuuunnnnnngggrryyyy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51019, 'enUS', 'Meeeee sooooorrryyyy....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51020, 'enUS', 'Wow, wow, wow! I can\'t believe Sputtervalve actually let me drive this baby! Have I mentioned this is my first time flying? Don\'t worry though. I have everything under control.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51021, 'enUS', 'Man, oh man...Just look at that amazing view! Are those LIONS? Oops, almost let go of the steering wheel there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51022, 'enUS', 'Wait until the guys back home find out about this! This is soooo totally geared out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51023, 'enUS', 'My dream is to be as brilliant as Sputtervalve someday. You should see his design for a two-wheeled tricycle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51024, 'enUS', 'Huh? Hold on, the engine stalled...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51025, 'enUS', 'Whoa! What\'s going on?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51026, 'enUS', 'No, no, no...this can\'t be happening. This wasn\'t part of the travel plan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51027, 'enUS', 'Auuugh! Looks like we\'re taking an unexpected detour! Hold on tight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51028, 'enUS', 'Maybe if I press this green button. No, what about this blue one? Hold on, let me try resplicing some wires. Ow!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51029, 'enUS', 'Nothing\'s working!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51030, 'enUS', 'Oh no, Sputtervalve is going to kill me! Sigh...You\'d better find your own transportation from here. I have to pick up the pieces and start the long walk back to camp...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51031, 'enUS', 'Well here we are, back at the Crossroads, safe and sound.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51032, 'enUS', 'The mists around the Altar of Hethiss dissipate as Venoxis prepares a powerful spell!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51033, 'enUS', '|TInterface\\Icons\\ability_creature_poison_06.blp:20|t High Priest Venoxis begins casting |cFFFF0000|Hspell:96637|h[Bloodvenom]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51034, 'enUS', 'High Priest Venoxis is exhausted!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51035, 'enUS', 'I can\'t stop it. We\'re in for a crash landing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51036, 'enUS', 'Inspect the body.', 'Inspect the body.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51037, 'enUS', 'The large cat doesn\'t seem quite as big as the hunter lead you to believe.', 'The large cat doesn\'t seem quite as big as the hunter lead you to believe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51038, 'enUS', 'Let\'s see what we got here...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51039, 'enUS', 'Hmm...  The beast looks smaller than I would have thought.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51040, 'enUS', '', 'Death to your kind!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51041, 'enUS', '', 'Serena Bloodfeather will rip out your eyes and feast upon your heart!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51042, 'enUS', '', 'You will pay for my sister\'s death!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51043, 'enUS', '', 'Our matron craves the taste of your blood!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51044, 'enUS', '', 'Rexxar\'s ally! Murderer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51045, 'enUS', 'The Argent Crusade stands behind you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51046, 'enUS', 'Ahoy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51047, 'enUS', 'Mrfmmmm... mrrr.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51048, 'enUS', 'Heeeeere\'s... Theldurin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51049, 'enUS', 'Senior Initiate Goldmine, reporting for duty!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51050, 'enUS', 'This is gonna be a bloodbath. A bloodbath... of flames!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51051, 'enUS', 'Fear not. Johnny Awesome fights by your side.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51052, 'enUS', '', 'Two knives and a goblin. What more could you ask for?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51053, 'enUS', 'Did somebody call for a paladin?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51054, 'enUS', 'Ahoy there, captain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51055, 'enUS', 'What ho, fellow knights! Are we fully barded and armored? Then let us thrust ourselves deeply into that which they call adventure!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51056, 'enUS', 'You have my sword.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51057, 'enUS', 'Let\'s see what kind of trouble we can get into today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51058, 'enUS', 'Lok\'tar, hero. Today I fight with you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51059, 'enUS', '', 'My talons will slice you to shreds, invader.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51060, 'enUS', 'Hello.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51061, 'enUS', 'Let the slaughter begin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51062, 'enUS', 'Here come Zen\'Kiki!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51063, 'enUS', '', 'Light be with you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51064, 'enUS', '', 'The Earthen Ring stands behind you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51065, 'enUS', 'The druids of DEHTA support your efforts here in Hyjal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51066, 'enUS', 'I heard Azeroth needed saving. May I lend a hand?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51067, 'enUS', 'I prefer my fish boiled AFTER they\'ve been caught. Let\'s put out these fires.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51068, 'enUS', 'Lunk here to help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51069, 'enUS', '', 'Da Darkspear fight alongside ya.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51070, 'enUS', '', 'Reporting for duty, commander.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51071, 'enUS', 'Let me at \'em! I\'ll blast \'em off the side o\' the mountain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51072, 'enUS', 'Congratulations. Today you will have the honor of fighting alongside me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51073, 'enUS', 'Fire elementals must die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51074, 'enUS', '', 'Wait a minute... where am I? WHEN am I? Are we in Hyjal?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51075, 'enUS', 'Calder Gray, reporting for mayhem... er, duty.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51076, 'enUS', 'Did somebody say Grapplehammer? No? Oh well, here I am.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51077, 'enUS', 'If only Gidwin were here... he\'d love to see this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51078, 'enUS', 'By Light be purged!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51079, 'enUS', 'Hah! I was expecting a challenge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51080, 'enUS', 'I\'m fiiiighting!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51081, 'enUS', 'Weee!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51082, 'enUS', 'Bonk! Yes sir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51083, 'enUS', 'MRRRFFFFMMMMRRR!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51084, 'enUS', 'MRRRFRRRR!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51085, 'enUS', 'mmmmmMMMMMMRRRF!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51086, 'enUS', 'Take that, dangerous enemy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51087, 'enUS', 'Too bad for you... I brought my fists with me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51088, 'enUS', 'Look out Ragnaros! You\'re next!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51089, 'enUS', 'I\'ve got a history with fire elementals, you know. I consider myself somewhat of an expert on the subject.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51090, 'enUS', 'Yeesh! These guys are really hot!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51091, 'enUS', 'Maybe after this, they\'ll take away my \"Initiate\" title.', '', 26124);
