INSERT INTO `broadcast_text_locale` VALUES (51096, 'enUS', 'Johnny! Awesome!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51097, 'enUS', 'You never stood a chance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51098, 'enUS', '', 'Stabby stabby stab stab!', 26124);
