INSERT INTO `broadcast_text_locale` VALUES (51103, 'enUS', 'For the Crusade! Hyaaah!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51104, 'enUS', 'I\'ve always wanted my very own dragon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51105, 'enUS', 'Come and get your pain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51106, 'enUS', 'I am... a tauren... of the SEA!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51107, 'enUS', 'Yarrr!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51108, 'enUS', 'For Doloria!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51109, 'enUS', 'Be slain by me, villainous cretin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51110, 'enUS', 'Be proud, dragon. Today you fall to the blade of Azeroth\'s greatest hero. And also to my blade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51111, 'enUS', 'That was fun! Can I keep the kite, please?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51112, 'enUS', 'Excuuuse me, princess!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51113, 'enUS', 'Oh, boy! I\'m so hungry, I could eat a fire elemental!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51114, 'enUS', 'I\'m not used to fighting with other people.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51115, 'enUS', 'I\'ve got a proposition for you: You die now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51116, 'enUS', 'They used to call me Tony THREE-Tusk.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51117, 'enUS', 'I\'m going to take you down to pain town.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51118, 'enUS', 'For the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51119, 'enUS', 'Blood and thunder!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51120, 'enUS', 'LOK\'TAR OGAR!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51121, 'enUS', 'For the Forsaken! For Lordaeron!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51122, 'enUS', 'This one won\'t be coming back.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51123, 'enUS', 'You\'ll get no mercy from me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51124, 'enUS', 'RRRAAAAGGHHH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51125, 'enUS', 'DIE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51126, 'enUS', 'MY WIFE DIED, YOU MONSTERS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51127, 'enUS', 'Zen\'Kiki really tempted to shapeshift right now...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51128, 'enUS', 'De Cenarion Circle gonna getcha, mon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51129, 'enUS', 'Dis is no problem for a mighty druid like Zen\'Kiki.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51130, 'enUS', '', 'Go with the Light.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51131, 'enUS', '', 'None escape the judgment of the Light.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51132, 'enUS', '', 'Confess your sins, and be forgiven!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51133, 'enUS', '', 'Leave Azeroth be!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51134, 'enUS', '', 'Azeroth has no place for you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51135, 'enUS', '', 'Be gone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51136, 'enUS', 'These abominations are responsible for the deaths of dozens of squirrels! They deserve no mercy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51137, 'enUS', 'No surrender! No mercy! For AZEROTH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51138, 'enUS', 'MRGLLL GLRRM GL! Translation: I will kill you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51139, 'enUS', 'We ogres are not all bad.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51140, 'enUS', 'You have the support of Ogri\'la.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51141, 'enUS', 'We are friends of the world that you call Azeroth. I am happy to provide any assistance that I can.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51142, 'enUS', 'Hook, line, and slaughter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51143, 'enUS', 'You know, this isn\'t really my scene. I\'m glad to help, though.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51144, 'enUS', 'I could use a nap right about now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51145, 'enUS', 'You go away now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51146, 'enUS', 'Lunk warn you one last time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51147, 'enUS', 'Lunk no kill!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51148, 'enUS', '', 'De elements walk wit\' us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51149, 'enUS', '', 'Dese spirits don\'t belong!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51150, 'enUS', '', 'For de Darkspear!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51151, 'enUS', 'My family has been running caravan routes in the Barrens since Orgrimmar was settled. We raise and train the kodos ourselves. My sister Nagala is the best kodo breeder outside of Mulgore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51152, 'enUS', '', 'Nature\'s fury overwhelms you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51153, 'enUS', '', 'Hyjal shall not fall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51154, 'enUS', '', 'Ancients be praised!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51155, 'enUS', 'Blam! Right between the eyes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51156, 'enUS', 'The thrill of the hunt, right lads?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51157, 'enUS', 'I\'ve got a present for ye! Here, let me just get it out of my gun...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51158, 'enUS', 'You have been tested... and you have failed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51159, 'enUS', 'Time to fight fire with fire! And frost! And shadow!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51160, 'enUS', 'You\'ve fallen right into my trap!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51161, 'enUS', 'I think I knocked that one into next week.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51162, 'enUS', 'I came all the way from Dalaran, and this is the welcoming I get?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51163, 'enUS', 'The ruling council has ordered you banished. I plan to see those orders out.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51164, 'enUS', '', 'I can\'t believe you guys are still fighting here in Hyjal. You should see what happens after this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51165, 'enUS', '', 'You know what time it is? That\'s right, it\'s time to DIE!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51166, 'enUS', '', 'You just got Chromie\'d!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51168, 'enUS', 'There\'s really nothing I enjoy more than the sound of screaming victims. Especially if they\'re MY victims.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51169, 'enUS', 'You know the worst thing about fire creatures? No harvestable organs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51170, 'enUS', 'Fire and rocks don\'t really do anything for me. I\'m really more of a flesh man.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51171, 'enUS', 'You know what? You remind me of the Ambulatory Campfire prototype that I\'ve been working on.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51172, 'enUS', 'If you can dodge a double-binary fluxcillometric auto-wrench, you can dodge a ball!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51173, 'enUS', 'I\'m a genius!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51174, 'enUS', 'What, are we done already?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51175, 'enUS', 'Toodly-doo!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51176, 'enUS', 'Mrmrffmm. Ffm.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51177, 'enUS', 'Wait until the guys hear about this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51178, 'enUS', 'Glad to be of service!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51179, 'enUS', 'Back to work for me. I\'m a busy goblin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51180, 'enUS', 'While you may be sad to see me go, I\'m sure you will enjoy watching me leave.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51181, 'enUS', '', 'Hey, good show out there!  You let me know if you need anything else stabbed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51182, 'enUS', 'The Crusade appreciates the work you\'re doing out here, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51183, 'enUS', 'Thanks for the opportunity to take it to Ragnaros.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51184, 'enUS', 'You have, in the face of terrifying adversity, shown true valor and chivalry. From one glimmering beacon of heroism to another: I salute you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51185, 'enUS', 'Well fought. Farewell, hero.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51186, 'enUS', 'Until next time! Try to stay alive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51187, 'enUS', 'You fight well, hero. I look forward to fighting by your side again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51188, 'enUS', 'Dark Lady watch over you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51189, 'enUS', 'Back to de plaguelands!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51190, 'enUS', '', 'Stand strong, combatants.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51191, 'enUS', '', 'Always know that you have allies in Deepholm, if you be needin\' us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51192, 'enUS', 'And with that, I shall return to my research.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51193, 'enUS', 'It is time that I returned to Ogri\'la. I hope you do not forget about us, as we have not forgotten you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51194, 'enUS', 'So long, and thanks for all the fights!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51195, 'enUS', 'Lunk think you killed too much bad guy. But Lunk still think you OK $g guy:lady;.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51196, 'enUS', '', 'Thank you for the opportunity. I shall report to Vol\'jin with your regards.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51197, 'enUS', '', 'I will never stop fighting to protect Hyjal. Not after all we\'ve done to heal her.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51198, 'enUS', 'I hope ye had as much fun as I did.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51199, 'enUS', 'This was not worth my time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51200, 'enUS', 'Until we meet again... you know where to find me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51201, 'enUS', '', 'See you next time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51202, 'enUS', 'I should really be going. All this killing reminded me I still have some fresh corpses that aren\'t sewn to one another.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51203, 'enUS', 'There we have it! Back to the labs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51204, 'enUS', 'My rage is not silenced... but it is quieter. For now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51205, 'enUS', 'Fall back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51206, 'enUS', 'Belay your attack orders, men!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51207, 'enUS', 'Stay your blades, heroes. This dragon\'s scales glow red with flame.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51208, 'enUS', 'Everybody, stop your attacks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51209, 'enUS', 'Hold your fire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51210, 'enUS', 'Hold your weapons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51211, 'enUS', 'Hold your attacks while the shield is up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51212, 'enUS', 'Stop your attacks! Its shield will overwhelm you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51213, 'enUS', 'I AM here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51214, 'enUS', 'Bet you didn\'t know that a kodo can eat its own weight in grass each day. Sure is a lot to shovel when it comes out the other end.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51215, 'enUS', 'Handmade toys from all over Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51216, 'enUS', '', 'I can hear the howls of the Burning Blade as we speak. The kodos are ready to ride for the Mor\'shan Ramparts. Tell me when you are ready to leave.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51217, 'enUS', 'Don\'t attack it while the shield is up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51218, 'enUS', '', 'Not now, mortal.$b$bI am here on urgent business.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51219, 'enUS', 'Let\'s see what be wrong wit\' dis creature?', 'Let\'s see what be wrong wit\' dis creature?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51220, 'enUS', '', 'Da whispers of Hethiss gonna poison ya\' mind!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51221, 'enUS', 'I\'m gonna turn ya\' blood ta venom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51222, 'enUS', '', 'Kilnara laughs maniacally.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51223, 'enUS', '', 'You kill him - you kill my cat! Why must I suffer so?\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51224, 'enUS', '', 'Stranglethorn belongs to da Gurubashi. You best be leavin\' before we bury you here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51225, 'enUS', '', 'Or stay if you want. We always be needin\' more to sacrifice.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51226, 'enUS', 'By the skin of our teeth, we made it! I haven\'t seen shooting like that since...well...ever! I wish we could ride together again, but Kadrak has more important work for you. Thank you for delivering us safely, my friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51227, 'enUS', 'Thank you, old friend. Our warriors thank you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51228, 'enUS', 'Good to see you again, friend. Make haste and hop on while the way is still clear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51229, 'enUS', 'Look sharp! They\'re closing in on our heels!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51230, 'enUS', 'Watch out above us! Use your rocket!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51231, 'enUS', 'They\'re coming from all sides! Hang on just a little longer - we\'re almost there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51232, 'enUS', 'Galenges is weakened by Malorne\'s Antlers! Attack him now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51233, 'enUS', 'Good shooting! That one won\'t be getting up anytime soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51234, 'enUS', '', 'Do not take offense, $r.$b$bI simply haven\'t the time for distractions.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51235, 'enUS', '', 'Forgive my abruptness, $c.$b$bPressing matters of great import require my undivided attention.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51236, 'enUS', 'Ahh! What is the meaning of this!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51237, 'enUS', 'A noble effort... but it will take more than one of your puny stags to take down Galenges, the UNBREAKABLE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51238, 'enUS', '', 'Ahh! There you are, $n.$b$bI must speak with you at once.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51239, 'enUS', 'You stand no chance against me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51240, 'enUS', 'Which one dares to challenge Galenges? Stand forward, weakling!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51241, 'enUS', 'Da Gurubashi be takin\' our land back.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51242, 'enUS', 'We let you stay here too long.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51243, 'enUS', 'You tink you strong.  You tink you brave.  You tink you can survive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51244, 'enUS', 'But we know...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51245, 'enUS', 'You and all you people, you already dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51246, 'enUS', 'An\' once you dead, den you serve da Gurubashi.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51247, 'enUS', 'Kill dese worms!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51248, 'enUS', 'Welcome to the Cold of Cold, friend! Our unique ice cream is chilled to order by one of Stormwind\'s finest apprentice mages!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51249, 'enUS', '|TInterface\\Icons\\inv_misc_eye_04.blp:20|t%s begins to cast |cFFFF0000|Hspell:96920|h[Eyes of Occu\'thar]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51250, 'enUS', 'Rocket launch in 3...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51251, 'enUS', 'Rocket launch in 2...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51252, 'enUS', 'Rocket launch in 1...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51253, 'enUS', 'GO!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51254, 'enUS', 'Shh! I\'m working undercover here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51255, 'enUS', 'What are you looking at?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51256, 'enUS', 'Have we met?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51257, 'enUS', 'I heard Tortolla was being held here, so I came over as quickly as I could.$b$bMount Hyjal is really tall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51258, 'enUS', 'Are you ready to face Nemesis? I hope you have your totem ready.$b$bLet me know when you want to summon him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51259, 'enUS', 'I am ready to meet with Anachronos, Ziradormi. ', 'I am ready to meet with Anachronos, Ziradormi. ', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51260, 'enUS', 'I am prepared to face Nemesis, Tooga.', 'I am prepared to face Nemesis, Tooga.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51261, 'enUS', 'NOW, YOUNG ONES! ATTACK THE PORTAL! FOR TORTOLLA!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51262, 'enUS', 'I am ready to meet with Anachronos, Coridormi. ', 'I am ready to meet with Anachronos, Coridormi. ', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51263, 'enUS', 'Ye don\' want ta\' be goin up dis\' way, mon.  Da spirits be restless.\n\nMebbe if ye\' know a ting or two about ancient artifacts, ye can fin\' a way to put dem to rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51264, 'enUS', '', 'You mustn\'t tarry, $n.$b$bAnachronos awaits!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51265, 'enUS', 'CLIMB.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51266, 'enUS', '', 'Sacrifice yo\' bodies ta Hethiss mah bruddahs, da voodoo will protect you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51267, 'enUS', 'Though this artifact seems to be in pristine condition, you sense nothing particularly special about it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51268, 'enUS', 'This artifact appears to be intact, though it is so thoroughly coated in dirt and mud that it is difficult to tell for sure.  There does not seem to be anything inherently magical about it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51269, 'enUS', 'This artifact has weathered many storms, and shows significant signs of deterioration.  It is doubtful that any powers it once held remain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51270, 'enUS', 'Several leaves and branches cling to this object, indicating that it may have been used as part of a nest for some creature.  There is otherwise nothing of note.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51271, 'enUS', 'Several strange carvings adorn this object, perhaps made by the trolls in an attempt to decipher its origins.  They did not seem to meet with much success, as the object remains inert.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51272, 'enUS', 'Mysterious runes adorn this artifact, though they appear to be unpowered at this time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51273, 'enUS', 'This object is of low quality and is likely one of many fake artifacts that have been flooding the black market.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51274, 'enUS', 'Several spots of dried blood can be seen on this object, likely a result of the struggle to obtain the artifact by the trolls.  The object is quite plain, however, and may not have been worth the struggle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51275, 'enUS', '', 'Ruby, do you want a treat?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51276, 'enUS', '', 'Ruby, play dead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51277, 'enUS', '', 'Ruby, speak.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51278, 'enUS', '', 'Ruby, jump.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51279, 'enUS', '', 'Ruby, walk backwards.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51280, 'enUS', '', 'Ruby, dance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51281, 'enUS', '', 'Good girl, Ruby!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51282, 'enUS', '', 'That\'s a good widdle waptor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51283, 'enUS', '', 'You\'re a smart girl, aren\'t you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51284, 'enUS', '', 'Yay! That was amazing!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51285, 'enUS', 'Wow, I never knew mages could make ice cream. That was good!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51286, 'enUS', 'When I grow up, I want to put strange things together too!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51287, 'enUS', 'What about... putting soap on a fork? Then it would have a handle and everything!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51288, 'enUS', 'And when the soap\'s gone, you\'d have a new fork!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51289, 'enUS', 'There is something odd about this particular object...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51290, 'enUS', 'Examine the object.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51291, 'enUS', 'This artifact has been cursed by the troll witch doctors!  At your touch, it begins to emit a foul glow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51292, 'enUS', 'Be at ease, mortal. Our time together here must be productive, and unclouded by tension.$b$bMuch depends on your ability to think clearly and reason well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51293, 'enUS', 'Examine the object.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51294, 'enUS', 'Examine the object.', 'Examine the object.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51295, 'enUS', 'Let me see what you have for sale.', 'Let me see what you have for sale.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51297, 'enUS', '', 'Children, $n has brought a gift for you! Why don\'t you come take a look?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51298, 'enUS', 'Wow, a surprise! I love surprises!', 'Wow, a surprise! I love surprises!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51299, 'enUS', 'BOOO!  Did I scare you?$b$bHallow\'s End is all about two things, tricks and treats!  Some adults think it\'s more.  They tried to tell me, but I got bored, until things got set on fire.  That was cool!$b$bBut still, even fire can get boring.  I can tell you what doesn\'t get boring... Tricking people and eating candy!$b$bBut I only want one kind of candy, Candy Coins!  They are the best.  They look like real gold coins!  Also, if you have too many coins, you can eat them!  It\'s like being rich and eating your money!  It\'s crazy!$b$bSo... Do you have some coins you want to trade?', 'BOOO!  Did I scare you?$b$bHallow\'s End is all about two things, tricks and treats!  Some adults think it\'s more.  They tried to tell me, but I got bored, until things got set on fire.  That was cool!$b$bBut still, even fire can get boring.  I can tell you what doesn\'t get boring... Tricking people and eating candy!$b$bBut I only want one kind of candy, Candy Coins!  They are the best.  They look like real gold coins!  Also, if you have too many coins, you can eat them!  It\'s like being rich and eating your money!  It\'s crazy!$b$bSo... Do you have some coins you want to trade?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51300, 'enUS', 'Nagala will have her famous grilled zhevra chops waiting for us, no doubt. She says I need to cut back and eat more vegetables, but I don\'t think I\'ve gained that much weight...do you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51301, 'enUS', 'I have some Candy Coins I would like to exchange.', 'I have some Candy Coins I would like to exchange.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51302, 'enUS', 'Show me the sugar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51303, 'enUS', 'Help! Let me go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51304, 'enUS', 'This is the best!', 'This is the best!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51305, 'enUS', 'Yes, Anachronos. I am ready to witness your vision of the future.', 'Yes, Anachronos. I am ready to witness your vision of the future.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51306, 'enUS', '', 'Aww Ruby, you\'re the best pet ever!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51307, 'enUS', 'Greetings, $c. My name is Thork--I lead the defenses here in the name of the Warchief.$B$BI see you have had a chance to help out here at the Crossroads. Carry on for the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51308, 'enUS', 'You can\'t catch me!', 'You can\'t catch me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51309, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51310, 'enUS', 'Look at me, I\'m Uther Lightbringer!', 'Look at me, I\'m Uther Lightbringer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51311, 'enUS', 'This is the biggest sword ever!', 'This is the biggest sword ever!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51312, 'enUS', 'Eject! Eject! Auuuuugghhh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51313, 'enUS', 'You overstepped your bounds, Jin\'do. You toy with powers that are beyond you. Have you forgotten who I am? Have you forgotten what I can do?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51314, 'enUS', 'Ye want Deepmountain Mining and Jewelcrafting, over on the northern side of The Great Forge.', 'Ye want Deepmountain Mining and Jewelcrafting, over on the northern side of The Great Forge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51315, 'enUS', 'You\'ll find a jewelcrafting trainer and supplies on the inner ring of the War Quarter.', 'You\'ll find a jewelcrafting trainer and supplies on the inner ring of the War Quarter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51316, 'enUS', 'Go to inner ring of War Quarter.  You find jewel-making people there.', 'Go to inner ring of War Quarter.  You find jewel-making people there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51317, 'enUS', 'Thank you for your help.', 'Thank you for your help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51318, 'enUS', 'You saved my life!', 'You saved my life!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51319, 'enUS', 'Wha... What is going on?  The last thing I remember was sitting down to eat...', 'Wha... What is going on?  The last thing I remember was sitting down to eat...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51320, 'enUS', 'Zombies... They are everywhere!', 'Zombies... They are everywhere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51321, 'enUS', 'We were just trying to eat and suddenly zombies started to appear!', 'We were just trying to eat and suddenly zombies started to appear!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51322, 'enUS', 'The food, there is something wrong with the food!', 'The food, there is something wrong with the food!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51323, 'enUS', 'I had just about given up hope.', 'I had just about given up hope.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51324, 'enUS', 'Oh, my stomach... That food really was awful.', 'Oh, my stomach... That food really was awful.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51325, 'enUS', 'Listen well, mortal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51326, 'enUS', 'Countless threads are woven to create the fabric of our timeline.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51327, 'enUS', 'As the wardens of time, we of the bronze dragonflight observe their infinite combinations.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51328, 'enUS', 'When disruptions are detected by our watchers, we are charged with their repair.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51329, 'enUS', 'A recent event, obscured by powerful magics, remains masked even to me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51330, 'enUS', 'But the unavoidable outcome is plain:', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51331, 'enUS', 'Azeroth will burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51332, 'enUS', 'While the details remain shrouded from our view, one thing we have gleaned...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51333, 'enUS', 'The fate of Azeroth hinges on the deeds of a mortal druid.', 'The fate of Azeroth hinges on the deeds of a mortal druid.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51334, 'enUS', 'The fate of Azeroth hinges on the deeds of a mortal mage.', 'The fate of Azeroth hinges on the deeds of a mortal mage.', 26124);
