INSERT INTO `broadcast_text_locale` VALUES (51336, 'enUS', 'The fate of Azeroth hinges on the deeds of a mortal shaman.', 'The fate of Azeroth hinges on the deeds of a mortal shaman.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51337, 'enUS', 'The fate of Azeroth hinges on the deeds of a mortal warlock.', 'The fate of Azeroth hinges on the deeds of a mortal warlock.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51338, 'enUS', 'Are you the mortal on which so much depends?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51339, 'enUS', 'The cub bats one paw at a tiny insect, only taking note of your attention when the plaything flies away.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51340, 'enUS', 'The cub nuzzles your hand and purrs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51341, 'enUS', 'A blacksmith and his assistant can be found working in the Warrior\'s Terrace, near the armor and weapon vendors there.', 'A blacksmith and his assistant can be found working in the Warrior\'s Terrace, near the armor and weapon vendors there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51342, 'enUS', 'Your little cub nips and paws at the air, eager to try out his hunting skills.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51343, 'enUS', 'The cub sniffs your hand and decides you\'re not a threat... this time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51344, 'enUS', 'At least one Gilnean engineer works out of the Craftsmen\'s Terrace. She should be able to help you.', 'At least one Gilnean engineer works out of the Craftsmen\'s Terrace. She should be able to help you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51345, 'enUS', 'Ah, you seek the Silverdews in the Craftmen\'s Terrace. They\'ve only recently set up shop, near the enchanter\'s.', 'Ah, you seek the Silverdews in the Craftmen\'s Terrace. They\'ve only recently set up shop, near the enchanter\'s.', 26124);
