INSERT INTO `broadcast_text_locale` VALUES (52453, 'enUS', 'Now you burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52454, 'enUS', 'Riplimb! No... no! Oh, you terrible little beasts! How could you?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52455, 'enUS', 'Oh, you murderers! Why? Why would you kill such a noble animal?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52456, 'enUS', 'Yes, I smell them too, Riplimb. Outsiders encroach on the Firelord\'s private grounds. Find their trail. Find them for me, that I may dispense punishment!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52457, 'enUS', 'Twist in flames, interlopers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52458, 'enUS', 'Go for the throat!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52459, 'enUS', '', 'I be healin\', mon.  I don\'t have no special shape.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52460, 'enUS', '', 'Can you feel the magic in the air, $c?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52461, 'enUS', 'Take your positions! We are ready to roll.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52462, 'enUS', '', 'Powerful magic fills the air this season, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52463, 'enUS', 'I\'m ready to attack Stormwind!', 'I\'m ready to attack Stormwind!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52464, 'enUS', 'Good work, druids! This area looks secure. Let\'s head back to base.', '', 26124);
