INSERT INTO `broadcast_text_locale` VALUES (52469, 'enUS', 'Good work, team! Take a breather and report back soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52470, 'enUS', '', 'Fire. My love has become fire. I feared this. To find him again, we must enter Ragnaros\' burning domain.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52471, 'enUS', '', 'Stay strong, friend. You know as well as I do - there\'ll be no help for us within the Firelands.', 26124);
