INSERT INTO `broadcast_text_locale` VALUES (53748, 'enUS', '<He looks like he was knocked unconscious in all the fighting.>$B$B<He clutches tightly to a leather bag.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53749, 'enUS', 'My skull feels like it was hit by a hammer, but otherwise I\'m all right. That\'s more than I can say for most of the crew.', '', 26124);
