INSERT INTO `broadcast_text_locale` VALUES (53754, 'enUS', 'Mysteries and wonders surround us every day, not just at the faire.  We\'ve compiled a list of some of the wonders you may encounter in your every day business.  The Darkmoon Adventurer\'s Guide will help you navigate the wonders of the world.$b$bWith the guide in your possession, you may come across items you never knew existed.  Items you may have taken for granted in the past are now treasures that others long for.$b$bKeep the guide with you, and keep your eyes open for mystery.$b$bIf you happen to lose your guide, seek us out and we will replace it for free.', 'Mysteries and wonders surround us every day, not just at the faire.  We\'ve compiled a list of some of the wonders you may encounter in your every day business.  The Darkmoon Adventurer\'s Guide will help you navigate the wonders of the world.$b$bWith the guide in your possession, you may come across items you never knew existed.  Items you may have taken for granted in the past are now treasures that others long for.$b$bKeep the guide with you, and keep your eyes open for mystery.$b$bIf you happen to lose your guide, seek us out and we will replace it for free.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53755, 'enUS', 'Darkmoon Adventurer\'s Guide?', 'Darkmoon Adventurer\'s Guide?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53756, 'enUS', 'May I have another Darkmoon Adventurer\'s Guide?', 'May I have another Darkmoon Adventurer\'s Guide?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53757, 'enUS', 'Death to all who oppose us!', 'Death to all who oppose us!', 26124);
