INSERT INTO `broadcast_text_locale` VALUES (53765, 'enUS', 'You did well, Agent Kearnen. Now save your energy... we\'ll fend them off.', 'You did well, Agent Kearnen. Now save your energy... we\'ll fend them off.', 26124);
