INSERT INTO `broadcast_text_locale` VALUES (53788, 'enUS', 'I\'m glad that these \"SI:7\" people decided to take me with.$b$bWe do not leave the comforts of our lake very often at all. This was the farthest I\'ve ever been from home.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53789, 'enUS', 'What happened next, Little Lu?', 'What happened next, Little Lu?', 26124);
