INSERT INTO `broadcast_text_locale` VALUES (53795, 'enUS', 'The statue\'s shoulder looks quite imposing in relation to the rest of its features. The subject was clearly a mighty creature!$b$bPerhaps it\'s a good natured sort. Or if not, then maybe it\'s extinct!$b$bOne can hope...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53796, 'enUS', 'My people do not like strangers, but I owe you this much. May the elders not punish me for this deed.', '', 26124);
